        xy_ += vec2( -.25, 0. );
        xy_ = vec2( cos( 2.2 * time ) * xy_.x + sin( 2.2 * time ) * xy_.y, -sin( 2.2 * time ) * xy_.x + cos( 2.2 * time ) * xy_.y );
        xy_ += vec2( +.25, 0. );
        xy_ = vec2( cos( 3.2 * time ) * xy_.x - sin( 3.2 * time ) * xy_.y, sin( 3.2 * time ) * xy_.x + cos( 3.2 * time ) * xy_.y );

        c = vec3( ( 16. * factor * mod( float( mb( ( 5. + 5.7 * sin( .125 * time ) ) * xy_ ) ), 16. ) ) ) * texture( iChannel0, txy ).rgb;
        return vec4( c, 1 );
    }

    float
    brt = clamp( 2. / pi * asin( ( clamp( -dot( normalize( n ), normalize( h - light1 ) ), 0., 1. ) ) ), 0., 1. ) + 
  		  clamp( 2. / pi * asin( ( clamp( -dot( normalize( n ), normalize( h - light2 ) ), 0., 1. ) ) ), 0., 1. ) +
          clamp( 2. / pi * asin( ( clamp( -dot( normalize( n ), normalize( h - light3 ) ), 0., 1. ) ) ), 0., 1. );
//    brt = sqrt( ( 2. / pi * asin( -dot( normalize( n ), normalize( h - light1 ) ) ) ) ) + 
