// APPLY TEXTURE
// -------------

vec3 applyTexture( vec4 hitPosAndID )
{
	vec4 objClr = vec4( 0.0 );

	vec3 pos = hitPosAndID.xyz,
	     base_color = vec3( 0.0 );

	float scale = 0.0,
	      complexity = 0.0,
	      mixVal = 0.0; 

	int texNum = 0,
	    objNum = int ( hitPosAndID.w );

	//  1: pyramid
	// 11: sphere in globe
	// 12: front pyramid platform
	// 16: east globe platform	
	if ( objNum == 1 || objNum == 11 || objNum == 12 || objNum == 16 )
	{				
		texNum = 20;
		objClr = vec4( 0.15, 0.5, 1.0, 1.0 );
		scale = 1.5;
		complexity = 4.0,
		mixVal = 0.85;	  
		base_color = getTexture( texNum, pos, scale, complexity, 
	                                                      objClr, mixVal ).xyz;
	}		
	//  2: center sphere
	// 15: globe pyramid
	else if ( objNum == 2 || objNum == 15 )
	{
		texNum = 21;
		objClr = vec4( 0.0, 0.15, 1.0, 1.0 );
		scale = 2.0;
		complexity = 1.5,
		mixVal = 0.9;	  
		base_color = getTexture( texNum, pos, scale, complexity, 
	                                                      objClr, mixVal ).xyz;
	}		
	// 3: upper sphere
	else if ( objNum == 3 )
	{
		texNum = 21;
		objClr = vec4( 0.0, 0.15, 1.0, 1.0 );
		scale = 5.0;
		complexity = 5.0,
		mixVal = 0.9;	  
		base_color = getTexture( texNum, pos, scale, complexity, 
	                                                      objClr, mixVal ).xyz;
	}				
	// 4: corner gazebo
	else if ( objNum == 4 )
	{
		texNum = 19;
		objClr = vec4( 0.2, 0.275, 1.0, 1.0 );
		scale = 2.0;
		complexity = 0.5,
		mixVal = 0.6;	  
		base_color = getTexture( texNum, pos, scale, complexity, 
	                                                      objClr, mixVal ).xyz;
	}		
	// 5: mound beneath corner gazebo
	else if ( objNum == 5 || objNum == 7 )
	{	
		texNum = 19;
		objClr = vec4( 0.66, 0.66, 1.0, 1.0 );
		scale = 3.0;
		complexity = 0.75,
		mixVal = 0.5;	  
		base_color = getTexture( texNum, pos, scale, complexity, 
	                                                      objClr, mixVal ).xyz;
	}		
	//  6: pyramid atop corner gazebo
	// 14: front pyramid sphere
	// 17: front pyramid upper sphere
	else if ( objNum == 6 || objNum == 14 || objNum == 17 )
	{
		texNum = 20;
		objClr = vec4( 0.5, 0.4, 1.0, 1.0 );
		scale = 3.0;
		complexity = 5.0,
		mixVal = 0.85;	  
		base_color = getTexture( texNum, pos, scale, complexity, 
	                                                      objClr, mixVal ).xyz;
	}
	// 8: corner sphere	
	else if ( objNum == 8 )
	{
		texNum = 21;
		objClr = vec4( 0.0, 0.15, 1.0, 1.0 );
		scale = 2.0;
		complexity = 1.5,
		mixVal = 0.9;	  
		base_color = getTexture( texNum, pos, scale, complexity, 
	                                                      objClr, mixVal ).xyz;
	}		
	// 9: upper corner sphere
	else if ( objNum == 9 )
	{
		texNum = 21;
		objClr = vec4( 0.0, 0.15, 1.0, 1.0 );
		scale = 2.0;
		complexity = 1.5,
		mixVal = 0.9;	  
		base_color = getTexture( texNum, pos, scale, complexity, 
	                                                      objClr, mixVal ).xyz;
	}
	// 10: east globe
	// 13: front pyramid
	else if ( objNum == 10 || objNum == 13 )
	{
		texNum = 19;
		objClr = vec4( 0.0, 0.15, 1.0, 1.0 );
		scale = 2.0;
		complexity = 1.5,
		mixVal = 0.9;	  
		base_color = getTexture( texNum, pos, scale, complexity, 
	                                                  objClr, mixVal ).xyz;		
	} 

	return base_color;
}

// end applyTexture()

