/* PROGRAM FLOW ANALYSIS 
 * =====================

main()
------	
	Store a non-normalized, scaled screen coord for use in skyColor();

	Adjust aspect ratio, normalize coords, translate origin to screen cntr.

	Create camera rotation matrices for rotation around the x and y axes.
	Multiply them together to create the camera rotation matrix.

	The rotation range is defined by a float value, which, when multiplied by
	mouse position offset from screen center in world coordinates, creates a
	range for vert and horiz movement in radians angles.

	The mouse manipulation of the camera has been replaced in this version with
	automatic rotations around the x and y axes.

	camPos is initialized as the original, static cam position multiplied by
	the camera rotation matrix.

	lightDir is initialized.

	color is calculated.

	raymarch() : the ray marching	
	-------
	'dist' is assigned a value returned from 

		scene(), which checks for the nearest object in the world by calling		
		--------  the Mercury SDF Library functions or IQ functions for each 
		          object, each of which returns a distance from the current ray 
		          position. This is combined with an object ID and returned to
		          raymarch() as a vec2.

	A vec4 'hit' is returned to main(), containing the vec3 hit position, 'p'. 
	// The w-component contains the ID of the object that was hit.

	shade() :
	-------
		sky_color() is combined with a vec3 ambient light value, and assigned to 
		----------- 'fogColor'.

		The w-component flag is checked. If the back plane was reached, the ray
		length is set to a tiny value, and color is set to fog.

		If an object was hit, the distance from ray origin to hit position is
		calculated, and 

		getNormal() : the average normal vector in the hit position vicinity is		
		---------   calculated

		base_color is assigned a vec3 from

			applyTexture(). The object ID number determines the values sent to			
			--------------

				getTexture(), which calculates the pixel color for the current
				------------  pixel.

		Diffuse light value is calculated as the dot product of the hit normal
		and the light direction.

		Specular light value is calculated.

		color is calculated as a mix of the base_color, and the diffuse and
		specular colors, and a calculated fog color.

		debug_plane() : a dist_color is calculated based on the distance from		
		-------------   the ray position to the debug plane.

		color is mixed with the debug plane color.

main()
------

color is modified ( brightened ) by an exponent, and assigned to gl_FragColor.	

*/

#define t     iTime
#define RES   iResolution
#define ASPXY RES.x / RES.y
#define ASPYX RES.y / RES.x

vec2
    xy;

const vec4
    fg = vec4( 1., .1, .0, 1. ),
    bg = vec4( .6, .6, .3, 1. ),
    c1 = vec4( 1., .9, .8, 1. ),
    c2 = vec4( .1, .1, .1, 1. ),
    c3 = vec4( .9, .9, .9, 1. ),
    c4 = vec4( .6, .5, .4, 1. );

int
    n2s( int n ) {

		// using binary searching for getting number sprite
        return 
        n < 8 
			? n < 4
        		? n < 2
        			? n < 1
        				? 0xE5294E	//0
        				: 0x421084	//1
                    : n < 3
                		? 0x64104E	//2
                		: 0xE4110E	//3
				: n < 6			
					? n < 5
                		? 0xA53908	//4
                    	: 0xE1390E	//5
             		: n < 7
			 			? 0xE1394E	//6
             			: 0xE42108	//7
			: n < 12
                ? n < 10
                	? n < 9 
                		? 0xE5394E	//8
            			: 0xE5390E	//9
					: n < 11
                        ? 0x123456	//10
                        : 0xABCDEF	//11
				: n < 14
                	? n < 13
                		? 0xE5394E	//12
            			: 0xE5390E	//13
					: n < 15
                        ? 0x123456	//14
                        : 0xABCDEF;	//15
	}

bool
    rdrN( int n, vec2 p ) {

        n = n2s( n );

        int
            j = int( 5. * p.x ) + 5 * int( 5. * p.y ),
            f = 0x2000000;

        for( int i = 25; i >= 0; i-- ) {

            if( i == j )

                break; 

            if( n >= f )

                n -= f;

            f /= 2;
        }

        return f <= n;
    }

vec4
    rdr( vec2 p, vec4 c, vec2 rd1, vec2 rd2 ) {

        vec2
            rel = 2. * p - 1.,
            rl2 = 5. * ( mod( rel + 1., .4 ) - .2 );

        rel = pow( abs( rel ), rd1 ),
        rl2 = pow( abs( rl2 ), rd2 );

        float
            f  = pow( 1. - dot( rel, rel ), 1. / dot( rd1, rd1 ) ),
            f2 = pow( 1. - dot( rl2, rl2 ), 1. / dot( rd2, rd2 ) );

        return vec4( ( .1 < f + f2 ) ? .5 * ( f + f2 ) * c.rgb : bg.rgb, 1. );
    }

int
    //digit of decimal number
    dod( in int dgt, in int len, in int dN ) {

        dgt = len - dgt - 1;

        if( dgt < 0 )

            dgt = 0;

        for( int i = 0; i < 10; ++i ) {

            int
                a = dN / 10,
                b = dN - 10 * a;

            if( i == dgt ) 

                return b;                

            dN = a;
        }

        return 0;
    }

vec4
    dec( int n, int d, vec2 p, vec2 r, vec4 _fg, vec4 _bg, vec2 rd1, vec2 rd2 ) {

        vec4
            c = vec4( 0. );

        vec2
            rel = xy - p;

        if( rel.x < 0. || float( d ) * r.x < rel.x || rel.y < 0. || r.y < rel.y )

            return c;      

        float
            x = rel.x / r.x;

        int
            di = int( x );

        if( d < di ) 

            return c;

        rel.x = fract( x );
        rel.y /= r.y;

        int
            dig = dod( di, d, n );

        return rdr( rel, rdrN( dig, rel ) ? _fg : _bg, rd1, rd2 );
    }

void
    mainImage( out vec4 p_c, in vec2 p_xy ) {

		xy = ( 2. * p_xy / RES.xy - 1. ) * vec2( ASPXY, 1. );

        p_c  = vec4( 0. );

        float
            x = +mod( +.2 * t, 2. * ASPXY ) - ASPXY,
            y = +mod( +.2 * t, 2. ) - 1.,
            f = 9.8;

        vec2
            ext = vec2( .375 + .125 * sin( t ),    .35 + .1   * sin( .4  * t ) ),
            _10 = vec2( 10. ),
            rd1 = _10 + f * vec2( sin( .3 * t ), sin( .31 * t ) ),
            rd2 = _10 + f * vec2( sin( .2 * t ), sin( .23 * t ) ),
            _15 = vec2( .15 );

        vec2
            rp = vec2( x - 2. * ASPXY, y - 2. );

        for( int dy = -1; dy < 1; ++dy ) 

	        for( int dx = -1; dx < 1; ++dx ) 

                p_c += dec( int( 100. * t ) , 8, vec2( x + 2. * float( dx ) * ASPXY, y + 2. * float( dy ) ), ext, bg, fg, rd1, rd2  );

        p_c += dec( int( mod( 100. * iDate.w, 100. ) ), 2, vec2( -1.5, -.75 ), vec2( 1.5 ), vec4( 1.25 * bg.rgb, 1. ), bg, vec2( 20. ) + 19. * vec2( sin( .4 * t ) ), vec2( 20. ) + 19. * vec2( sin( .23 * t ) ) );

        p_c += dec( int( iDate.x      ), 4, vec2( +ASPXY -  .82, +.77 ), vec2( .2 ), c2, c3, _10 + f * vec2( sin( .4  * t ), sin( .31 * t ) ), rd1 );
        p_c += dec( int( iDate.y + 1. ), 2, vec2( +ASPXY - 1.04, +.88 ), vec2( .1 ), c2, c3, _10 + f * vec2( sin( .41 * t ), sin( .31 * t ) ), rd1 );
        p_c += dec( int( iDate.z      ), 2, vec2( +ASPXY - 1.04, +.76 ), vec2( .1 ), c2, c3, _10 + f * vec2( sin( .42 * t ), sin( .31 * t ) ), rd1 );

        p_c += dec( int( mod( iDate.w / 3600., 24. ) ), 2, vec2( -ASPXY + .02, .83 ), _15, c3, c2, _10 + f * vec2( sin( .6  * t ), sin( .61 * t ) ), rd2 );
        p_c += dec( int( mod( iDate.w / 60.,   60. ) ), 2, vec2( -ASPXY + .34, .83 ), _15, c3, c2, _10 + f * vec2( sin( .50 * t ), sin( .51 * t ) ), rd2 );
        p_c += dec( int( mod( iDate.w,         60. ) ), 2, vec2( -ASPXY + .66, .83 ), _15, c3, c2, _10 + f * vec2( sin( .41 * t ), sin( .41 * t ) ), rd2 );

        rd1 = vec2( 9.5 + .5 * sin( .1 * t ) );
        p_c += dec(  1234, 5, vec2( -.85, -.96 ), _15, c4, c1, rd1, rd2 );
        p_c += dec( 56789, 5, vec2(  .1,  -.96 ), _15, c1, c4, rd2, rd1 );

        if( p_c.a < 1. )

            p_c = bg;

        else

            p_c.rgb /= p_c.a;

        p_c.rgb = mix( p_c.rgb, texture( iChannel0, ( 5.8 + 5. * .1 * floor( 10. * cos( .03 * t ) ) ) * xy ).rgb, .2 );
    }

const int renderDepth = 400;
const bool showRenderDepth = false;

const vec3 background = vec3(0.2, 0.2, 0.8);
const vec3 white = vec3(0.8, 0.8, 0.8);
const vec3 black = vec3(0, 0, 0);
const vec3 red = vec3(0.8, 0.2, 0.2);
const vec3 green = vec3(0.2, 0.8, 0.2);
const vec3 lightPos = vec3(0, 10, 10);

const float a = 1.0;
const float b = 3.0;
vec3 forces[3];


vec3 getRayDir(vec3 camDir, vec2 fragCoord) {
  vec3 yAxis = vec3(0, 1, 0);
  vec3 xAxis = normalize(cross(camDir, yAxis));
  vec2 q = fragCoord / iResolution.xy;
  vec2 p = 2.0 * q - 1.0;
  p.x *= iResolution.x / iResolution.y;
  return normalize(p.x * xAxis + p.y * yAxis + 5.0 * camDir);
}

// http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdBox(vec3 p, vec3 b) {
  vec3 d = abs(p) - b;
  return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0)) - 0.1;
}

float sdPlane(vec3 p, vec4 n) {
  return dot(p,n.xyz) + n.w;
}

float getMetaball(vec3 p, vec3 v) {
  float r = length(p - v);
  if (r < b / 3.0) {
    return a * (1.0 - 3.0 * r * r / b * b);
  } else if (r < b) {
    return (3.0 * a / 2.0) * (1.0 - r / b) * (1.0 - r / b);
  } else {
    return 0.0;
  }
}

float sdImplicitSurface(vec3 p) {
  float mb = 0.0;
  float minDist = 10000.0;
  for (int i = 0; i < 3; i++) {
    mb += getMetaball(p, forces[i]);
    minDist = min(minDist, length(p - forces[i]));
  }
  if (minDist > b) {
    return max (minDist - b, b - 1.2679529);
  } else if (mb == 0.0) {
    return b - 1.2679529;  // 1.2679529 is the x-intercept of the metaball expression - 0.5.
  } else {
    return b - sqrt(6.0 * mb) - 1.2679529;
  }
}

float getSdf(vec3 p) {
  float f = sdImplicitSurface(p);
  for (int i = 0; i < 6; i++) {
    float t = float(i) + iTime;
    f = min(f, sdBox(
        p - vec3(
            3.0 + 3.0 * cos(t * 3.141592 / 3.0), 
            0, 
            3.0 * sin(t * 3.141592 / 3.0)),
        vec3(0.5)));
  }
  return f;
}

float getSdfWithPlane(vec3 p) {
  return min(getSdf(p), sdPlane(p, vec4(0,1,0,1)));
}

float diffuse(vec3 point,vec3 normal) {
  return clamp(dot(normal, normalize(lightPos - point)), 0.0, 1.0);
}

float getShadow(vec3 pt) {
  vec3 lightDir = normalize(lightPos - pt);
  float kd = 1.0;
  int step = 0;
  float t = 0.1;

  for (int step = 0; step < renderDepth; step++) {
    float d = getSdf(pt + t * lightDir);
    if (d < 0.001) {
      kd = 0.0;
    } else {
      kd = min(kd, 16.0 * d / t);
    }
    t += d;
    if (t > length(lightPos - pt) || step >= renderDepth || kd < 0.001) {
      break;
    }
  }
  return kd;
}

vec3 getGradient(vec3 pt) {
  return vec3(
    getSdfWithPlane(vec3(pt.x + 0.0001, pt.y, pt.z)) - getSdfWithPlane(vec3(pt.x - 0.0001, pt.y, pt.z)),
    getSdfWithPlane(vec3(pt.x, pt.y + 0.0001, pt.z)) - getSdfWithPlane(vec3(pt.x, pt.y - 0.0001, pt.z)),
    getSdfWithPlane(vec3(pt.x, pt.y, pt.z + 0.0001)) - getSdfWithPlane(vec3(pt.x, pt.y, pt.z - 0.0001)));
}

vec3 getDistanceColor(vec3 pt) {
  float d = getSdf(pt);
  vec3 color = mix(red, green, 0.5 + 0.5 * sin(d * 3.141592));
  if (fract(d) < 0.05) {
    color = mix(color, black, smoothstep(0.0, 0.05, fract(d)));
  } else if (fract(d) < 0.1) {
    color = mix(black, color, smoothstep(0.05, 0.1, fract(d)));
  }
  return color;
}

vec3 illuminate(vec3 pt) {
  vec3 color = (abs(pt.y + 1.0) < 0.001) ? getDistanceColor(pt) : white;
  vec3 gradient = getGradient(pt);
  float diff = diffuse(pt.xyz, normalize(gradient));
  return (0.25 + diff * getShadow(pt))  * color;
}

vec3 raymarch(vec3 rayorig, vec3 raydir) {
  vec3 pos = rayorig;
  float d = getSdfWithPlane(pos);
  int work = 0;

  for (int step = 0; step < renderDepth; step++) {
    work++;
    pos = pos + raydir * d;
    d = getSdfWithPlane(pos);
    if (abs(d) < 0.001) {
      break;
    }
  }

  return showRenderDepth
    ? vec3(float(work) / float(renderDepth))
    : (abs(d) < 0.001) 
      ? illuminate(pos)
      : background;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  forces[0] = vec3(-3, 0, 0);
  forces[1] = vec3(3.0 * sin(iTime), 4.0 * abs(cos(iTime)), 0.0);
  forces[2] = vec3(3, 0, 0);

  vec3 camPos = 20.0 * vec3(cos(iTime / 10.0), 0.5, sin(iTime / 10.0));
  vec3 camDir = normalize(-camPos);
  fragColor = vec4(raymarch(camPos, getRayDir(camDir, fragCoord)), 1.0);
}

#define IT 256
#define ITF float(IT)
#define STR 1.5/sqrt(ITF)
#define A 1.0

#define M_PI 3.14159254

vec3 colorFilter(vec3 c) {
    vec3 color;

    float g = (c.r+c.g+c.b)/3.0;
    float s = abs(c.r-g)+abs(c.g-g)+abs(c.b-g);

    color = c*s+(1.0-s)*(c-s);

    return color*color;
}

vec3 value(vec3 pos) {
    vec3 color;

    color.r = sin(pos.x);
    color.g = sin(pos.y);
    color.b = sin(pos.z);

    return color;
}

vec3 scan(vec3 pos, vec3 dir){
    vec3 c = vec3(0.5);
    for (int i=0; i<IT; i++) {
        float f = (1.0- float(i)/ITF)*STR;

        vec3 posMod = value(pos);
        vec3 newPos;
        newPos.x = posMod.y*posMod.z;
        newPos.y = posMod.x*posMod.z;
        newPos.z = posMod.x*posMod.y;

        c+=value(pos+newPos*4.0)*f;
        pos+=dir*2.0;
    }
    return colorFilter(c);
}

void mainImage(out vec4 fragColor,in vec2 uv){
    uv = (uv-iResolution.xy/2.0)/iResolution.y;
    float a = iTime*0.125;

    vec3 pos = vec3(cos(a/4.0+sin(a/2.0)),cos(a*0.2),sin(a*0.31)/4.5)*16.0;
    vec3 on = vec3(1.0,uv.x,uv.y);
    vec3 n;

    n = normalize(pos + vec3(cos(a*2.3),cos(a*2.61),cos(a*1.62)));
	vec3 crossRight = normalize( cross(n,vec3(0.0,0.0,1.0)));
	vec3 crossUp = normalize(cross(n, crossRight));
	n = n*1.5 + crossRight*uv.x + crossUp*uv.y;

    fragColor.rgb = scan(pos,normalize(n)).rgb;
}

// A simple fractal.
// From https://www.shadertoy.com/view/MslGWN
float field(in vec3 p,float s) {
	float strength = 7. + .03 * log(1.e-6 + fract(sin(iTime) * 4373.11));
	float accum = s/4.;
	float prev = 0.;
	float tw = 0.;
	for (int i = 0; i < 26; ++i) {
		float mag = dot(p, p);
		p = abs(p) / mag + vec3(-.5, -.4, -1.5);
		float w = exp(-float(i) / 7.);
		accum += w * exp(-strength * pow(abs(mag - prev), 2.2));
		tw += w;
		prev = mag;
	}
	return max(0., 5. * accum / tw - .7);
}

const float mindist = 1.0;
const float maxdist = 4.0;

float map2( vec3 p ) {

    float t = field(p,.5);

    return t;
}

// From https://www.shadertoy.com/view/MtX3Ws
vec3 raymarch( in vec3 ro, vec3 rd, vec2 tminmax )
{
    float t = tminmax.x;
    //float d2 = .02
    float dt = .2 * sin(iTime*.1);
    //float dt = .2 - .195*cos(iTime*.05);//animated
    vec3 col= vec3(0.);
    float c = 0.;
    for( int i=0; i<10; i++ )
	{
        t+=dt*exp(-2.*c);
        if(t>tminmax.y)break;

        c = map2(ro+t*rd);               

        //col = .99*col+ .08*vec3(c*c, c, c*c*c);//green	
        col = .99*col+ .08*vec3(c*c*c, c*c, c);//blue
    }    
    return col;
}

vec3 render( in vec3 ro, in vec3 rd )
{
    return raymarch(ro, rd, vec2(mindist, maxdist));
}

mat3 setCamera( in vec3 ro, in vec3 rt, in float cr )
{
	vec3 cw = normalize(rt-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, -cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

    vec2  p = (-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;
    float an = 1.87 - 0.04*(1.0-cos(0.5*iTime));

    vec3  ro = vec3(-0.4,0.2,0.0) + 2.2*vec3(cos(an),0.0,sin(an));
    vec3  ta = vec3(-0.6,0.2,0.0);
    mat3  ca = setCamera( ro, ta, 0.0 );
    vec3  rd = normalize( ca * vec3(p,-2.8) );

    vec3 col = render( ro, rd );

    fragColor = vec4( col, 1.0 );
}

