vec3 doBackground( vec2 uv)
{
    return vec3( uv,0.5+0.5*sin(iTime)*texture(iChannel0,uv).x)+0.1;
}

float sMin( float a, float b )
{
    float k = .12;
	float h = clamp(0.5 + 0.5*(b-a)/k, 0.0, 1.0 );
	return mix( b, a, h ) - k*h*(1.-h);
}

//functions that build rotation matrixes
mat2 rotate_2D(float a){float sa = sin(a); float ca = cos(a); return mat2(ca,sa,-sa,ca);}
mat3 rotate_x(float a){float sa = sin(a); float ca = cos(a); return mat3(1.,.0,.0,    .0,ca,sa,   .0,-sa,ca);}
mat3 rotate_y(float a){float sa = sin(a); float ca = cos(a); return mat3(ca,.0,sa,    .0,1.,.0,   -sa,.0,ca);}
mat3 rotate_z(float a){float sa = sin(a); float ca = cos(a); return mat3(ca,sa,.0,    -sa,ca,.0,  .0,.0,1.);}

float sdCappedCylinder( vec3 p, vec2 h )
{

  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
float udBox( vec3 p, vec3 b )
{
  return length(max(abs(p)-b,0.0));
}
vec2 doModel( vec3 p )
{
     float id;

    vec3 music = texture(iChannel0,p.xz).xyz;

    float scene[19]; //array to hold scene objects
    float s,s1,s2;
    vec3 hpos=vec3(0.+cos(p.y+iTime*4.)*p.x*0.005,0,0); //model position
    s1 = length(p+hpos+p.y*0.2) - 1.02;

     s2 = .5;///length(p+vec3(1.,0,0)) - .30;
      float s3;

    s=sMin(s1,s2);
     scene[0] = s1;
     p+=hpos;
    for(int i=0;i<4;i++)
    {

         s2 = length(p+vec3(1.+float(i)*0.15,0.+cos(p.x*3.5)*0.49,0)) - .25+p.x*0.1;
         s=sMin(s,s2);scene[1] = s2;


        s2 = length(p+vec3(-1.-float(i)*0.2,0.+cos(p.x*3.5)*0.6,0)) - .20-p.x*0.1;
         s=sMin(s,s2);scene[2] = s2;

         s2 = length(p+vec3(0.+cos(p.y*2.5+iTime*2.5)*0.05,1.+float(i)*0.05,0)) - .30+p.y*0.4;
         s=sMin(s,s2);scene[3] = s2;

        p = p*rotate_y(.7);
         s2 = length(p+vec3(.8+float(i)*0.25,  1.+cos(p.x*4.+iTime*music.x*0.5)*0.2*music.x  ,0.)) - .25;
         s=sMin(s,s2);scene[4] = s2;
        p = p*rotate_y(-.7);

          p = p*rotate_y(-.7);
         s2 = length(p+vec3(-.6-float(i)*0.25,  1.+sin(-p.x*5.5+iTime)*0.08  ,0.)) - .25;
         s=sMin(s,s2);scene[5] = s2;
        p = p*rotate_y(.7);


    }

       s2 = length(p+vec3(.0,0.,.8)) - .30+p.y*0.4;
         s=sMin(s,s2);scene[6] = s2;

      s2 = length(p+vec3(.0,-0.1,1.)) - .20+p.y*0.4;
         s=min(s,s2);scene[7] = s2;

      s2 = length(p+vec3(.3,-0.4,.7)) - .29;
         s=sMin(s,s2);scene[8] = s2;
     s2 = length(p+vec3(.3,-0.4,.85)) - .19;
         s=sMin(s,s2);scene[9] = s2;


     s2 = length(p+vec3(-.3,-0.4,.7)) - .27;
        s=sMin(s,s2); scene[10] = s2;
      s2 = length(p+vec3(-.3,-0.4,.85)) - .19;
         s=sMin(s,s2);scene[11] = s2;

     s2 = length(p+vec3(-.3,-0.39,.9)) - .15;
         s=sMin(s,s2);scene[12] = s2;
     s2 = length(p+vec3(.3,-0.39,.9)) - .15;
         s=sMin(s,s2);scene[13] = s2;

    s2 = length(p+vec3(.1,0.3,1.)) - .1;
         s=sMin(s,s2);scene[14] = s2;

     s2 = sdCappedCylinder(p+vec3(1.1,1.5,1.3), vec2 (0.7,.02) );
         s=sMin(s,s2);scene[15] = s2;


    s2 = sdCappedCylinder(p+vec3(1.1,1.5,1.3), vec2 (0.2,.023) );
         s=sMin(s,s2);scene[16] = s2;
    s2 = sdCappedCylinder(p+vec3(1.1,1.5,1.3), vec2 (0.08,.025) );
         s=sMin(s,s2);scene[17] = s2;
     s2 = udBox(p+vec3(.8,2.,1.5), vec3 (2.1,.5,1.) );
         s=sMin(s,s2);scene[18] = s2;

      //////SORT OBJECTS
    float test=9999.0;  //return closest object in scene
    for(int i=0;i<19;i++){
        float test2=scene[i];
        if(test2<test)test=test2;
    }


    if(test == scene[0])id=1.0;
    if(test == scene[1])id=1.0;
    if(test == scene[2])id=1.0;
    if(test == scene[3])id=1.0;
    if(test == scene[4])id=1.0;
    if(test == scene[5])id=1.0;
    if(test == scene[6])id=2.0;
     if(test == scene[8])id=3.0;
     if(test == scene[9])id=4.0;
    if(test == scene[10])id=3.0;
    if(test == scene[11])id=4.0;
    if(test == scene[14])id=5.0;
     if(test == scene[16])id=6.0;
     if(test == scene[18])id=3.0;
    return vec2(s,id);
}

vec3 doMaterial( in vec3 pos, in vec3 nor,vec2 obj )
{
    vec3 col;

    if(obj.y==1.) col =  vec3(0.2,0.07,0.01);
     if(obj.y==2.) col =  vec3(.3,0.07,0.01);
       if(obj.y==3.) col =  vec3(.3);
     if(obj.y==4.) col =  vec3(0,0,0.5);
    if(obj.y==5.) col =  vec3(0.1,0,0.);
    if(obj.y==6.) col = texture(iChannel1,pos.xz*1.5+0.5 ).xyz*0.5;
    return col;
}

//------------------------------------------------------------------------
// Lighting
//------------------------------------------------------------------------
float calcSoftshadow( in vec3 ro, in vec3 rd );

vec3 doLighting( in vec3 pos, in vec3 nor, in vec3 rd, in float dis, in vec3 mal )
{
    vec3 lin = vec3(0.);

    // key light
    //-----------------------------
    vec3  lig = normalize(vec3(-1.0,0.7,-0.9));
    float dif = max(dot(nor,lig),0.0);
    float sha = 0.0; if( dif>0.01 ) sha=calcSoftshadow( pos+0.01*nor, lig );
    lin += dif*vec3(4.00,4.00,4.00)*sha;

    // ambient light
    //-----------------------------
    lin += vec3(0.50,0.50,0.50);


    // surface-light interacion
    //-----------------------------
    vec3 col = mal*lin;


    // fog    
    //-----------------------------
	col *= exp(-0.001*dis);

    return col;
}

float calcIntersection( in vec3 ro, in vec3 rd )
{
	const float maxd = 10.0;           // max trace distance
	const float precis = 0.001;        // precission of the intersection
    float h = precis*2.0;
    float t = 0.0;
	float res = -1.0;
    for( int i=0; i<60; i++ )          // max number of raymarching iterations is 90
    {
        if( h<precis||t>maxd ) break;
	    h = doModel( ro+rd*t ).x;
        t += h;
    }

    if( t<maxd ) res = t;
    return res;
}

vec3 calcNormal( in vec3 pos )
{
    const float eps = 0.002;             // precision of the normal computation

    const vec3 v1 = vec3( 1.0,-1.0,-1.0);
    const vec3 v2 = vec3(-1.0,-1.0, 1.0);
    const vec3 v3 = vec3(-1.0, 1.0,-1.0);
    const vec3 v4 = vec3( 1.0, 1.0, 1.0);

	return normalize( v1*doModel( pos + v1*eps ).x + 
					  v2*doModel( pos + v2*eps ).x + 
					  v3*doModel( pos + v3*eps ).x + 
					  v4*doModel( pos + v4*eps ).x );
}

float calcSoftshadow( in vec3 ro, in vec3 rd )
{
    float res = 1.0;
    float t = 0.0005;                 // selfintersection avoidance distance
	float h = 1.0;
    for( int i=0; i<25; i++ )         // 40 is the max numnber of raymarching steps
    {
        h = doModel(ro + rd*t).x;
        res = min( res, 64.0*h/t );   // 64 is the hardness of the shadows
		t += clamp( h, 0.02, 2.0 );   // limit the max and min stepping distances
    }
    return clamp(res,0.0,1.0);
}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

    // camera movement
    vec3 ro, ta;
    doCamera( ro, ta, iTime, m.x );

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,1.8) ); // 2.0 is the lens length

    //-----------------------------------------------------
	// render
    //-----------------------------------------------------

	vec3 col = doBackground(fragCoord.xy/iResolution.xy);

	// raymarch
    float t = calcIntersection( ro, rd );
    if( t>-0.5 )
    {
        // geometry
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal(pos);

        vec2 obj = doModel(pos);
        // materials
       vec3 mal = doMaterial( pos, nor, obj );
		//vec3 obj = doModel(p);
        col = doLighting( pos, nor, rd, t, mal );
	}

	//-----------------------------------------------------
	// postprocessing
    //-----------------------------------------------------
    // gamma
	col = pow( clamp(col,0.0,1.0), vec3(0.4545) );

    fragColor = vec4( col, 1.0 );
}

