	vec3 cameraUp = normalize( cross( cameraRight, cameraDir ) );


	vec3 rd = normalize( cameraDir + vec3( pos, 0.0 ) );

	float t = 0.0;
	float d = 100.0;

	vec3 r;
	vec3 color = vec3(0.0);

	for( int i = 0; i < 60; i++ ){


		if( d > .001 )
		{	
			r = ro + rd * t;
			d = hit( r );
			t+=d;	

		}
	}

	  	vec3 n = vec3( hit( r + eps ) - hit( r - eps ),
	  			hit( r + eps.yxz ) - hit( r - eps.yxz ),
	  			hit( r + eps.zyx ) - hit( r - eps.zyx ) );


	vec3 mat = vec3( .5, .1, .3 ); 
