float noiseHash( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0 + 113.0*p.z;

    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                        mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
                    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                        mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
    return res;
}

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);
	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = textureLod( iChannel0, (uv+.5)/256., 0.).yx;
	return mix( rg.x, rg.y, f.z );
}

float fbm( vec3 p )
{
    float f = 0.0;

    f += 0.5000*noise( p ); p = m*p*2.02;
    f += 0.2500*noise( p ); p = m*p*2.03;
    f += 0.1250*noise( p ); p = m*p*2.01;
    f += 0.0625*noise( p );

    return f/0.9375;
}

float fbmHash( vec3 p )
{
    float f = 0.0;

    f += 0.5000*noiseHash( p ); p = m*p*2.02;
    f += 0.2500*noiseHash( p ); p = m*p*2.03;
    f += 0.1250*noiseHash( p ); p = m*p*2.01;
    f += 0.0625*noiseHash( p );

    return f/0.9375;
}

float age(){
   return (1.0+sin((iTime*0.25)-(3.14159/2.0)))/2.0;	
}	

float doubleAge(){
   return (1.0+sin((iTime*0.5)-(3.14159/2.0)))/2.0;	
}

vec2 map( vec3 p )
{

    float a = atan(p.x,p.z);
	//float a = atan(pos.y/pos.x);
	float u = a/(3.14159/2.0);
    float r = length(p.xz);
	float b = acos(p.y/(6.0*r));
	float f = 1.0-smoothstep( 0.20, 0.35, r );
	//return vec3(f,f,f);
	//b = min(b+abs(f*(acos(pos.y/(100.0*r)))),1.0);
	b = acos(p.y/(10.0*r));
	float v = (b/(3.14159/2.0))*6.0;
	vec3 uvw = vec3(u*1.2,v*1.2,0);	


    // mold displacement	
    //float mold = (smoothstep( 0.0, 1.0, fbmHash(uvw*2.0) )*0.3) +(smoothstep(0.5,1.0,age())/2.2);
    //mold = smoothstep( 0.3,1.0,mold);	
	float top = 1.0-smoothstep( 0.10, 1.0, r );
	//mold = mold*(1.0-top);

	float boom = smoothstep(0.0,1.0,fbmHash(p*17.0))*(1.0-top);
	boom = boom + 1.5*smoothstep(0.0,1.0,fbmHash(p*2.0))*(1.0-top);			  

   vec2 d2 = vec2( p.y+0.55, 2.0 );

   p.y -= ((doubleAge()-0.530)*0.75*pow(dot(p.xz,p.xz),0.2))+(boom*0.5);//+(mold*-0.20);//+((stripes-0.5)*0.40);
   vec2 d1 = vec2( length(p) - 1.0, 1.0 );

   if( d2.x<d1.x) d1=d2;
   return d1;
}

vec3 appleColor( in vec3 pos, in vec3 nor, in vec3 pRef, in vec3 nRef, out vec2 spe )
{
    spe.x = 1.0;
    spe.y = 1.0;

    // spherical-ish projection
    float a = atan(pos.x,pos.z);
    float u = a/(3.14159/2.0);
    float r = length(pos.xz);
    float b = acos(pos.y/(6.0*r));
    float f = 1.0-smoothstep( 0.20, 0.35, r );
    b = acos(pos.y/(10.0*r));
    float v = (b/(3.14159/2.0))*6.0;

    // uvw manifold
    vec3 uvw = vec3(u*1.2,v*1.2,0);

    // red
    vec3 col = vec3(1.0,0.0,0.0);

    // green
    f = smoothstep( 0.1, 1.0, fbm(uvw*1.0)+((1.0-age())*(1.0-age())) );
    col = mix( col, vec3(0.7,1.0,0.2), f);

    //brown
    float decay = smoothstep(0.75,1.0,age()*age()*age());
    col = mix( col, vec3(0.7,0.6,0.5), decay);	

    // dirty
    f = smoothstep( 0.0, 1.0, fbm(uvw*10.0) );
	f = f*(2.0*age());
    col *= 0.8+0.2*f;

    // frekles
    f = smoothstep( 0.0, 1.0, fbm(uvw*48.0) );
    f = smoothstep( 0.7,0.9,f);
    col = mix( col, vec3(0.9,0.9,0.6), f*0.5 );

    // stripes
    f = fbm( vec3(a*7.0 + uvw.z,3.0*uvw.y,uvw.x)*2.0);
	f = f*(age()*2.0);
    f = smoothstep( 0.2,1.0,f);
    f *= smoothstep(0.4,1.2,pos.y + 0.75*(noise(4.0*pos.zyx)-0.5) );
	col = mix( col, vec3(0.4,0.2,0.0), 0.5*f );
    spe.x *= 1.0-0.35*f;
    spe.y = 1.0-0.5*f;

    // mold
    float mold = (smoothstep( 0.0, 1.0, fbmHash(uvw*2.0) )*0.6) +(age()/2.2);
    mold = smoothstep( 0.8,0.9,mold);
    col = mix( col, vec3(0.9,0.9,0.6), mold*1.0 );	

    //mold stripe
    float moldStripe = smoothstep(0.4,0.7,mold);
    moldStripe = moldStripe * (1.0-smoothstep(0.75,1.0,mold));
    col = mix( col, vec3(0.97,0.97,0.90), moldStripe*1.0 );


    // top
    f = 1.0-smoothstep( 0.12, 0.20, r+((fbm(pos*20.0)-0.5)*0.1) );
	vec3 topColor = mix(vec3(0.6,0.6,0.5),vec3(0.8,0.8,0.6),fbm(pos*30.0));
    col = mix( col, topColor, f );
    spe.x *= 1.0-f;

    spe.x = spe.x*(1.0-decay) * (1.0-mold);

    float ao = 0.5 + 0.5*nor.y;
    col *= ao*1.2;

    return col;
}

vec3 floorColor( in vec3 pos, in vec3 nor, out vec2 spe )
{
    spe.x = 1.0;
    spe.y = 1.0;
    vec3 col = vec3(0.5,0.4,0.3)*1.7;

    float f = fbm( 4.0*pos*vec3(6.0,0.0,0.5) );
    col = mix( col, vec3(0.3,0.2,0.1)*1.7, f );
    spe.y = 1.0 + 4.0*f;

    f = fbm( 2.0*pos );
    col *= 0.7+0.3*f;

    // frekles
    f = smoothstep( 0.0, 1.0, fbm(pos*48.0) );
    f = smoothstep( 0.7,0.9,f);
    col = mix( col, vec3(0.2), f*0.75 );


    // fake ao
    f = smoothstep( 0.1, 1.55, length(pos.xz) );
    col *= f*f*1.4;
    col.x += 0.1*(1.0-f);

    return col;
}

vec2 intersect( in vec3 ro, in vec3 rd )
{
    float t=0.0;
    float dt = 0.12;
    float nh = 0.0;
    float lh = 0.0;
    float lm = -1.0;
    for(int i=0;i<45;i++)
    {
        vec2 ma = map(ro+rd*t);
        nh = ma.x;
        if(nh>0.0) { lh=nh; t+=dt;  } lm=ma.y;
    }

    if( nh>0.0 ) return vec2(-1.0);
    t = t - dt*nh/(nh-lh);

    return vec2(t,lm);
}

float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    float dt = 0.4;
    float t = mint;
    for( int i=0; i<8; i++ )
    {
        float h = map(ro + rd*t).x;
        if( h>0.001 )
            res = min( res, k*h/t );
        else
            res = 0.0;
        t += dt;
    }
    return res;
}
vec3 calcNormal( in vec3 pos )
{
    vec3  eps = vec3(.001,0.0,0.0);
    vec3 nor;
    nor.x = map(pos+eps.xyy).x - map(pos-eps.xyy).x;
    nor.y = map(pos+eps.yxy).x - map(pos-eps.yxy).x;
    nor.z = map(pos+eps.yyx).x - map(pos-eps.yyx).x;
    return normalize(nor);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0 * q;
    p.x *= iResolution.x/iResolution.y;

    // camera
    vec3 ro = (1.0+(iTime*0.01))*normalize(vec3(cos(0.2*sin(iTime)),1.15+0.4*cos(iTime*.11),
								  sin(0.2*iTime)));
    vec3 ww = normalize(vec3(0.0,0.5,0.0) - ro);
    vec3 uu = normalize(cross( vec3(0.0,1.0,0.0), ww ));
    vec3 vv = normalize(cross(ww,uu));
    vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

    // raymarch
    vec3 col = vec3(0.96,0.98,1.0);
	col =vec3(0.26,0.1,0.0);
    vec2 tmat = intersect(ro,rd);
    if( tmat.y>0.5 )
    {
        // geometry
        vec3 pos = ro + tmat.x*rd;
        vec3 nor = calcNormal(pos);
        vec3 ref = reflect(rd,nor);
        vec3 lig = normalize(vec3(1.0,0.8,-0.6));

        float con = 1.0;
        float amb = 0.5 + 0.5*nor.y;
        float dif = max(dot(nor,lig),0.0);
        float bac = max(0.2 + 0.8*dot(nor,vec3(-lig.x,lig.y,-lig.z)),0.0);
        float rim = pow(1.0+dot(nor,rd),3.0);
        float spe = pow(clamp(dot(lig,ref),0.0,1.0),16.0);

        // shadow
        float sh = softshadow( pos, lig, 0.06, 4.0, 4.0 );

        // lights
        col  = 0.10*con*vec3(0.80,0.390,0.200);
        col += 0.70*dif*vec3(1.00,0.97,0.85)*vec3(sh, (sh+sh*sh)*0.5, sh*sh );
        col += 0.15*bac*vec3(1.00,0.397,0.185);
        col += 0.20*amb*vec3(0.10,0.05,0.020);


        // color
        vec2 pro;
        if( tmat.y<1.5 )
        col *= appleColor(pos,nor, pos, nor ,pro);
        else
        col *= floorColor(pos,nor,pro);

        // rim and spec
        col += 0.60*rim*vec3(0.96,0.1,0.05)*amb*amb;
        col += 0.60*pow(spe,pro.y)*vec3(1.0)*pro.x*sh;

        col = 0.3*col + 0.7*sqrt(col);

	    float r = length(pos.xyz);
	    col = mix(col,vec3(0.26,0.1,0.0),smoothstep(0.0,1.0,r/3.5));  		

    }



    col *= 0.25 + 0.75*pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.15 );

    fragColor = vec4(col,1.0);
}

#define pi  3.14159
#define ID2 vec2( 1., 0., 0., 1. )

struct Ellipse {

    vec2
        center,
        size;

    mat2
        loc;

    vec4
        col;
};

mat2
    rot( float p_angle ) {

        float
            c = cos( p_angle ),
            s = sin( p_angle );

        return mat2( c, s, -s, c );
    }

Ellipse
    createEllipse( in vec2 p_center, in float p_width, in float p_height, in float p_angle, in vec4 p_col ) {

    	return Ellipse( p_center, vec2( p_width, p_height ), rot( p_angle ), p_col );
    }

bool
    inEllipse( out float p_distSqr, in vec2 p_xy, in Ellipse p_ellipse ) {

        vec2
            dst = ( p_ellipse.loc * ( p_ellipse.center - p_xy ) ) * ( 1. /  p_ellipse.size );

        p_distSqr = dot( dst, dst );

        return ( p_distSqr < 1. );
    }

float
    t;

vec2
    xy;

#define NUM_OF_ELLIPSES 4

Ellipse
    ellipses[ NUM_OF_ELLIPSES ];

void
    init( in vec2 p_xy ) {

        t = iTime;
        xy = ( p_xy / iResolution.xy - .5 ) * vec2( iResolution.x / iResolution.y, 1. );

        ellipses[ 0 ] = createEllipse( vec2( +0., +0. ), .50, .25 , 0., vec4( .3 + .1 * sin( t ), .5 - .4 * sin( t ), .6 + .4 * sin( t ), 1. ) );
        ellipses[ 1 ] = createEllipse( vec2( +0., +0. ), .50, .50 , 0., vec4( .9 - .2 * sin( t ), .7 + .2 * sin( t ), .2 - .2 * sin( t ), 1. ) );
        ellipses[ 2 ] = createEllipse( vec2( +0., +0. ), .25, .25 , 0., vec4( .5 - .45 * sin( t ), 0., .5 + .45 * sin( t ), 1. ) );
        ellipses[ 3 ] = createEllipse( vec2( +0., +0. ), .35, .15 , 0., vec4( .6 + .3 * sin( 2.231 * t ), .5 + .45 * sin( 1.231 * t ), 0., 1. ) );
    }

void
    animate( ) {

        float
            t2 = mod( t, 45. );

        if( t2 < 5. ) {

        	ellipses[ 0 ].loc = rot( t ) * ellipses[ 0 ].loc;
        }
        else {

	        if( t2 < 20. ) {

                ellipses[ 0 ].loc = rot( t ) * ellipses[ 0 ].loc;
                ellipses[ 0 ].center = .1 + .3 * vec2( cos( t ), sin( t ) );
                ellipses[ 1 ].loc = rot( 2.1 * t ) * ellipses[ 1 ].loc;
                ellipses[ 1 ].center = ellipses[ 1 ].center + .3 * vec2( cos( t ), sin( t ) );
                ellipses[ 1 ].size = vec2( .5 + .2 * cos( .6 * t ), .5 + .2 * sin( .6 * t ) ); 
            }
            else {

                ellipses[ 0 ].loc = rot( t ) * ellipses[ 0 ].loc;
                ellipses[ 0 ].center = .2 * vec2( cos( t ), sin( t ) );
                ellipses[ 1 ].loc = rot( 2.1 * t ) * ellipses[ 1 ].loc;
                ellipses[ 1 ].center = ellipses[ 0 ].center + .3 * vec2( cos( t ), sin( t ) );
                ellipses[ 1 ].size = vec2( .5 + .2 * cos( .6 * t ), .5 + .2 * sin( .6 * t ) ); 
                ellipses[ 2 ].loc = rot( 4.1 * t ) * ellipses[ 2 ].loc;
                ellipses[ 2 ].center = ellipses[ 1 ].center + .2 * vec2( cos( 3. * t ), sin( 3. * t ) );
                ellipses[ 2 ].size = vec2( .5 + .2 * cos( 3.6 * t ), .5 + .2 * sin( 3.6 * t ) ); 
                ellipses[ 3 ].loc = rot( -2.1 * t ) * ellipses[ 2 ].loc;
                ellipses[ 3 ].center = ellipses[ 2 ].center + .2 * vec2( cos( 4. * t ), sin( 4. * t ) );
                ellipses[ 3 ].size = vec2( .2 + .1 * cos( 1.6 * t ), .2 + .1 * sin( 1.6 * t ) ); 
            }
        }
    }

vec4
    mixAll( ) {

        vec4
            col = vec4( 0. );

        float
            dist = 10000.;

        for( int i = 0; i < NUM_OF_ELLIPSES; i++ ) {

            if( inEllipse( dist, xy, ellipses[ i ] ) ) {

                col += ellipses[ i ].col * sqrt( 1. - dist );
            }
        }

        if( col.a < 1.01 - cos( .5 * t ) ) {

              col = vec4( .01, .02, .04,  1. );
        }
//        else {

        	//col /= col.a;
//        }

        return col;
    }


void 
    mainImage( out vec4 p_col, in vec2 p_xy ) {

        init( p_xy );

        animate( );

        p_col = mixAll( );

}

bool Is(vec2 uv,lowp vec2 A,lowp vec2 B,lowp vec2 C){
    lowp vec3 AB,BC,AC;
    return dot(cross(AB = vec3(B-A,0.),vec3(uv-A,0.)),cross(AB, AC = vec3(C-A,0.)))>0. && dot(cross(BC = vec3(C-B,0.),vec3(uv-B,0.)),cross(BC,-AB))>0. && dot(cross(-AC,vec3(uv-C,0.)),-cross(AC,BC))>0.; 
}

void mainImage( out vec4 f, in vec2 u )
{
