#define PI 3.14159265359

float radToAngle(float r){
    return mod(r *(180./PI), 360.);
}

float wedge(float low, float high, float current){ 
    float midpoint = mod(high-low,360.)/2.;        
    float a        = mod(current - low,360.);   
    return clamp(abs(midpoint - a)/midpoint, 0., 1.) ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - vec2(0.5,0.5);

    //Calculate the angle from the center.
    float angle = atan(uv.y,uv.x);
    float time = iTime * 100.;
    fragColor = vec4(0.3);
    fragColor = mix(vec4(1.), fragColor, wedge(time, time+20., radToAngle(angle)));
}

#define PI  3.14159265359
#define PIH PI/2.0

mat2 rotate(float a)
{
    float s=sin(a),c=cos(a);
    return mat2(c,s,-s,c);
}

mat3 lookat(in vec3 eye, in vec3 target)
{
	vec3 w = normalize(target-eye);
	vec3 u = normalize(cross(w,vec3(0.0,1.0,0.0)));
	vec3 v = normalize(cross(u,w));
    return mat3(u,v,w);
}

float gaussian(in float t)
{
    return exp(-t*t);
}

vec2 fold(in vec2 p, in float a)
{
    p.x = abs(p.x);
    vec2 v = vec2(cos(a),sin(a));
    for(int i=0;i<6;i++)
    {	
    	p -= 2.0*min(0.0,dot(p,v))*v;
    	v = normalize(vec2(v.x-1.0,v.y));
    }
 	return p;    
}

float map(in vec3 p)
{   
    float t = iTime*0.2;
 	float de = length(p)-1.0;
    de -= 0.015*gaussian(fold(p.xy*50.0,t+PIH).x);
    de -= 0.015*gaussian(fold(p.yz*50.0,t-PIH).x);
    de -= 0.015*gaussian(fold(p.zx*50.0,t).x);    
    return de;
}

vec3 doColor(in vec3 p)
{
    return normalize(abs(p));
}

vec3 calcNormal(in vec3 pos)
{
    vec2 e = vec2(1.0,-1.0)*0.001;
    return normalize(
        e.xyy*map(pos+e.xyy)+e.yyx*map(pos+e.yyx)+ 
        e.yxy*map(pos+e.yxy)+e.xxx*map(pos+e.xxx));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p2 = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec3 col = vec3(0.0);
    vec3 ro = vec3(0.8);
    vec3 rd = normalize(vec3(p2,2.0));
    ro.xz *= rotate(iTime*0.3);
    rd = lookat(ro, vec3(0.0))*rd;
    float maxd = 10.0;
    float t = 0.0, d;
    for(int i=0;i<64;i++)
    {
        t += d = map(ro+rd*t);
        if(d<0.001 || t>maxd) break;
    }
    if(t<maxd)
    {
        vec3 p = ro+t*rd;
        vec3 nor = calcNormal(p);
		col = doColor(p);
		vec3 li = normalize(vec3(2.0,3.0,3.0));
        float dif = clamp(dot(nor,li),0.0,1.0);
        float amb = max(0.5+0.5*nor.y,0.0);
        float spc = pow(clamp(dot(reflect(rd, nor),li),0.0,1.0),10.0);
        col *= clamp(0.0,1.0,max(dif,0.3)*amb+spc);
        col = mix(vec3(1.0),col,exp(-t*t*0.0015));
		col = pow(col, vec3(0.7));
    }
    fragColor = vec4(col, 1.0);
}

/*

This shader demonstrates the effect of gamma
correction in rendering.

Inspired by:
http://renderwonk.com/blog/index.php/archive/adventures-with-gamma-correct-rendering/

Move the mouse to see the difference between
shading with no gamma correction (on the left)
and with gamma correction (on the right).

See also: https://www.shadertoy.com/view/llBGz1

--
Zavie

*/

#define MAX_BOUNCES 2
float gamma = 2.2;

// ---8<----------------------------------------------------------------------
// Material

struct Material
{
    vec3 c;		// diffuse color
    float f0;	// specular color (monochrome)
};

// ---8<----------------------------------------------------------------------
// Geometry

struct Ray
{
    vec3 o;		// origin
    vec3 d;		// direction
};

struct Hit
{
    float t;	// solution to p=o+t*d
    vec3 n;		// normal
    Material m;	// material
};
const Hit noHit = Hit(1e10, vec3(0.), Material(vec3(-1.), -1.));

struct Plane
{
    float d;	// solution to dot(n,p)+d=0
    vec3 n;		// normal
    Material m;	// material
};

struct Sphere
{
	float r;	// radius
    vec3 p;		// center position
    Material m;	// material
};

Hit intersectPlane(Plane p, Ray r)
{
    float dotnd = dot(p.n, r.d);
    if (dotnd > 0.) return noHit;

    float t = -(dot(r.o, p.n) + p.d) / dotnd;
    return Hit(t, p.n, p.m);
}

Hit intersectSphere(Sphere s, Ray r)
{
	vec3 op = s.p - r.o;
    float b = dot(op, r.d);
    float det = b * b - dot(op, op) + s.r * s.r;
    if (det < 0.) return noHit;

    det = sqrt(det);
    float t = b - det;
    if (t < 0.) t = b + det;
    if (t < 0.) return noHit;

    return Hit(t, (r.o + t*r.d - s.p) / s.r, s.m);
}

bool compare(inout Hit a, Hit b)
{
    if (b.m.f0 >= 0. && b.t < a.t)
    {
        a = b;
        return true;
    }
    return false;
}

Hit intersectScene(Ray r)
{
    Sphere s = Sphere(1., vec3(0., 1., 0.), Material(vec3(1.0), 0.04));
    Plane p  = Plane(0., vec3(0., 1., 0.), Material(vec3(1.0), 0.04));

    Hit hit = noHit;
    compare(hit, intersectPlane(p, r));
    compare(hit, intersectSphere(s, r));
    return hit;
}

// ---8<----------------------------------------------------------------------
// Lights

struct DirectionalLight
{
    vec3 d;		// Direction
    vec3 c;		// Color
};

struct SpotLight
{
    vec3 p;		// Position
    vec3 d;		// Direction
    float fa;	// Falloff start cos
    float fb;	// Falloff end cos
    vec3 c;		// Color
};

DirectionalLight sunLight = DirectionalLight(normalize(vec3(-2., 1., 1.)), vec3(.1));
SpotLight spotLights[2];

void InitLights()
{
    float t = abs(fract(.2*iTime)*2.-1.);
    spotLights[0] = SpotLight(vec3(mix(-1.5, -0.2, t), 3., 1.), vec3(0., -1., 0.), .9, .902, vec3(1.));
    spotLights[1] = SpotLight(vec3(mix(+1.5, +0.2, t), 3., 1.), vec3(0., -1., 0.), .9, .95, vec3(1.));
}

vec3 skyColor(vec3 d)
{
    return vec3(0.01);
}

float pow5(float x) { return x * x * x * x * x; }

// Schlick approximation
float fresnel(vec3 h, vec3 v, float f0)
{
  return pow5(1. - clamp(dot(h, v), 0., 1.)) * (1. - f0) + f0;
}

float epsilon = 4e-4;

vec3 accountForDirectionalLight(vec3 p, vec3 n, DirectionalLight l)
{
    if (intersectScene(Ray(p + epsilon * l.d, l.d)).m.f0 < 0.)
    {
        return clamp(dot(n, l.d), 0., 1.) * l.c;
    }
    return vec3(0.);
}

vec3 accountForSpotLight(vec3 p, vec3 n, SpotLight l)
{
    vec3 d = normalize(l.p - p);
    float sqrDist = dot(l.p - p, l.p - p);
    Hit test = intersectScene(Ray(p + epsilon * d, d));

    if (test.t * test.t > sqrDist)
    {
        float atten = smoothstep(l.fa, l.fb, dot(l.d, -d)) / sqrDist;
        return atten * clamp(dot(n, d), 0., 1.) * l.c;
    }
    return vec3(0.);
}

vec3 radiance(Ray r)
{
    vec3 accum = vec3(0.);
    vec3 attenuation = vec3(1.);

    for (int i = 0; i <= MAX_BOUNCES; ++i)
    {
        Hit hit = intersectScene(r);

        if (hit.m.f0 >= 0.)
        {
            float f = fresnel(hit.n, -r.d, hit.m.f0);

            vec3 hitPos = r.o + hit.t * r.d;

            // Diffuse
            vec3 incoming = vec3(0.);
            incoming += accountForDirectionalLight(hitPos, hit.n, sunLight);
            incoming += accountForSpotLight(hitPos, hit.n, spotLights[0]);
            incoming += accountForSpotLight(hitPos, hit.n, spotLights[1]);

            accum += (1. - f) * attenuation * hit.m.c * incoming;

            // Specular: next bounce
            attenuation *= f;
            vec3 d = reflect(r.d, hit.n);
            r = Ray(r.o + hit.t * r.d + epsilon * d, d);
        }
        else
        {
            accum += attenuation * skyColor(r.d);
            break;
        }
    }
    return accum;
}

// ---8<----------------------------------------------------------------------
// Scene

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    InitLights();

    vec2 uv = 2. * fragCoord.xy / iResolution.xy - 1.;

    float o1 = 0.25;
    float o2 = 0.75;
    vec2 msaa[4];
    msaa[0] = vec2( o1,  o2);
    msaa[1] = vec2( o2, -o1);
    msaa[2] = vec2(-o1, -o2);
    msaa[3] = vec2(-o2,  o1);

    vec3 color = vec3(0.);
    for (int i = 0; i < 4; ++i)
    {
        vec3 p0 = vec3(0., 1.1, 4.);
        vec3 p = p0;
        //vec3 p = vec3(0., (2. * (iMouse.y==0.?.5*iResolution.y:iMouse.y) / iResolution.y - 1.), 0.) + p0;
        vec3 offset = vec3(msaa[i] / iResolution.y, 0.);
        vec3 d = normalize(vec3(iResolution.x/iResolution.y * uv.x, uv.y, -1.5) + offset);
        Ray r = Ray(p, d);
        color += radiance(r) / 4.;
    }

    float compareLimit = (iMouse.z > 0. ? iMouse.x : abs(fract(0.1*iTime) * 2. - 1.) * iResolution.x);
	if (compareLimit < fragCoord.x)
        fragColor = vec4(pow(color, vec3(1./gamma)),1.0);
    else
        fragColor = vec4(color*2.,1.0); // Fudge x2 factor to compensate for darkened result.
}

#define PI 3.14159265359

const vec2 constantList = vec2(1.0, 0.0);

float antiAlias(float x) {return (x-(1.0-2.0/iResolution.y))*(iResolution.y/2.0);}

float asrat;
struct Obj{    int type;    vec3 col;    vec2 pos;    float emis;}T_Obj;
struct Hit{    float d,t;    vec2 pos;    Obj obj;}T_Hit;
struct Ray{    vec2 ro,rd;    float tmin,tmax;}T_Ray;
const Obj NoObj=Obj(-1,vec3(0.),vec2(0.),0.);
const Hit NoHit=Hit(400.,400.,vec2(0.),NoObj);

float sdCircle(vec2 p){    return distance(p,vec2(0.));}
float sdBox(vec2 p, vec2 size, float radius)
{
    vec2 d = abs(p) - size-vec2(radius);
    return clamp(min(max(d.x, d.y), 0.0) + length(max(d, 0.0)) - radius,0.,1.);
}

Hit renderSdf(vec2 p,inout Obj o)
{
    Hit hit=NoHit;
    if(o.type==1) hit= Hit(sdCircle(p-o.pos),0.,p,o);
    if(o.type==2) hit= Hit(sdBox(p-o.pos,vec2(0.02),0.),090.1,p,o);
    return hit;
}
Hit sdU(in Hit a,vec2 p,in Obj d)
{
    Hit hit=renderSdf(p,d);
    if (a.d<hit.d) 
        return (a); else return (hit);
}

Hit map(vec2 p, in Obj oo)
{
	//----8<----- SOLIDS ----8<-----//

    const vec3 solidCol=vec3(0.95,0.57,0.);

    Hit hit=NoHit;
    hit=sdU(hit,p,Obj(1,solidCol,vec2(0.5*asrat,0.5),0.));
    hit=sdU(hit,p,Obj(1,solidCol,vec2(0.5*asrat,0.8),0.));
    hit=sdU(hit,p,Obj(2,solidCol,vec2(0.53*asrat,0.2),0.));
    hit=sdU(hit,p,Obj(2,solidCol,vec2(0.53*asrat,0.3),0.));
    hit=sdU(hit,p,Obj(2,solidCol,vec2(0.47*asrat,0.2),0.));
    hit=sdU(hit,p,Obj(2,solidCol,vec2(0.47*asrat,0.3),0.));
    return hit;
}

vec3 render(vec3 incol,inout Hit hit)
{
    float aares=clamp(antiAlias(smoothstep(((1.-hit.d)), .977, 0.)),0.,1.);
    if(aares==0.)
    {
        hit=NoHit;    
    }
    return mix(incol,hit.obj.col,aares);
}
Hit castRay(in Ray ray){
    float precis =.023;
    float t = ray.tmin;
    Hit hit=NoHit;
    vec2 p;
    for( int i=0; i<200; i++ )
    {
        p=ray.ro+ray.rd*t;
        hit = map( p,NoObj );
        if( hit.d<precis || t>ray.tmax ) break;
        t += hit.d;
    }
    hit.t=t;

    if( t>ray.tmax ) return NoHit;
    return hit;

}
float getFloor(vec2 uv){    return mod( floor(32.0*(uv.x/asrat)) + floor(18.0*(uv.y)), 2.0);}

vec3 getLight(vec2 uv, Obj light)
{
    float rot=atan(uv.y-light.pos.y, uv.x-light.pos.x);
    Hit hit=castRay(Ray(light.pos.xy,vec2(sin(rot),cos(rot)).yx,.0001,distance(light.pos,uv)));

    render(vec3(0.),hit);

    float lamnt=10.5*distance(uv,light.pos);
    lamnt=pow((1.2/(lamnt*lamnt)),.5)*0.39*light.emis;

    vec3 lcol=vec3(0.01);        
    if(hit==NoHit)
    {
        lcol+=light.col*lamnt;
    }

    lcol-=lamnt*light.col*0.5*getFloor(uv);


    return lcol;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 um = iMouse.xy / iResolution.xy;
    vec4 col = vec4(0.0);

    asrat=iResolution.x/iResolution.y;

    if(iMouse.z<0.5)
    {
        um=0.5+0.4*vec2(sin(iTime),cos(iTime));
    }

    uv.x*=asrat;um.x*=asrat;

    Hit hit=map(uv,NoObj);
    col.rgb +=render(vec3(0.),hit);

    if(hit==NoHit)
    {
        //----8<----- LIGHTS ----8<-----//

        col.rgb+=getLight(uv, Obj(1,vec3(0.5,1.,.5),um,1.));
        col.rgb+=getLight(uv, Obj(1,vec3(1.,.5,.5),vec2(asrat-um.x,1.-um.y),2.));
    }

    fragColor = col.xyzw * constantList.xxxy + constantList.yyyx;
}

// The formula for a heart

float heartFormula(vec3 pos){
  float EPISLON = 0.000001;

  float a = (pos.x * pos.x) + (9.0/4.0 * pos.z * pos.z) + pos.y * pos.y - 1.0;
  a = a * a * a;
  float b = pos.x * pos.x * pos.y * pos.y * pos.y;
  float c = 9.0/80.0 * pos.z * pos.z * pos.y * pos.y * pos.y;
  float d = a - b - c;

  return d - EPISLON;
}

// Given an origin and a normalized direction, shoot off a ray and raymarch

float castRay(vec3 rayOrigin, vec3 rayDirection ){

  float prevdist = 0.0;
  for( float t = 0.001; t < 4.0; t += 0.07) {

    vec3 p = rayOrigin + rayDirection * t;

    vec3 translate = vec3(sin(iTime), 0.0, cos(iTime) / 2.5 );

    float dist = heartFormula(p - translate);

    if (dist < 0.0 && prevdist > 0.0){
      return t;
    }

    prevdist = dist;
  }
  return 100.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


  // Convert the frag coord into its -1 to 1 representation

  vec2 q = gl_FragCoord.xy / iResolution.xy;
  vec2 p = -1.0 + 2.0 * q;
  p.x *= iResolution.x/iResolution.y;


  // We assume the screen is at 0 in distance and our camera is behind it
  float xp = 0.0;
  float zp = -1.5;

  vec3 camera = vec3(xp,0.0,zp);
  vec3 screen = vec3(p.x-xp,p.y,-1.1);

  // A quick optimisation to make sure the ray starts at the screen

  vec3 diff = screen - camera;
  vec3 dir = normalize(diff);
  float dist = length(diff);

  float val = castRay(camera + dir * dist, dir);

  val = clamp(1.0 - val,0.0,1.0);

  fragColor = vec4( val,0.1,0.1, val );

}

const float PI = 3.14159;

const float FALL_SPEED = 0.27;

const float W = 21.0;

const float H = 9.0;

const vec3 SNOW = vec3(255.0, 255.0,255.0) / 255.0;
const vec3 BACK = vec3(21.0, 21.0, 35.0) / 255.0;

float static_rand(vec2 seed)
{
    return fract(sin(dot(seed.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float rand(const float timeShift, const float index, const float timeStep)
{
    float f = floor((iTime + timeShift) / timeStep);

    vec2 v = vec2(mod(f + index, iResolution.y),
                  mod(f - index, iResolution.x));

    return static_rand(v);
}

float continuousRand(const float index, const float timeStep)
{
    float c00 = 0.5 *       rand(0.0     , index, timeStep);
    float c10 = 0.5 *       rand(timeStep, index, timeStep);
    float c01 = 2.0 * PI  * rand(0.0     , index + 0.5, timeStep);
    float c11 = 2.0 * PI  * rand(timeStep, index + 0.5, timeStep);

    float t = 0.5 + 0.5 * sin(PI * (fract(iTime / timeStep) - 0.5));

    return (1.0 - t) * c00 * sin(iTime / timeStep + c01) +
                  t  * c10 * sin(iTime / timeStep + c11);

}

vec2 plot(float i, float j)
{
    float x = (i + 0.5) / W;
    float y = (j + 0.5) / H;

    y -= iTime * FALL_SPEED;

    float dx = 0.65 * continuousRand(j * W + i, 5.0);
    float dy = 0.65 * continuousRand(i * H + j, 5.0);

    dx = sin(dx * 0.5 * PI);
    dy = sin(dy * 0.5 * PI);
    dx = sin(dx * 0.5 * PI);
    dy = sin(dy * 0.5 * PI);
    dx = sin(dx * 0.5 * PI);
    dy = sin(dy * 0.5 * PI);

    float fx = max(0.0, 0.7 - abs(dx));
    float fy = max(0.0, 0.7 - abs(dy));

    dx += 3.0 * fx * continuousRand(i * W + j * H, 1.5);
    dy += 3.0 * fy * continuousRand(i * H + j * W, 2.0);

    x += dx * 0.5 / W;
    y += dy * 0.5 / H;

    return vec2(x, fract(y));
}

vec2 distanceField(vec2 p)
{
    float rj = (p.y + iTime * FALL_SPEED) * H;

    float i = p.x * W;
    float j = mod(rj, H);

    float fi = floor(i);
    float fj = floor(j);

    float rd = static_rand(vec2(fi * W + floor(rj), 0.0));    
    float sr = 1.0 + sign(rd - 0.65); 

    float theta = 4.0 * PI * continuousRand(fi * W + fj, 1.0);
    float s1 = continuousRand(fi * W + fj * H, 1.1);
    float s2 = continuousRand(fj * H + fi, 0.2);

    mat2 rot2 = mat2(cos(theta), sin(theta), -sin(theta), cos(theta));

    vec2 d = p - plot(fi,fj);

    d *= rot2;

    d.x /= pow((1.0 - abs(s1)), 0.3);
    d.y /= 2.0 * pow(abs(s1), 0.3);

    float fieldx = sr + length(d) * (1.1 + pow(rd, 0.3));

    float fieldy = pow(abs(s2), 0.4);

    return vec2(fieldx, fieldy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.xy;

    vec2 df = distanceField(p);

    float o = smoothstep(0.0, 0.009, df.x);

	fragColor = vec4(o * BACK + (1.0 - o) * (BACK * df.y + SNOW * (1.0 - df.y)), 1.0);
}

//#define SHOW_DISTANCE

#define BACK_COL_TOP vec3(1,0,1)
#define BACK_COL_BOTTOM vec3(0,0,1)

#define TEXT_COL1_TOP vec3(0.05, 0.05, 0.40)
#define TEXT_COL1_BOTTOM vec3(0.60, 0.90, 1.00)
#define TEXT_COL2_TOP vec3(0.10, 0.10, 0.00)
#define TEXT_COL2_BOTTOM vec3(1.90, 1.30, 1.00)

//--- Primiives ---
float dfSemiArc(float rma, float rmi, vec2 uv)
{
	return max(abs(length(uv) - rma) - rmi, uv.x-0.0);
}

//p0 = bottom left, clockwise winding
float dfQuad(vec2 p0, vec2 p1, vec2 p2, vec2 p3, vec2 uv)
{
	vec2 s0n = normalize((p1 - p0).yx * vec2(-1,1));
	vec2 s1n = normalize((p2 - p1).yx * vec2(-1,1));
	vec2 s2n = normalize((p3 - p2).yx * vec2(-1,1));
	vec2 s3n = normalize((p0 - p3).yx * vec2(-1,1));

	return max(max(dot(uv-p0,s0n),dot(uv-p1,s1n)), max(dot(uv-p2,s2n),dot(uv-p3,s3n)));
}

float dfRect(vec2 size, vec2 uv)
{
	return max(max(-uv.x,uv.x - size.x),max(-uv.y,uv.y - size.y));
}
//-----------------

//--- Letters ---
void S(inout float df, vec2 uv)
{
	df = min(df, dfSemiArc(0.25, 0.125, uv - vec2(-0.250,0.250)));
	df = min(df, dfSemiArc(0.25, 0.125, (uv - vec2(-0.125,-0.25)) * vec2(-1)));
	df = min(df, dfRect(vec2(0.125, 0.250), uv - vec2(-0.250,-0.125)));
	df = min(df, dfQuad(vec2(-0.625,-0.625), vec2(-0.500,-0.375), vec2(-0.125,-0.375), vec2(-0.125,-0.625), uv));	
	df = min(df, dfQuad(vec2(-0.250,0.375), vec2(-0.250,0.625), vec2(0.250,0.625), vec2(0.125,0.375), uv));
}

void N(inout float df, vec2 uv)
{
	df = min(df, dfRect(vec2(0.250, 1.250), uv - vec2(-0.625,-0.625)));
    df = min(df, dfRect(vec2(0.250, 1.250), uv - vec2(-0.000,-0.625)));
	df = min(df, dfQuad(vec2(-0.375,-0.125), vec2(-0.375,0.125), vec2(0.000, 0.125), vec2(-0.125,-0.125), uv));	
}

void A(inout float df, vec2 uv)
{
	df = min(df, dfRect(vec2(0.250, 0.825), uv - vec2(-0.625,-0.625)));
    df = min(df, dfRect(vec2(0.250, 0.825), uv - vec2(-0.000,-0.625)));
	df = min(df, dfQuad(vec2(-0.375,-0.125), vec2(-0.375,0.125), vec2(0.000, 0.125), vec2(-0.125,-0.125), uv));	
    df = min(df, dfSemiArc(0.3125, 0.125, (uv.yx - vec2(0.1875,-0.1875)) * -1.0));
}

void D(inout float df, vec2 uv)
{
	df = min(df, dfRect(vec2(0.250, 1.25), uv - vec2(-0.625,-0.625)));
    df = min(df, dfSemiArc(0.5, 0.125, (uv.xy * vec2(-1,1) - vec2(0.375,-0.00))));
}

void E(inout float df, vec2 uv)
{
	df = min(df, dfRect(vec2(0.250, 1.250), uv - vec2(-0.625,-0.625)));    
    df = min(df, dfQuad(vec2(-0.375,-0.625), vec2(-0.375,-0.375), vec2(0.250, -0.375), vec2( 0.125,-0.625), uv));	
    df = min(df, dfQuad(vec2(-0.375,0.375), vec2(-0.375,0.625), vec2(0.250, 0.625), vec2(0.125, 0.375), uv));	   
    df = min(df, dfQuad(vec2(-0.375,-0.125), vec2(-0.375,0.125), vec2(0.000, 0.125), vec2(-0.125,-0.125), uv));	
}

void R(inout float df, vec2 uv)
{
	df = min(df, dfRect(vec2(0.250, 1.250), uv - vec2(-0.625,-0.625)));
    df = min(df, dfSemiArc(0.25, 0.125, (uv.xy * vec2(-1,1) - vec2(0.125,0.25))));    
