vec3 appleColor(in vec3 pos, in vec3 nor, out vec2 spe) // if spec changes, outer scope spec changes
{
    // what does in do? 
    spe.x = 1.0;
	spe.y = 1.0; 

	float a = atan(pos.x, pos.z);
	float r = length(pos.xz);

	//red
	vec3 col = vec3(1.0, 0.0, 0.0);

	// green
	float f = smoothstep(0.1, 1.0 , fbm(pos*1.0) ); 
	col = mix (col, vec3(0.8, 3.0, 0.2), f);

    // dirty 
    f = smoothstep(0.0, 1.0, fbm(pos*4.0)) ;
    col *= 0.8 + 0.2 * f;

    //freckles
    f = smoothstep(0.0, 1.0, fbm(pos*48.0));
    f = smoothstep (0.7, 0.9, f);
    col = mix(col , vec3(0.9, 0.9, 0.6), f*0.5);

    //stripes 
    f = fbm(vec3(a*7.0 + pos.z, 3.0* pos.y,pos.x))*2.0;
    f = smoothstep(0.2,1.0, f);
    col = mix(col, vec3(0.4,0.2,0.0), 0.5*f); // what is f in this case?
    spe.x *= 1.0 -0.35 *f ; 
    spe.y = 1.0 -0.5*f;

    //top 
     f = 1.0-smoothstep(0.14, 0.2, r ) ; 
     col = mix(col, vec3(0.6,0.6, 0.5), f);
     spe.x +=1.0-f;

    float ao = 0.5 +0.5*nor.y;
   	col *= ao *1.2;

	return col;

}
vec3 floorColor(in vec3 pos, in vec3 nor, out vec2 spe){
	spe.x = 1.0;
    spe.y = 1.0;
    vec3 col = vec3(0.5,0.4,0.3)*1.7;
    float f = fbm( 4.0 * pos* vec3 (6.0, 0.0, 0.5)) ;
    col = mix( col , vec3(0.3, 0.2, 0.1)*1.7, f);
	spe.y = 1.0 + 4.0*f ; 

    f = fbm (2.0*pos);
    col *= 0.7 + 0.3*f;

    // freckles 
    f = smoothstep(0.0,1.0,fbm(pos*48.0) );
    f = smoothstep(0.7, 0.9, f);
    col = mix(col , vec3(0.2) , f*0.75);

    // fake ao 
    f = smoothstep(0.1 , 1.55, length(pos.xz) );
    col *= f*f*1.4; 
    col.x += 0.1*(1.0-f); 
    return col; 
}
vec3 calcNormal( in vec3 p){
	vec3 e = vec3(0.001, 0.0, 0.0);
        vec3 n; 
    n.x = map(p+ e.xyy).x - map(p-e.xyy).x;
    n.y = map (p+e.yxy).x - map(p-e.yxy).x;
    n.z = map( p+ e.yyx).x - map(p-e.yyx).x;
    return normalize(n) ; 

}


float softshadow(in vec3 ro, in vec3 rd, float mint, float maxt, float k)
{
    float res = 1.0;
    float dt = 0.1;
    float t = mint;
    for( int i = 0; i < 30; i++){
    	float h = map(ro +rd*t).x;
    	h = max (h , 0.0);
        res = min(res, smoothstep(0.0, 1.0, k*h/t) );
        t += dt; 
        if(h <0.001) break;

    }
	return res;

}

vec2 intersect(vec3 ro, vec3 rd){
	float t = 0.0 ;
    float dt = 0.06;
    float nh = 0.0;
    float lh = 0.0;
    float lm = -1.0;

   for (int  i = 0; i < 128; i++ )
     {
       vec2 ma = map ( ro+ rd*t);
         nh = ma.x;
         if(nh > 0.0 ){ lh = nh; t += dt; } lm = ma.y;
     }
	if( nh > 0.0) return vec2(-1.0);
    t= t- dt*nh/(nh-lh);

        return vec2(t,lm);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
    vec2 q = fragCoord.xy/ iResolution.xy;
    vec2 p = (-1.0 +2.0 *q) ;
       p.x *= iResolution.x/iResolution.y; // aspect ratio, centers everything.
    //camera 
	vec3 ro = 2.5*normalize(vec3(cos(0.2),1.15+0.4*cos(time*0.5),
                                 sin(0.2*time)));
    //vec3 rd = normalize(vec3( (-1.0 +2.00*q)*vec2(1.77,1.0), - 1.5));
    vec3 ww = normalize(vec3(0.0,0.5,0.0) - ro );
    vec3 uu = normalize ( cross(vec3(0.0, 1.0, 0.0), ww)); 
    vec3 vv = normalize ( cross(ww,uu)); 
    vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww);
    // raymarch 
       vec3 col = vec3(0.88, 0.98, 1.0);

     vec2 tmat = intersect( ro, rd);
    //vec3 col = vec3(0.6);

    if(tmat.y > 0.5)
    {
        //geometry 
        vec3 pos = ro + tmat.x*rd;
        vec3 nor = calcNormal( pos);
        vec3 ref = reflect(rd,nor); 			// what does the reflect f(x) look like? 
        vec3 lig = normalize( vec3(1.0,0.8, 0.6)) ;


        float con = 1.0;
        float amb = 0.5 + 0.5*nor.y ;
        float dif = max( 0.0, dot(nor,lig) ) ;
        float bac = max(0.2+0.8*dot(nor, vec3( -lig.x, lig.y, -lig.z)), 0.0);
        float rim = pow(1.0 + dot(nor, rd), 3.0);
        float spe = pow(clamp(dot(lig,ref), 0.0, 1.0), 16.0);


        //shadow
        float sha = softshadow(pos, lig,0.06,4.0,4.0);

        // lights 
        col = 0.10*con*vec3(.8, 0.9,1.0);
        col += 0.70*dif*vec3(1.0,0.97,0.85)*vec3(sha, (sha+sha*sha)*0.5, sha*sha);
        col +=0.15*bac*vec3(1.0,0.97,0.85);
    	col +=0.20*amb*vec3(0.10,0.15,0.2);        

        // color 
        vec2 pro ; 
        if(tmat.y < 1.5) // what does this do? it looks like it limits the shader to only affect 
            	// the y part less than 1.5 of something. 
        col *= appleColor(pos, nor, pro ); // what is getting passed in? position, normal, ?
        else
        col *= floorColor(pos,nor,pro);

        // rim and spec
        col += 0.60*rim*vec3(1.0, 0.97, 0.85)*amb*amb;
        col += 0.60*pow(spe,pro.y)*vec3(1.0)*pro.x*sha;
        col = 0.3*col + 0.7*sqrt(col);
    }	
    col *= 0.25 + 0.75*pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.15 );
	fragColor = vec4(col,1.0);
}

