#define STEP 0.2
#define PI 3.14159265359


float rect(vec2 r, vec2 bottomLeft, vec2 topRight) {
	float ret;
	float d = 0.005;
	ret = smoothstep(bottomLeft.x-d, bottomLeft.x+d, r.x);
	ret *= smoothstep(bottomLeft.y-d, bottomLeft.y+d, r.y);
	ret *= 1.0 - smoothstep(topRight.y-d, topRight.y+d, r.y);
	ret *= 1.0 - smoothstep(topRight.x-d, topRight.x+d, r.x);
	return ret;
}



vec3 piece (vec3 col, vec2 r, vec2 pos, vec3 mainColor, vec3 borderColor, vec3 shadowColor)
{
	vec3 ret = col;

	vec2 bottomLeft = pos;
	vec2 topRight = pos + vec2(STEP, STEP);
	vec2 c = (bottomLeft+topRight)/2.0;
	float d = length(r - c);

	// Square border
	ret = mix(ret, borderColor, rect(r, bottomLeft, topRight) );

	// Square color
	vec2 margin = vec2(0.01, 0.01);
	ret = mix(ret, mainColor, rect(r, bottomLeft + margin, topRight - margin) );

	// Inner shadow
	ret = mix(ret, shadowColor, (3.0*sin(d))*rect(r, bottomLeft + margin , topRight - margin));

	// Inner square
	margin = vec2(0.035, 0.035);
	ret = mix(ret, mainColor, rect(r, bottomLeft + margin , topRight - margin));


	return ret;
}

vec2 up(vec2 pos)
{
	vec2 up = pos + vec2(0.0, +STEP);
	return up;
}

vec2 down(vec2 pos)
{
	vec2 down = pos + vec2(0.0, -STEP);
	return down;
}

vec2 left(vec2 pos)
{
	vec2 left = pos + vec2(-STEP, 0.0);
	return left;
}

vec2 right(vec2 pos)
{
	vec2 right = pos + vec2(+STEP, 0.0);
	return right;
}

vec3 piece1 (vec3 col, vec2 r, vec2 pos, float rot, vec3 colBlack, vec3 colShadow)
{
	vec3 ret = col;
	vec2 q = r - pos;

	float angle = rot;
	mat2 rotationMatrix = mat2(cos(angle), -sin(angle),
                               sin(angle),  cos(angle));

	float height = 2.0*STEP;
	float width = 2.0*STEP;

	vec2 c = vec2(width/2.0, height/2.0);

	q =  rotationMatrix*q;

	vec3 colPurple = vec3(0.724, 0.324, 0.728);

	vec2 start = -c/2.0;
	ret = piece(ret, q, start, colPurple, colBlack, colShadow);
	ret = piece(ret, q, up(start), colPurple, colBlack, colShadow);
	ret = piece(ret, q, left(up(start)), colPurple, colBlack, colShadow);

	return ret;
}

