vec3 invader (vec3 col, vec2 r, vec2 pos, float rot, vec3 colBlack, vec3 colShadow)
{
	vec3 ret = col;

	ret = piece3(ret, r, pos, rot + PI, colBlack, colShadow);
	ret = piece3(ret, r, pos + vec2(0.0, 0.6), rot, colBlack, colShadow);
	ret = pieceL(ret, r, pos + vec2(0.4, 0.6), rot + 3.0*PI/2.0, colBlack, colShadow);
	ret = piece4(ret, r, pos + vec2(0.8, 1.0), 0.0, colBlack, colShadow);
	ret = piece4(ret, r, pos + vec2(-0.8, 1.0), 0.0, colBlack, colShadow);
	ret = piecel(ret, r, pos + vec2(-0.8, 0.4), rot + PI/2.0, colBlack, colShadow);
	ret = pieceL(ret, r, pos + vec2(-1.2, 0.4), rot + PI, colBlack, colShadow);
	ret = piece2(ret, r, pos + vec2(-1.0, -0.2), rot + PI/2.0, colBlack, colShadow);
	ret = piece4(ret, r, pos + vec2(-0.6, -0.6), 0.0, colBlack, colShadow);
	ret = piece4(ret, r, pos + vec2(-0.4, -0.6), 0.0, colBlack, colShadow);
	ret = piece4(ret, r, pos + vec2(-0.6, -0.2), 0.0, colBlack, colShadow);
	ret = piece3(ret, r, pos + vec2(-0.2, 0.0), rot, colBlack, colShadow);
	ret = pieceLi(ret, r, pos + vec2(0.4, -0.2), rot + PI/2.0, colBlack, colShadow);
	ret = piece4(ret, r, pos + vec2(0.6, -0.6), 0.0, colBlack, colShadow);
	ret = piece4(ret, r, pos + vec2(0.4, -0.6), 0.0, colBlack, colShadow);
	ret = piece2i(ret, r, pos + vec2(0.8, -0.2), rot + PI/2.0, colBlack, colShadow);
	ret = pieceLi(ret, r, pos + vec2(1.2, 0.4), rot + PI, colBlack, colShadow);
	ret = pieceLi(ret, r, pos + vec2(-0.4, 0.6), rot + PI/2.0, colBlack, colShadow);
	ret = piecel(ret, r, pos + vec2(0.8, 0.4), rot + PI/2.0, colBlack, colShadow);

	return ret;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 r = (2.0*fragCoord.xy-iResolution.xy)/min(iResolution.y,iResolution.x);
	vec2 q = r;
	vec2 w = r;
	vec2 cursor = (2.0*iMouse.xy-iResolution.xy)/min(iResolution.y,iResolution.x);

	float xMax = iResolution.x/min(iResolution.y,iResolution.x);
	float yMax = iResolution.y/min(iResolution.y,iResolution.x);
	float distance = length(cursor);
	float cursorMod = (1.0-step(0.05*min(xMax,yMax), distance));
	float modif = 4.0 + ( cursorMod*sin(iTime*PI)*0.3 );

	r *= modif;
	cursor *= modif;


	// COLOR DEFINITIONS
	vec3 bgCol = vec3(1.0);
	vec3 colShadow = vec3(1.0) + ( 1.0-mod(length(cursor), max(iResolution.y, iResolution.x)) );
	vec3 colShadowInv = vec3(0.0);
	vec3 colBlack = vec3(0.0);
	vec3 colWhite = vec3(1.0);
	vec3 colGray = vec3(0.7);


	// GRID
	vec3 ret = colWhite;


	// BACKGROUND
	float t = iTime*0.05;
	vec2 br = r/modif;

	float v1 = sin(br.x +5.*t);
    float v2 = sin(br.y +3.*t);
    float v3 = sin(br.x+br.y +t);
    float v4 = sin(sqrt(br.x*br.x+br.y*r.y) +3.*t);
	float v = v1+v2+v3+v4;
	v *= 3.0;
	ret = vec3(0.2*sin(v), 0.1*sin(v+0.5*PI), 0.9*sin(v+1.0*PI));
	ret += vec3(0.8*sin(v+1.0*PI), 0.1*sin(v+3.5*PI), 0.1*sin(v));
	ret = 0.5*ret;

	br = 0.5*vec2(br.x*v/3.0, br.y*v/3.0);

	ret = mix(ret, texture(iChannel0, br).xyz, cursorMod*0.6*sin(iTime));

	// TETRIS PIECES
	float rot = 0.0;
	float factor = clamp(distance, 0.2, 1.0)-0.2;

	ret = mix(piece3(ret, r, vec2(-0.2, -0.7), rot + 3.0*PI/2.0, colBlack, colShadow), ret, factor);
	ret = mix(piece4(ret, r, vec2(-0.2, -0.5), 0.0, colBlack, colShadow), ret, factor);
	ret = mix(piecel(ret, r, vec2(-0.4, -0.3), rot, colBlack, colShadow), ret, factor);
	ret = mix(pieceLi(ret, r, vec2(0.2, -0.5), rot + PI, colBlack, colShadow), ret, factor);
	ret = mix(piece4(ret, r, vec2(0.6, -0.3), 0.0, colBlack, colShadow), ret, factor);
	ret = mix(piece4(ret, r, vec2(0.6, 0.1), 0.0, colBlack, colShadow), ret, factor);
	ret = mix(piece4(ret, r, vec2(0.4, 0.5), 0.0, colBlack, colShadow), ret, factor);
	ret = mix(piece4(ret, r, vec2(-0.6, 0.1), 0.0, colBlack, colShadow), ret, factor);
	ret = mix(piece4(ret, r, vec2(-0.6, 0.3), 0.0, colBlack, colShadow), ret, factor);
	ret = mix(pieceL(ret, r, vec2(-0.4, 0.3), rot + PI, colBlack, colShadow), ret, factor);
	ret = mix(pieceLi(ret, r, vec2(-1.0, 0.7), rot + PI, colBlack, colShadow), ret, factor);
	ret = mix(piece2i(ret, r, vec2(-0.4, 1.1), rot + PI, colBlack, colShadow), ret, factor);
	ret = mix(piece2(ret, r, vec2(0.4, 1.1), rot + PI, colBlack, colShadow), ret, factor);
	ret = mix(pieceL(ret, r, vec2(1.0, 0.7), rot + PI, colBlack, colShadow), ret, factor);
	ret = mix(piece2i(ret, r, vec2(0.6, 0.3), rot + PI/2.0, colBlack, colShadow), ret, factor);


	// DECORATION PIECES
	float y;
	float x;

	w.y -= 1.5;
	w *= 4.0;

	float speed = 1.0;
	t = iTime*speed;
	float stopEveryAngle = PI/2.0;
	float stopRatio = 1.0;
	rot = (floor(t) + smoothstep(0.0, 1.0-stopRatio, fract(t)) )*stopEveryAngle;

	y = -mod(iTime*0.2*PI - 2.0*PI, yMax*12.0);
	ret = piece2i(ret, w, vec2(-0.45*xMax*4.0, y), rot + PI/2.0, colBlack, colShadowInv);

	y = -mod(iTime*0.8*PI + 3.0*PI/2.0, yMax*12.0);
	ret = piece3(ret, w, vec2(0.8*xMax*4.0, y), rot + PI, colBlack, colShadowInv);

	t -= PI;
	rot = (floor(t) + smoothstep(0.0, 1.0-stopRatio, fract(t)) )*stopEveryAngle;
	y = -mod(iTime*0.5*PI + PI, yMax*12.0);
	ret = pieceL(ret, w, vec2(0.5*xMax*4.0, y), rot + PI, colBlack, colShadowInv);

	y = -mod(iTime*0.3*PI, yMax*12.0);
	ret = piece2(ret, w, vec2(-0.3*xMax*4.0, y), rot + PI, colBlack, colShadowInv);

	t -= PI/2.0;
	rot = (floor(t) + smoothstep(0.0, 1.0-stopRatio, fract(t)) )*stopEveryAngle;
	y = -mod(iTime*0.6*PI, yMax*12.0);
	ret = piecel(ret, w, vec2(-0.7*xMax*4.0, y), rot + PI, colBlack, colShadowInv);


	// DECORATION INVADERS -> performance!!!
	/*q.y -= 1.5;
	q *= 8.0;

	y = -mod(iTime*PI - 2.0*PI, yMax*25.0);
	x = (-0.6*xMax)*8.0+1.5*sin(iTime*PI);
	ret = invader(ret, q, vec2(x, y), PI, colBlack, colShadowInv);

	y = -mod(iTime*PI + 3.0*PI/2.0, yMax*25.0);
	x = (0.6*xMax)*8.0+1.5*sin(iTime*2.5*PI);
	ret = invader(ret, q, vec2(x, y), PI, colBlack, colShadowInv);*/


	// MOUSE
	ret = invader(ret, r, cursor, PI, colBlack, colShadow);


	vec3 pixel = ret;
	fragColor = vec4(pixel, 1.0);
}

float dot2( in vec2 v ) { 
    return dot(v,v);
}
float det(vec2 a, vec2 b) {
    return a.x * b.y - a.y * b.x;
}

// d1, d2 must be normalized
// if one of the planes is axis aligned, the terms are simpler
float sdMiter(vec2 p, vec2 d1, vec2 d2, float r) {
    float d1d2 = det(d1,d2);
    float sd1d2 = sign(d1d2);
    p += (d1 + d2) * r / (dot(d1,d2) + 1.0);

    float h1 = det(p,d1) * 0.5;
    float h2 = det(p,d2) * 0.5;
    vec2 l1 = p - vec2(d1.y,-d1.x) * (h1 + abs(h1)*sd1d2);
    vec2 l2 = p - vec2(d2.y,-d2.x) * (h2 - abs(h2)*sd1d2);

	return sqrt(min(dot2(l1),dot2(l2))) * sign(max(dot(p, d1),dot(p, d2))) - r;
}

float map (vec2 p) {
    float a = iTime * 0.3;
    float b = 3.14159 * 0.1 + iTime * 0.1;
    vec2 d1 = vec2(cos(a),sin(a)); // 1 0
    vec2 d2 = vec2(cos(b),sin(b)); // -0.70710678118655 0.70710678118655

    return sdMiter(p,d1,d2,(sin(iTime)*0.5+0.5)*0.5);
}

