vec3 rotate(vec3 p, float angle, vec3 axis){
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    mat3 m = mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
    return m * p;
}

float opS( float d1, float d2 ){
    return max(-d1,d2);
}

float opU( float d1, float d2 ) {
    return min(d1,d2);
}

float opI( float d1, float d2 ) {
    return max(d1,d2);
}

float box( vec3 p, vec3 b ) {
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) +
         length(max(d,0.0));
}

float dist(vec3 p){
    vec3 p0 = rotate(p, t * 2., vec3(0.0,1.0,0.0)) + vec3(-1.0, 0.0,0.0);
    vec3 p1 = rotate(p, t, vec3(0.0,1.0,0.0)) + vec3(1.0, 0.0,0.0);
    float d = 100.0;
    d = opI(box(p0, vec3(1.2)),
                opU(box(p, vec3(0.8))
                    , box(p1, vec3(0.8))
               	)
           );
    return d;
}


vec3 norm(vec3 p){
	vec2 d = vec2(0.001, 0.0);
    float di = dist(p);
	return normalize(vec3(di - dist(p - d.xyy), di - dist(p - d.yxy), di - dist(p - d.yyx)));
}

float shadow(vec3 o, vec3 d){
    o += norm(o) * 0.001;
	float len = 0.0, lev = 1.0;
	for(float t = 0.0; t < 32.0; t++){
		float di = dist(o + d * len);
		if (di < 0.001){ return 0.5;}
		lev = min(lev, di  * 24.0 / min(len, 1.0));
		len += di;
	}
	return max(0.5, lev) ;
}

vec3 phong(vec3 p, vec3 ray) {
    vec3 n = norm(p);
    return vec3(0.2,0.2,0.2) * clamp(dot(light, n), 0.0, 1.0)
		+ pow(clamp(dot(normalize(light - ray), n), 0.0, 1.0), 256.0) * 0.2;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ){
	vec2 p = (fragCoord.xy * 2.0 - res) / min(res.x, res.y);
	vec3 ray = normalize(cside * p.x + cup * p.y + cdir * 2.0);

	float len = 0.0, di;
	vec3 rhead = cpos;
	for(int i = 0; i < 64; i++){
		di = dist(rhead);
        if (abs(di) < 0.001) {
            break;
        }
		len += di;
		rhead = cpos + len * ray;
	}

	vec3 color = vec3(1.0);
	if(abs(di) < 0.001){
        color = phong(rhead, ray) * shadow(rhead, light);
	} 
	fragColor = vec4(color, 1.0);
}

#define PI 3.14159
#define TWO_PI 6.28319

const float Gold = (1.0+sqrt(5.0)) / 2.0;
const float InvGold = 1. / Gold;
const float OneMinusInvGold = 1.0 - InvGold;
const float OneMinusInvGoldSquared = OneMinusInvGold * OneMinusInvGold;

float superformula (float angle, vec4 shape, vec2 symmetry) {    
    float m = shape.x;
    float n1 = shape.y;
    float n2 = shape.z;
    float n3 = shape.w;
    float a = symmetry.x;
    float b = symmetry.y;

    float rpart = (m * angle) / 4.;
    float apart = abs (cos (rpart) / a);
    float bpart = abs (sin (rpart) / b);

    float r = pow (
        pow (apart, n2) + pow (bpart, n3),
        -1. / n1
    );

    return r;
}

float angleBetween (vec2 a, vec2 b) {
    float cosTheta = dot (a, b) / (length (a) * length (b));

    return acos (cosTheta);
}

vec4 createShape (float t) {    
    float m = 1. + abs (sin (t) + cos (1.6 * t)) * 8.;
    float n1 = 1. + abs (sin (2. * t) * 4.);
    float n2 = 1. + (cos (t + PI / 1.6) * 2.);
    float n3 = 2. + sin (1.6 * t + PI) * 4.;

    return vec4 (m, n1, n2, n3);
}

vec2 createSymmetry (float t) {
    return  vec2 (.618 + sin (t + PI / 2.) * .5, .5 + cos (t) * .618);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
    float restraint = Gold * PI;
    float radius = iResolution.y / restraint;
    float border = 23.07;
    vec4 borderColor = vec4 (0.618, 0.123, 0.987, 1.0);

    vec2 center = iResolution.xy / 2.;
    vec2 d = fragCoord - center;
    vec2 up = vec2 (0., 1.);
    vec2 uv = fragCoord.xy / iResolution.xy;

    float t = iTime * 0.6;
    float angle = angleBetween (up, d);
    vec2 symmetry = createSymmetry (t);
    vec4 shape = createShape (t);
    float r = superformula (angle, shape, symmetry) * radius;

    float size = length (d); 
    vec4 colorFromShape = 1.-normalize (shape);

    if (size <= r) {
        vec4 noise = texture(iChannel0, uv + vec2(iTime, iTime/2.0));
        float much = (r-size);
        fragColor = vec4(colorFromShape.rgb, much) + (noise*InvGold);
    }
    else if (size <= (r+border)) {
        float much = ((r+border)-size)/border;
        fragColor = mix(borderColor, colorFromShape, much);
    }
    else {
        vec4 noise = texture(iChannel0, uv + vec2(fract(iTime), tan(iTime/2.0)));
        fragColor = vec4 (vec3(OneMinusInvGoldSquared), 1.) + OneMinusInvGoldSquared*vec4((.5*sin(iTime)+.5)*noise.rgb, 1.0);
    }
}

