    // to edges of triangle/points of triangle ?
    vec2 v0 = coord - point0;
    vec2 v1 = coord - point1;
    vec2 v2 = coord - point2;

    // I don't know what these do yet xD
    vec2 pq0 = v0 - edge0 * saturate(dot(v0, edge0) / dot(edge0, edge0));
    vec2 pq1 = v1 - edge1 * saturate(dot(v1, edge1) / dot(edge1, edge1));
    vec2 pq2 = v2 - edge2 * saturate(dot(v2, edge2) / dot(edge2, edge2));

    // or these either xD xD
    float s = sign(edge0.x*edge2.y - edge0.y*edge2.x);
    vec2 d = min(min(vec2(dot(pq0, pq0), s*(v0.x*edge0.y-v0.y*edge0.x)),
                     vec2(dot(pq1, pq1), s*(v1.x*edge1.y-v1.y*edge1.x))),
                 	 vec2(dot(pq2, pq2), s*(v2.x*edge2.y-v2.y*edge2.x)));

    return -sqrt(d.x)*sign(d.y);
}

