// Convert r, g, b to normalized vec3
vec3 rgb(float r, float g, float b) {
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}

// Defined by apothem.
vec4 poly1(vec2 uv, vec2 pos, float mid, vec3 color, float n) {
  vec2 trans = pos - uv;
  float a = atan(trans.x,trans.y);
  float r = length(trans);

  float i = 2.*acos(-1.)/n;
  float th= mod(a, i) - (i/2.0);
  float l = mid/cos(th);

  float t = clamp(r-l, 0.0, 1.);
  return vec4(color, 1. -t);
}

// Lerp between the boundaries of the first wedge.
vec4 poly2(vec2 uv, vec2 pos, float rad, vec3 color, float n) {
  vec2 trans = pos - uv;
  float a = atan(trans.x,trans.y);

  float i = 2.*acos(-1.)/n; // Wedge angle is 2pi/n.
  float th = mod(a,i); // Where does trans sit in wedge?

  vec2 bound = mix(vec2(rad, 0.), vec2(rad*cos(i), rad*sin(i)), th/i);

  float t = clamp(length(trans)-length(bound), 0.0, 1.);
  return vec4(color, 1. -t);
}

// Law of sines.
vec4 poly3(vec2 uv, vec2 pos, float rad, vec3 color, float n) {
  float pi = acos(-1.);
  vec2 trans = pos - uv;
  float a = atan(trans.x,trans.y);
  float r = length(trans);

  float i = 2.*pi/n; // wedge angle

  float th = mod(a, i);
  float b = (pi - i) / 2.0;
  float c = pi - th - b;
  float l = (sin(b) * rad)/sin(c);

  float t = clamp(r-l, 0.0, 1.);
  return vec4(color, 1. -t);
}

// Rotate first triangle until side is perp with x-axis.
// Maintain y bound.
vec4 poly4(vec2 uv, vec2 pos, float rad, vec3 color, float n) {
  float pi = acos(-1.);
  vec2 trans = pos - uv;
  float a = atan(trans.x,trans.y);
  float r = length(trans);

  float th = mod(a, 2.*pi/n);
  th += pi/2. - pi/n;
  r = r*sin(th);

  float l = rad *cos(pi/n); // get radius from apothem
  float t = clamp(r-l, 0.0, 1.);
  return vec4(color, 1. -t);
}

