const vec2 gridSize = vec2(2.,2.);//grid size (columns, rows)

// encode id from coord // s:screenSize / h:pixelCoord / sz=gridSize
float EncID(vec2 s, vec2 h, vec2 sz) 
{
    float cx = floor(h.x/(s.x/sz.x));
    float cy = floor(h.y/(s.y/sz.y));
    return cy*sz.x+cx;
}

// return id / uv
vec3 getcell(vec2 s, vec2 h, vec2 sz) 
{
    float cx = floor(h.x/(s.x/sz.x));
    float cy = floor(h.y/(s.y/sz.y));

    float id = EncID(s,h,sz);

    vec2 size = s/sz;
    float ratio = size.x/size.y;
    vec2 uv = (2.*(h)-size)/size.y - vec2(cx*ratio,cy)*2.;

    return vec3(id, uv);
}

void mainImage( out vec4 f, in vec2 g )
{
	f.xyz = iResolution;

    vec4 p = vec4((g+g-f.xy)/f.y,0,1), r = p-p, q = r, m = iMouse, c;

    if(m.z>0.) 
    {
        c.x = EncID(f.xy,m.xy,gridSize);
        c.yz = p.xy;
    }
    else
    {
        c.xyz = getcell(f.xy,g,gridSize);
        p.xy = c.yz;
    }

	float k = 0.;
	if (c.x == 0.) k = .258;
	if (c.x == 1.) k = .276;
	if (c.x == 2.) k = .282;
	if (c.x == 3.) k = .3;

    q.w += iTime * 0.3 + 1.;

    // i is the color of pixel while hit 0. => 1.
	for (float i=1.; i>0.; i-=.01) 
	{
        float d=0.,s=1.;

        for (int j = 0; j <3; j++)
		{
			r = abs( mod(q * s + 1.,2.) - 1. );
            d = max(d, (k - length( sqrt(r * .6) ) * .3) / s );
			s *= 3.;
		}

        q += p * d;

        f = f - f + i;

        if(d < 1e-5) break;
    }
}

float prng (in vec3 n) {
    n*=0.03;
 	return fract(sin(dot(n.xyz ,vec3(12.9898,78.233,119.569))) * 43758.5453);  
}

float terrain(vec3 p){
    p.xy += vec2(sin(p.z*0.2), cos(p.z*0.2))*4.0;
    return prng(p)*6.0 + dot(p.xy, p.xy)*0.2 - 7.0 - sin(p.z*0.04);
}

vec3 march(vec3 ro, vec3 rd){

    vec3 dt = abs(1.0 / rd);
    vec3 n = vec3(0.0);

    ivec3 p = ivec3(floor(ro));

    float t = 0.0;

    n = max(vec3(0.0), 1.0 / rd) - fract(ro) / rd;


    ivec3 inc = ivec3(sign(rd));

    vec3 surf = vec3(0);

    for (int i = 0; i < 30; i++){
        if(n.x < n.y) {
            if(n.x < n.z) {
                p.x += inc.x;
                t = n.x;
                n.x += dt.x;
                surf = vec3(1.0, 0.0, 0.0);
            } else {
                p.z += inc.z;
                t = n.z;
                n.z += dt.z;
                surf = vec3(0.0, 0.0, 1.0);
            }
        } else {
            if(n.y < n.z) {
                p.y += inc.y;
                t = n.y;
                n.y += dt.y;
                surf = vec3(0.0, 1.0, 0.0);
            } else {
                p.z += inc.z;
                t = n.z;
                n.z += dt.z;
                surf = vec3(0.0, 0.0, 1.0);
            }
        }

        if (terrain(vec3(p)) > 0.9) break;
    }

    float b = 1.0/(t*t*0.02+1.0);
    b *= abs(dot(rd, surf))*0.7+0.3;

    vec4 col = vec4(1.0);

    ro = ro + rd * t;
    ro *= 0.075;

    col =  texture(iChannel1, ro.xy+ro.z);
    col =  0.25 * col + 0.8 * col * texture(iChannel0, ro.xy+ro.z);


    col = sqrt(col);

    col = col * vec4(0.7, 0.75, 0.6, 0.0) + vec4(0.35, 0.3, 0.25, 1.0);

    return col.xyz * b ;
}

