const int renderDepth = 400;
const bool showRenderDepth = false;

const vec3 background = vec3(0.2, 0.2, 0.8);
const vec3 white = vec3(0.8, 0.8, 0.8);
const vec3 black = vec3(0, 0, 0);
const vec3 red = vec3(0.8, 0.2, 0.2);
const vec3 green = vec3(0.2, 0.8, 0.2);
const vec3 lightPos = vec3(0, 10, 10);

const float a = 1.0;
const float b = 3.0;
vec3 forces[3];


vec3 getRayDir(vec3 camDir, vec2 fragCoord) {
  vec3 yAxis = vec3(0, 1, 0);
  vec3 xAxis = normalize(cross(camDir, yAxis));
  vec2 q = fragCoord / iResolution.xy;
  vec2 p = 2.0 * q - 1.0;
  p.x *= iResolution.x / iResolution.y;
  return normalize(p.x * xAxis + p.y * yAxis + 5.0 * camDir);
}

// http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdBox(vec3 p, vec3 b) {
  vec3 d = abs(p) - b;
  return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0)) - 0.1;
}

float sdPlane(vec3 p, vec4 n) {
  return dot(p,n.xyz) + n.w;
}

float getMetaball(vec3 p, vec3 v) {
  float r = length(p - v);
  if (r < b / 3.0) {
    return a * (1.0 - 3.0 * r * r / b * b);
  } else if (r < b) {
    return (3.0 * a / 2.0) * (1.0 - r / b) * (1.0 - r / b);
  } else {
    return 0.0;
  }
}

float sdImplicitSurface(vec3 p) {
  float mb = 0.0;
  float minDist = 10000.0;
  for (int i = 0; i < 3; i++) {
    mb += getMetaball(p, forces[i]);
    minDist = min(minDist, length(p - forces[i]));
  }
  if (minDist > b) {
    return max (minDist - b, b - 1.2679529);
  } else if (mb == 0.0) {
    return b - 1.2679529;  // 1.2679529 is the x-intercept of the metaball expression - 0.5.
  } else {
    return b - sqrt(6.0 * mb) - 1.2679529;
  }
}

float getSdf(vec3 p) {
  float f = sdImplicitSurface(p);
  for (int i = 0; i < 6; i++) {
    float t = float(i) + iTime;
    f = min(f, sdBox(
        p - vec3(
            3.0 + 3.0 * cos(t * 3.141592 / 3.0), 
            0, 
            3.0 * sin(t * 3.141592 / 3.0)),
        vec3(0.5)));
  }
  return f;
}

float getSdfWithPlane(vec3 p) {
  return min(getSdf(p), sdPlane(p, vec4(0,1,0,1)));
}

float diffuse(vec3 point,vec3 normal) {
  return clamp(dot(normal, normalize(lightPos - point)), 0.0, 1.0);
}

float getShadow(vec3 pt) {
  vec3 lightDir = normalize(lightPos - pt);
  float kd = 1.0;
  int step = 0;
  float t = 0.1;

  for (int step = 0; step < renderDepth; step++) {
    float d = getSdf(pt + t * lightDir);
    if (d < 0.001) {
      kd = 0.0;
    } else {
      kd = min(kd, 16.0 * d / t);
    }
    t += d;
    if (t > length(lightPos - pt) || step >= renderDepth || kd < 0.001) {
      break;
    }
  }
  return kd;
}

vec3 getGradient(vec3 pt) {
  return vec3(
    getSdfWithPlane(vec3(pt.x + 0.0001, pt.y, pt.z)) - getSdfWithPlane(vec3(pt.x - 0.0001, pt.y, pt.z)),
    getSdfWithPlane(vec3(pt.x, pt.y + 0.0001, pt.z)) - getSdfWithPlane(vec3(pt.x, pt.y - 0.0001, pt.z)),
    getSdfWithPlane(vec3(pt.x, pt.y, pt.z + 0.0001)) - getSdfWithPlane(vec3(pt.x, pt.y, pt.z - 0.0001)));
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 colorWheel(float angle) {
    return hsv2rgb(vec3(angle, 1.0, 0.8));
}

vec3 getDistanceColor(vec3 pt) {
  return colorWheel(getSdf(pt) / 2.0);
}

vec3 illuminate(vec3 pt) {
  vec3 color = (abs(pt.y + 1.0) < 0.001) ? getDistanceColor(pt) : white;
  vec3 gradient = getGradient(pt);
  float diff = diffuse(pt.xyz, normalize(gradient));
  return (0.25 + diff * getShadow(pt))  * color;
}

vec3 raymarch(vec3 rayorig, vec3 raydir) {
  vec3 pos = rayorig;
  float d = getSdfWithPlane(pos);
  int work = 0;

  for (int step = 0; step < renderDepth; step++) {
    work++;
    pos = pos + raydir * d;
    d = getSdfWithPlane(pos);
    if (abs(d) < 0.001) {
      break;
    }
  }

  return showRenderDepth
    ? vec3(float(work) / float(renderDepth))
    : (abs(d) < 0.001) 
      ? illuminate(pos)
      : background;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  forces[0] = vec3(-3, 0, 0);
  forces[1] = vec3(3.0 * sin(iTime), 4.0 * abs(cos(iTime)), 0.0);
  forces[2] = vec3(3, 0, 0);

  vec3 camPos = 20.0 * vec3(cos(iTime / 10.0), 0.5, sin(iTime / 10.0));
  vec3 camDir = normalize(-camPos);
  fragColor = vec4(raymarch(camPos, getRayDir(camDir, fragCoord)), 1.0);
}


void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 rayOrigin, in vec3 rayDirection ) {
  forces[0] = vec3(-3, 0, 0);
  forces[1] = vec3(3.0 * sin(iTime), 4.0 * abs(cos(iTime)), 0.0);
  forces[2] = vec3(3, 0, 0);
  fragColor = vec4(raymarch(rayOrigin * 10.0 + vec3(0.0, 3.0, 6.0), rayDirection), 1.0);
}

#define pi            3.1415926535
#define _t_           .5 * iTime
#define ID_ARROW      0
#define ID_ELLIPSE    1
#define NUM_OF_ARROWS 6

struct Object { int id; vec2 pos; mat2 loc; vec4 col; };
struct Scene { vec2 xy; Object ellipse, arrows[ NUM_OF_ARROWS ]; };

Scene
    scene;

mat2
    rotMat( in float p_angle ) {

        float
            c = cos( p_angle ),
            s = sin( p_angle );

        return mat2( c, s, -s, c );
    }


mat2
    invMat( in mat2 p_mat ) {

        float
            det = p_mat[ 0 ][ 0 ] * p_mat[ 1 ][ 1 ] - p_mat[ 1 ][ 0 ] * p_mat[ 0 ][ 1 ];

        return mat2( p_mat[ 1 ][ 1 ], -p_mat[ 1 ][ 0 ], -p_mat[ 0 ][ 1 ], p_mat[ 0 ][ 0 ] ) / det;
    }

void
    init( vec2 p_xy ) {

        scene.xy = vec2( p_xy / iResolution.xy - .5 ) * vec2( iResolution.x / iResolution.y, 1. );
        scene.ellipse =     Object( ID_ELLIPSE, vec2( +.25, +0. ), mat2( +.3, +0., +0., +.2 ), vec4( .7, .7, .7, 1. ) );
        scene.arrows[ 0 ] = Object( ID_ARROW,   vec2( +0., +0. ), mat2( +.04, +0., +0., +.01 ), vec4( 1., .0, 0., 1. ) );
        scene.arrows[ 1 ] = Object( ID_ARROW,   vec2( +0., +0. ), mat2( +0., +.04, -.01, +0. ), vec4( 0., 0., 1., 1. ) );
        scene.arrows[ 2 ] = Object( ID_ARROW,   vec2( +0., +0. ), mat2( +0., +.4, -.01, +0. ), vec4(  .125, .25, .125, 1. ) );
        scene.arrows[ 3 ] = Object( ID_ARROW,   vec2( +0., +0. ), mat2( +.8, +0., +0., +.02 ), vec4( .25, .125, .125, 1. ) );
        scene.arrows[ 4 ] = Object( ID_ARROW,   vec2( +0., +0. ), mat2( +0., +.4, -.01, +0. ), vec4( 0., 1., 0., 1. ) );
        scene.arrows[ 5 ] = Object( ID_ARROW,   vec2( +0., +0. ), mat2( +.25, +0., +0., +.01 ), vec4( .9, .9, .9, 1. ) );
    }

vec4
    intersectArrow( in Object p_obj ) {

        float
            border = .5 + .4 * sin( _t_ );

        vec2
            d  = ( scene.xy - p_obj.pos ) * p_obj.loc;

        d.x /= dot( p_obj.loc[ 0 ], p_obj.loc[ 0 ] );
        d.y /= dot( p_obj.loc[ 1 ], p_obj.loc[ 1 ] );

        if( 0. <= d.x && d.x < border ) {

            if( abs( d.y ) < .3 ) return p_obj.col;
            else if( abs( d.y ) < 1. ) return vec4( .3 * p_obj.col.rgb, 1. );
        }

        d.x -= border;
        border = 1. - border;

        if( 0. <= d.x && d.x < border ) {

            if( abs( d.y ) < ( 1. - d.x / border ) ) return p_obj.col;
            else  if( abs( d.y ) < 1. ) return vec4( .3 * p_obj.col.rgb, 1. );
        }

        return vec4( .0 );
    }

vec4
    intersectEllipse( in Object p_obj ) {

        vec2
            d  = ( scene.xy - p_obj.pos ) * p_obj.loc;

        d.x /= dot( p_obj.loc[ 0 ], p_obj.loc[ 0 ] );
        d.y /= dot( p_obj.loc[ 1 ], p_obj.loc[ 1 ] );


        if( abs( d.x ) <= 1. && abs( d.y ) < 1. ) {

	        if( dot( d, d ) < 1. ) return p_obj.col;

            return vec4( .3 * p_obj.col.rgb, 1. );
        }

        return vec4( 0. );
    }

vec4
    intersect( in Object p_obj ) {

        if( p_obj.id == ID_ARROW ) {

            return intersectArrow( p_obj );
        }

        if( p_obj.id == ID_ELLIPSE ) {

            return intersectEllipse( p_obj );
        }

        return vec4( 0. );
    }

vec4
    render( ) {

        vec4
            col = vec4( 0., 0., 0., 1. );

        col += intersect( scene.ellipse );

        for( int i = 0; i < NUM_OF_ARROWS; i++ ) {

            col += intersect( scene.arrows[ i ] );
        }
        //col /= col.a;
        return vec4( .5 * col.rgb, 1. );
    }

void
    animate( ) {

        mat2
            rot1 = rotMat( _t_ * pi / 180. ),
            rot90 = rotMat( pi / 2. ),
            rot270 = rotMat( 3. * pi / 2. );

        mat2
            rotT0 = rotMat( 1.3 * _t_ ),
            rotT1 = rotMat( _t_ ),
            rotT2 = rotMat( .1 * pi * sin( _t_ ) ),
            rotT3 = rotMat( pi * sin( 5. / 3. * _t_ ) );

        scene.ellipse.loc = rotT3 * scene.ellipse.loc;
		scene.ellipse.pos = rotT0 * scene.ellipse.pos;

        scene.arrows[ 0 ].loc = scene.ellipse.loc;
        scene.arrows[ 0 ].pos = scene.ellipse.pos;

        scene.arrows[ 1 ].loc = rot90 * rotT1 *  scene.arrows[ 0 ].loc;
        scene.arrows[ 1 ].pos = scene.ellipse.pos;

        scene.arrows[ 2 ].loc = rotT2 * scene.arrows[ 2 ].loc;

        scene.arrows[ 3 ].loc = rotT2 * scene.arrows[ 3 ].loc;

        scene.arrows[ 4 ].pos = scene.ellipse.pos;
        scene.arrows[ 4 ].loc = scene.ellipse.loc * rotT1;

        scene.arrows[ 5 ].loc = rotT0 * scene.arrows[ 5 ].loc;
    }

void
    mainImage( out vec4 p_c, in vec2 p_xy ) {

        init( p_xy );

        animate( );

        p_c = render( );
    }

