	const vec2 e = vec2(0.005, 0);
	return normalize(vec3(map(p + e.xyy) - map(p - e.xyy), map(p + e.yxy) - map(p - e.yxy),	map(p + e.yyx) - map(p - e.yyx)));
}
*/

// Ambient occlusion, for that self shadowed look.
// XT95 came up with this particular version. Very nice.
//
// Hemispherical SDF AO - https://www.shadertoy.com/view/4sdGWN
// Alien Cocoons - https://www.shadertoy.com/view/MsdGz2
float calculateAO(in vec3 p, in vec3 n){

	float ao = 0.0, l;
	const float nbIte = 6.0;
	const float falloff = 1.;

    const float maxDist = 1.;
    for(float i=1.; i<nbIte+.5; i++){

        l = (i + hash(i))*.5/nbIte*maxDist;
        ao += (l - map( p + n*l ))/ pow(1. + l, falloff);
    }

    return clamp( 1.-ao/nbIte, 0., 1.);
}


// Cheap shadows are hard. In fact, I'd almost say, shadowing repeat objects - in a setting like this - with limited 
// iterations is impossible... However, I'd be very grateful if someone could prove me wrong. :)
float softShadow(vec3 ro, vec3 lp, float k){

    // More would be nicer. More is always nicer, but not really affordable... Not on my slow test machine, anyway.
    const int maxIterationsShad = 16; 

    vec3 rd = (lp-ro); // Unnormalized direction ray.

    float shade = 1.0;
    float dist = 0.05;    
    float end = max(length(rd), 0.001);
    float stepDist = end/float(maxIterationsShad);

    rd /= end;

    // Max shadow iterations - More iterations make nicer shadows, but slow things down. Obviously, the lowest 
    // number to give a decent shadow is the best one to choose. 
    for (int i=0; i<maxIterationsShad; i++){

        float h = map(ro + rd*dist);
        //shade = min(shade, k*h/dist);
        shade = min(shade, smoothstep(0.0, 1.0, k*h/dist)); // Subtle difference. Thanks to IQ for this tidbit.
        //dist += min( h, stepDist ); // So many options here: dist += clamp( h, 0.0005, 0.2 ), etc.
        dist += clamp(h, 0.02, 0.25);

        // Early exits from accumulative distance function calls tend to be a good thing.
        if (h<0.001 || dist > end) break; 
    }

    // I've added 0.5 to the final shade value, which lightens the shadow a bit. It's a preference thing.
    return min(max(shade, 0.) + 0.5, 1.0); 
}

/*
// Cool curve function, by Shadertoy user, Nimitz.
//
// It gives you a scalar curvature value for an object's signed distance function, which 
// is pretty handy for all kinds of things. Here's it's used to darken the crevices.
//
// From an intuitive sense, the function returns a weighted difference between a surface 
// value and some surrounding values - arranged in a simplex tetrahedral fashion for minimal
// calculations, I'm assuming. Almost common sense... almost. :)
//
// Original usage (I think?) - Cheap curvature: https://www.shadertoy.com/view/Xts3WM
// Other usage: Xyptonjtroz: https://www.shadertoy.com/view/4ts3z2
float curve(in vec3 p){

    //const float eps = 0.05, amp = 4.0, ampInit = 0.5;
    const float eps = 0.15, amp = 2.5, ampInit = 0.0;

    vec2 e = vec2(-1., 1.)*eps; //0.05->3.5 - 0.04->5.5 - 0.03->10.->0.1->1.

    float t1 = map(p + e.yxx), t2 = map(p + e.xxy);
    float t3 = map(p + e.xyx), t4 = map(p + e.yyy);

    return clamp((t1 + t2 + t3 + t4 - 4.*map(p))*amp + ampInit, 0., 1.);
}
*/

void mainImage( out vec4 fragColor, in vec2 fragCoord ){


    // Unit direction ray vector: Note the absence of a divide term. I came across this via a comment 
    // Shadertoy user "Coyote" made. I'm pretty happy with this.
    vec3 rd = (vec3(2.*fragCoord - iResolution.xy, iResolution.y)); // Normalizing below.

    // Barrel distortion. Looks interesting, but I like it because it fits more of the scene in.
    // If you comment this out, make sure you normalize the line above.
    rd = normalize(vec3(rd.xy, sqrt(max(rd.z*rd.z - dot(rd.xy, rd.xy)*.2, 0.))));

    // Rotating the ray with Fabrice's cost cuttting matrix. I'm still pretty happy with this also. :)
    vec2 m = sin(vec2(0, 1.57079632) + iTime/4.);
    rd.xy = mat2(m.y, -m.x, m)*rd.xy;
    rd.xz = mat2(m.y, -m.x, m)*rd.xz;


    // Ray origin, set off in the Z direction.
    vec3 ro = vec3(0.0, 0.0, iTime);
    vec3 lp = ro  + vec3(0.0, 1.0, 0.0); // Light, near the ray origin.

    // Initiate the scene color to black.
    vec3 col = vec3(0);

    float t = trace(ro, rd); // Raymarch.

    // Scene hit, so do some lighting.
    if(t<FAR){

        vec3 sp = ro + rd*t; // Surface position.
        vec3 sn = normal(sp); // Surface normal.
        vec3 ref = reflect(rd, sn); // Reflected ray.

		const float ts = 2.; // Texture scale.
        vec3 oCol = tex3D(iChannel0, sp*ts, sn); // Texture color at the surface point.


        // Darker toned wood paneling. Very fancy. :)
        vec3 q = abs(mod(sp, 3.) - 1.5);
        if (max(max(q.x, q.y), q.z) < 1.063) oCol = oCol*vec3(.7, .85, 1.); 

        // Bringing out the texture colors a bit.
        oCol = smoothstep(0.0, 1.0, oCol);

        float sh = softShadow(sp, lp, 16.); // Soft shadows.
        float ao = calculateAO(sp, sn); // Self shadows.

        vec3 ld = lp - sp; // Light direction.
        float lDist = max(length(ld), 0.001); // Light to surface distance.
        ld /= lDist; // Normalizing the light direction vector.

        float diff = max(dot(ld, sn), 0.); // Diffuse component.
        float spec = pow(max(dot(reflect(-ld, sn), -rd), 0.), 12.); // Specular.
        //float fres = clamp(1.0 + dot(rd, sn), 0.0, 1.0); // Fresnel reflection term.

        float atten = 1.0 / (1.0 + lDist*0.25 + lDist*lDist*.1); // Attenuation.


        // Secondary camera light, just to light up the dark areas a bit more. It's here just
        // to add a bit of ambience, and its effects are subtle, so its attenuation 
        // will be rolled into the attenuation above.
        diff += max(dot(-rd, sn), 0.)*.45;
        spec += pow(max(dot(reflect(rd, sn), -rd), 0.), 12.)*.45;

        // Based on Eiffie's suggestion. It's an improvement, but I've commented out, 
        // for the time being.
        //spec *= curve(sp); 


		// REFLECTION BLOCK.
        //
        // Cheap reflection: Not entirely accurate, but the reflections are pretty subtle, so not much 
        // effort is being put in.
        float rt = refTrace(sp + ref*0.1, ref); // Raymarch from "sp" in the reflected direction.
        vec3 rsp = sp + ref*rt; // Reflected surface hit point.
        vec3 rsn = normal(rsp); // Normal at the reflected surface.

        vec3 rCol = tex3D(iChannel0, rsp*ts, rsn); // Texel at "rsp."
        q = abs(mod(rsp, 3.) - 1.5);
        if (max(max(q.x, q.y), q.z)<1.063) rCol = rCol*vec3(.7, .85, 1.);  
        // Toning down the power of the reflected color, simply because I liked the way it looked more. 
        rCol = sqrt(rCol); 
        float rDiff = max(dot(rsn, normalize(lp-rsp)), 0.); // Diffuse at "rsp" from the main light.
        rDiff += max(dot(rsn, normalize(-rd-rsp)), 0.)*.45; // Diffuse at "rsp" from the camera light.

        float rlDist = length(lp - rsp);
        float rAtten = 1./(1.0 + rlDist*0.25 + rlDist*rlDist*.1);
        rCol = min(rCol, 1.)*(rDiff + vec3(.5, .6, .7))*rAtten; // Reflected color. Not accurate, but close enough.
        //
    	// END REFLECTION BLOCK.


        // Combining the elements above to light and color the scene.
        col = oCol*(diff + vec3(.5, .6, .7)) + vec3(.5, .7, 1)*spec*2. + rCol*0.25;


        // Shading the scene color, clamping, and we're done.
        col = min(col*atten*sh*ao, 1.);






    }

    // Working in a bit of a blue fadeout in the distance. Totally fake. I chose blue to counter all
    // that walnut. Seemed like a good idea at the time. :)
    col = mix(col, vec3(.55, .75, 1.), smoothstep(0., FAR - 15., t));////1.-exp(-0.01*t*t)


	fragColor = vec4(col, 1.0);

}

#define PI	3.14159265359
#define PI2 PI*2.0
#define PIH PI*0.5
#define EPS 0.01
#define BB_OFFSET 0.1

#define HEAD -2.0
#define TAIL 2.0

#define ZOOM 0

mat2 rotate(in float a)
{
    float s=sin(a),c=cos(a);
    return mat2(c,s,-s,c);
}

mat3 lookat(in vec3 eye, in vec3 target)
{
	vec3 w = normalize(target-eye);
	vec3 u = normalize(cross(w,vec3(0.0,1.0,0.0)));
	vec3 v = normalize(cross(u,w));
    return mat3(u,v,w);
}

vec4 quaternion(in vec3 p, in float a)
{
	return vec4(p*sin(a/2.0),cos(a/2.0));
}

vec4 invq(in vec4 q)
{
    return vec4(-q.xyz,q.w);
}

vec3 qtransform(in vec4 q, in vec3 p)
{
	return p+2.0*cross(cross(p,q.xyz)-q.w*p,q.xyz);
}

float smin(in float a, in float b, in float k)
{
    float h = clamp(0.5+0.5*(b-a)/k,0.0,1.0);
    return mix(b,a,h) - k*h*(1.0-h);
}    

vec2 fold1(in vec2 p, in float a)
{
    p.x = abs(p.x);
    vec2 v = vec2(cos(a),sin(a));
    p -= 2.0*min(0.0,dot(p,v))*v;
    return p;    
}

vec2 fold3(in vec2 p, in float a)
{
    p.x = abs(p.x);
    vec2 v = vec2(cos(a),sin(a));
    for(int i=0;i<3;i++)
    {	
    	p -= 2.0*min(0.0,dot(p,v))*v;
    	v = normalize(vec2(v.x-1.0,v.y));
    }
 	return p;    
}

vec2 bend(in vec2 p, in float d)
{
    p.y -= d*p.x*p.x;
    p.y *= cos(atan(2.0*d*p.x));
 	return p;   
}

float gaussian(in float t)
{
    return exp(-t*t);
}

float lengthN(in vec2 p, in float n)
{
	p = pow(abs(p),vec2(n));
	return pow(p.x+p.y,1.0/n);
}

vec2 segment(in vec2 p, in float a, in float b)
{
    float x=clamp(p.x,min(a,b),max(a,b));
    return vec2(length(p-vec2(x,0.0)),(x-a)/(b-a));
}

float pattern(in vec2 p) {
    p *= vec2(8.0,25.0);
 	p.x = mod(p.x,2.0)-1.0;
    p.y = mod(p.y,4.0)-2.0;    
    float de = abs(abs(p.x)+clamp(abs(p.y)-1.0,-0.5,0.5)-0.5);
    return clamp(de+0.7,0.0,1.0);
}

vec2 bodyCurve(in float x)
{
    float t = iTime * 1.5;
    return vec2(
        0.05*cos(x*5.0+t)+0.1*cos(x*3.0+t),
        0.1*sin(x*5.0+t)+0.15*sin(x*3.0+t));
}

float barbCurve(in float x)
{
    float t = iTime*1.5;
 	return mix(0.0,0.02*sin(-40.0*x+t),clamp(x*5.0,0.0,1.0));   
}

float HairCurve(in float x)
{
    return mix(0.0,0.02*sin(35.0*x),clamp(x*6.0,0.0,1.0))+x*0.6;   
}

float mapTop(in vec3 p) // head parts
{
    float bump = 0.005*pattern(vec2(atan(p.y,p.z),p.x)*vec2(1.0,1.5));
    p -= vec3(-0.03,0.07,0.0);
    p *= vec3(0.8,1.5,1.0);
    float de = length(vec2(lengthN(p.xy,3.0),p.z))-0.07;
    p -= vec3(-0.047,-0.01,0.0);
    p.z = abs(p.z); p.z -= 0.035;
    de = smin(de, length(p)-0.035,0.01);    
    p.y -= 0.004;
    p.yz *= rotate(-0.1);
    p.y *= 1.8;
    de -= -0.06*gaussian(length(p.yz)*90.0)*step(0.0,-p.x);      
    de -= bump;
    return de;
}

float mapUpperJaw(in vec3 p) // head parts
{
    float bump = 0.005*pattern(vec2(atan(p.y,p.z),p.x)*vec2(1.0,1.5));    
    vec3 q = p;
    p.y -= 0.01;
    p.xy = bend(p.xy, 0.2);
    vec2 sg = segment(p.xy,-0.3,0.0);
    float de = lengthN(vec2(sg.x, p.z*0.6),2.5)-(0.02*sg.y*sg.y+0.03);
    p.x -= -0.3;
    p.z = abs(p.z); p.z -= 0.032;
    de -= 0.006*gaussian(length(p.xz)*70.0)*step(0.0,p.y);
    p.y -= 0.0045;
    p.yz *= rotate(-0.3);
    p.y *= 1.6;
    de -= -0.02*gaussian(length(p.yz)*120.0)*step(0.0,-p.x);   
    q.x -= -0.28;
    q.x *= 0.3;
    de -= 0.01*gaussian(length(q.xz)*70.0)*step(0.0,q.y);   
    de -= bump;
    return de;
}

float mapUpperFang(in vec3 p) // head parts
{
    p -= vec3(-0.3,-0.04,0.0);
    p.z = abs(p.z); p.z -= 0.04;
    vec2 sg = segment(p.yz,0.0,0.05);
    return lengthN(vec2(sg.x,p.x),1.5)-0.01*sg.y;
}

float mapBottomJaw(in vec3 p) // head parts
{
    float bump = 0.005*pattern(vec2(atan(p.y,p.z),p.x)*vec2(0.7,1.7))*step(-0.02,-p.y);
	p.xy = bend(p.xy,-0.2);
    vec2 sg = segment(p.xy,-0.25,0.0);
    float de = lengthN(vec2(sg.x, p.z*0.7),2.5)-(0.02*sg.y*sg.y+0.03);    
    de -= -0.008*gaussian(length(p.yz)*50.0)*step(0.0,-p.x);        
    p.x -= -0.1;
    p.x *= 0.15;   
    de -= -0.05*gaussian(length(p.xz)*60.0)*step(0.0,p.y);            
    de -= bump;
    return de;
}

float mapBottomFang(in vec3 p) // head parts
{
    p -= vec3(-0.24,0.017,0.0);
    p.z = abs(p.z); p.z -= 0.03;
    p.x = abs(p.x); p.x -= 0.01;
    p.x = abs(p.x); p.x -= 0.01;
    p.yz *= rotate(0.2);
    vec2 sg = segment(p.yz,0.03,0.0);
    return lengthN(vec2(sg.x,p.x),1.5)-0.01*sg.y;
}

float mapEyes(in vec3 p) // head parts
{
    p -= vec3(-0.115,0.065,0.0);
    p.z = abs(p.z); p.z -= 0.035;
    return length(p)-0.015;
}

float mapEars(in vec3 p) // head parts
{
    p -= vec3(-0.025,0.06,0.0);
    p.z = abs(p.z); p.z -= 0.08;
    p.z *=1.5;
    p.yz *= rotate(0.5);
    p.xy *= rotate(-0.3);
    vec2 sg = segment(p.yx,0.08,0.0);
    float de = lengthN(vec2(sg.x,p.z),5.0)-(0.02*sg.y+0.01);
    p.y -= 0.02;
    p.y *=0.5;
    de -= -0.02*gaussian(length(p.yx)*70.0)*step(0.0,p.z);
    return de;
}

float mapHorns(in vec3 p) // head parts
{
    p -= vec3(0.0,0.05,0.0);
    p.z = abs(p.z); p.z -= 0.04;
    p.xz *= rotate(0.3);
    p.xy *= rotate(-0.5);
    p.yz = bend(p.yz, 0.3);
    vec2 sg = segment(p.yz,0.25,0.0);
    return lengthN(vec2(sg.x,p.x),1.5)-0.01;
}

float mapBarb(in vec3 p) // head parts
{
    p -= vec3(-0.27,0.04,0.0);
    p.z = abs(p.z); p.z -= 0.05;
    p.zy *= rotate(0.5);
    p.xz *= rotate(-0.8);
    p.y -= barbCurve(p.z);
    float g = (barbCurve(p.z+EPS)-barbCurve(p.z-EPS))/(2.0*EPS);
    p.x *= cos(atan(g));
    vec2 sg = segment(p.zx,0.25,0.0);
    return 0.7*(length(vec2(sg.x, p.y))-(0.005*sg.y+0.003));
}

float mapHair(in vec3 p) // head parts
{
    p -= vec3(-0.02,0.055,0.0);
    p.zy = fold3(p.zy,radians(140.0));
    p.y -= 0.06;    
    p.y -= HairCurve(p.x);
    float g = (HairCurve(p.z+EPS)-HairCurve(p.z-EPS))/(2.0*EPS);
    p.x *= cos(atan(g));
    vec2 sg = segment(p.xz,0.12,0.0);
    return 0.7*(length(vec2(sg.x, p.y))-(0.01*sg.y+0.004));
}

float mapHead(in vec3 p)
{
    p.x -= HEAD;
    p.zy -= bodyCurve(HEAD);

    // bounding box
    vec3 q = p;
    q -= vec3(-0.1,0.07,0.0);
    vec3 bb = vec3(0.24,0.21,0.2)+BB_OFFSET;
    if (any(greaterThan(abs(q),bb))) return length(max(abs(q)-bb,0.0))+BB_OFFSET;

    // BottomJaw postion
    q = p;
    q.y -= -0.01;    
    q.xy *= rotate(0.1*sin(iTime)+0.3);

    float de = 1.0;
    de =  min(de, mapTop(p));
	de = smin(de, mapUpperJaw(p),0.02);
    de = smin(de, mapEars(p),0.03);
	de =  min(de, mapHair(p));
	de =  min(de, mapEyes(p));
	de =  min(de, mapHorns(p));
    de =  min(de, mapUpperFang(p));
    de =  min(de, mapBarb(p));
    de =  min(de, mapBottomJaw(q));
    de =  min(de, mapBottomFang(q));
    return de;
}

float mapBody(in vec3 p)
{
    p.zy -= bodyCurve(p.x);
	vec2 g = (bodyCurve(p.x+EPS)-bodyCurve(p.x-EPS))/(2.0*EPS);
    p.zy *= cos(atan(g));

    // bounding box
    vec3 q = p;
    q -= vec3(0.0,0.03,0.0);
    vec3 bb = vec3(2.2,0.2,0.15)+BB_OFFSET;
    if (any(greaterThan(abs(q),bb))) return length(max(abs(q)-bb,0.0))+BB_OFFSET;

    // main
    vec3 pMain = p;
    vec2 sg = segment(p.xy,TAIL,HEAD);
    float t = -abs(sg.y-0.6)*1.2+0.75;
    t = 0.1*smoothstep(0.0,0.25,t*t);

    float deMain = 0.8*(lengthN(vec2(sg.x,p.z*0.8),2.5)-(t+0.02));
    float a = atan(p.z,-p.y);    
    deMain -= -0.005*gaussian((abs(a)-0.6)*20.0)*step(0.0,-p.y);
    if (abs(a)<0.6)
    {
        deMain -= -0.005*gaussian(sin(p.x*PI2*6.0)*3.0)*step(0.0,-p.y);
        deMain -= 0.001*length(sin(vec2(p.x*10.0,a)*20.0));
    } else {
        deMain -= 0.005*pattern(vec2(a,p.x));
    }

    // fin
    p -= vec3(-0.4,t+0.03,0.0);
    float s = 0.05;
    for(int i=0;i<14;i++){
        p.x = abs(p.x);
        p.x -= s*2.0;
    }
    p.x += s;
    p.y *= 0.4;
    p.xy *= rotate(0.8);
    float deFin = max(abs(p.z)-0.01,lengthN(p.xy,5.0)-0.02);

    float de = 1.0;
    de =  min(de,deMain);
    de = smin(de,deFin,0.01);
    return de;
}

float  mapArm1(in vec3 p) // arms parts
{
    vec2 sg = segment(p.zx,0.3,0.0);
    float de = lengthN(vec2(sg.x, p.y*1.2),2.5)-(0.05*sg.y+0.05);
    de -= 0.005*pattern(vec2(atan(p.y,p.x),p.z)*vec2(0.7,1.8));
    return de;
}

float  mapArm2(in vec3 p) // arms parts
{
    vec2 sg = segment(p.zx,0.3, 0.0);
    float de = lengthN(vec2(sg.x, p.y*1.2),2.5)-(0.02*sg.y+0.04);
    de -= 0.005*pattern(vec2(atan(p.y,p.x),p.z)*vec2(0.7,1.8));
    return de;
}

float  mapHand(in vec3 p) // arms parts
{
    p -= vec3(-0.05,-0.02,0.0);
 	p.zx = fold1(p.zx,radians(110.0));
    p.xy *= rotate(-0.5);
    vec2 sg = segment(p.xz,0.08,0.0);
    float deHand = lengthN(vec2(sg.x,p.y),3.0)-(0.02*sg.y+0.02);
    p.x -= 0.08;
    p.xy *= rotate(-0.6);
    p.xy = bend(p.xy, -5.0);
    sg = segment(p.xz,0.1,0.0);
    float deClaw = lengthN(vec2(sg.x,p.y),2.5)-(0.02*sg.y);
    return smin(deHand, deClaw, 0.03);
}

float  mapClaw(in vec3 p) // arms parts for coloring
{
    p -= vec3(-0.05,-0.02,0.0);
 	p.zx = fold1(p.zx,radians(110.0));
    p.xy *= rotate(-0.5);
    p.x -= 0.08;
    p.xy *= rotate(-0.6);
    p.xy = bend(p.xy, -5.0);
    vec2 sg = segment(p.xz,0.1,0.0);
    return lengthN(vec2(sg.x,p.y),2.5)-(0.02*sg.y);
}

float  mapBall(in vec3 p, in float s) // arms parts
{
    p -= vec3(-0.05,-0.12,0.01);
    if (s<0.0) return length(p)-0.06;
    return 1.0;
}    

float mapArms(in vec3 p)
{
    const float offset = -1.2;
    p.x -= offset;
    p.zy -= bodyCurve(offset);

    // bounding box
    vec3 q = p;
    q -= vec3(-0.015,-0.12,0.0);
    vec3 bb = vec3(0.28,0.25,0.62)+BB_OFFSET;
    if (any(greaterThan(abs(q),bb))) return length(max(abs(q)-bb,0.0))+BB_OFFSET;

	float s = sign(p.z);   
    p.z = abs(p.z);

    vec4 quat;
	p -= vec3(0.0, 0.0, 0.15);
    quat = quaternion(normalize(vec3(0.5,1.0,0.0)),-0.7);
    vec3 p1 = qtransform(quat, p);

    p -= qtransform(invq(quat),vec3(0.0,0.0,0.3));
    quat = quaternion(normalize(vec3(-0.7,1.0,0.0)),1.0);
    vec3 p2 = qtransform(quat, p);

    p -= qtransform(invq(quat),vec3(0.0,0.0,0.3));
    quat = quaternion(normalize(vec3(0.0,0.0,1.0)),0.5);
    vec3 p3 = qtransform(quat, p);

    float de = 1.0;
    de =  min(de,mapArm1(p1));
    de = smin(de,mapArm2(p2),0.04);
    de = smin(de,mapHand(p3),0.03);
    de =  min(de,mapBall(p3,s));

    return de;
}

float mapDragon(in vec3 p)
{
    p.zx *= rotate(PIH);
    p.yx *= rotate(0.2);
    float de = 1.0;
    de =  min(de,mapBody(p));
    de = smin(de,mapHead(p),0.03);
    de = smin(de,mapArms(p),0.06);
    return de;
}

float mapGround(in vec3 p)
{
    float de = p.y-0.2;
    de -= 0.02*sin(5.0*p.x+iTime*0.5);
    de -= 0.02*sin(7.0*p.z+iTime*1.5);
    de -= 0.02*sin(8.0*length(p.zx)+iTime);
    return de;
}

vec3 doMotion(in vec3 p)
{
#if ZOOM
    p.z -= -2.0;
    p.y -= 1.0;
    return p;
#endif

    float t = mod(iTime,60.0);
    float h;
    if (t < 40.0)
    {
    	// 0-10s
    	p -= vec3(0.0,-0.5,-0.5);
    	h = clamp(t-0.0,0.0,10.0)/10.0;
    	p -= mix(vec3(0.0),vec3(0.0,0.9,0.0),h);    
    	//10-20s
    	h = clamp(t-10.0,0.0,10.0)/10.0;
    	p -= mix(vec3(0.0),vec3(0.8,-0.3,0.0),h*h);
    	p.xz *= rotate(PIH * h*h);
    	p.zy *= rotate(0.3* h);
    	//20-30s
    	h = clamp(t-20.0,0.0,10.0)/10.0;
    	p -= mix(vec3(0.0),vec3(-0.2,1.0,0.0),h*h);
    	p.zy *= rotate(-0.5* h);
    	//30-40s
    	h = clamp(t-30.0,0.0,10.0)/10.0;
    	p -= mix(vec3(0.0),vec3(-0.5,-2.3,0.0),h*h);
		p.zy *= rotate(-0.6* h*h);
    }
    else if (t < 50.0)    
    {
    	//40-50s
        p -= vec3(0.0,-2.1,0.7);
        p.zy *= rotate(PIH);        
    	h = clamp(t-40.0,0.0,10.0)/10.0;
    	p -= mix(vec3(0.0),vec3(0.5,0.0,6.0),h*h);
    	p.yx *= rotate(-PI2*1.5*h);
    }
    else
    {
    	//50-60s
    	p -= vec3(0.0,0.15,-4.5);
    	h = clamp(t-50.0,0.0,10.0)/10.0;
    	p -= mix(vec3(0.0),vec3(0.0,0.0,7.5),h);
    }
    return p;
}

float map(in vec3 p)
{   
    return min(mapGround(p),mapDragon(doMotion(p)));
}

vec3 doColor(in vec3 p)
{
    float e = 0.001;
    if (mapGround(p)<e) return vec3(0.2,0.2,0.35);
    p = doMotion(p);
    p.zx *= rotate(PIH);
    p.yx *= rotate(0.2);
    vec3 q = p;

    // body
    p.zy -= bodyCurve(p.x);
	vec2 g = (bodyCurve(p.x+EPS)-bodyCurve(p.x-EPS))/(2.0*EPS);
    p.zy *= cos(atan(g));
    float a = atan(p.z,-p.y);
    if (abs(a)<0.65 && p.x>-1.95) return vec3(0.75,0.65,0.5);
    vec2 sg = segment(p.xy,TAIL,HEAD);
    float t = -abs(sg.y-0.6)*1.2+0.75;
    t = 0.1*smoothstep(0.0,0.25,t*t);
    if (p.y>t+0.03 && p.x>-1.8) return vec3(0.7,0.1,0.1);

    // arms
    p = q;
    const float offset = -1.2;
    p.x -= offset;
    p.zy -= bodyCurve(offset);
	float s = sign(p.z);   
    p.z = abs(p.z);
    vec4 quat;
	p -= vec3(0.0, 0.0, 0.15);
    quat = quaternion(normalize(vec3(0.5,1.0,0.0)),-0.7);
    p -= qtransform(invq(quat),vec3(0.0,0.0,0.3));
    quat = quaternion(normalize(vec3(-0.7,1.0,0.0)),1.0);
    p -= qtransform(invq(quat),vec3(0.0,0.0,0.3));
    quat = quaternion(normalize(vec3(0.0,0.0,1.0)),0.5);
    p = qtransform(quat, p);
    if (mapClaw(p)<e) return vec3(1.0);
    if (mapBall(p,s)<e) return vec3(0.9,0.8,0.3);

    // head
    p = q;
    p.x -= HEAD;
    p.zy -= bodyCurve(HEAD);
    if (mapHair(p)<e) return vec3(0.1,0.05,0.05);
	if (mapHorns(p)<e) return vec3(0.9,0.5,0.1);
    if (mapUpperFang(p)<e) return vec3(0.8);
    if (mapBarb(p)<e) return vec3(0.1);
    if (mapEyes(p)<e)
    {
        p -= vec3(-0.115,0.065,0.0);
    	p.z = abs(p.z); p.z -= 0.035;
        vec3 col = mix(vec3(0.05,0.2,0.35),vec3(1.0),step(0.4,length(50.0*p.yz)));
        vec2 c = vec2(0.0,0.007);
        col = mix(vec3(1.0,1.0,0.0),col,            
            step(0.4,max(length(50.0*(p.yz-c)),length(50.0*(p.yz+c)))));
        return col;
    }
    p.y -= -0.01;    
    p.xy *= rotate(0.1*sin(iTime)+0.3);
    if (mapBottomFang(p)<e) return vec3(0.8);

    return vec3(0.15,0.3,0.25);
}

vec3 calcNormal(in vec3 pos)
{
    vec2 e = vec2(1.0,-1.0)*0.001;
    return normalize(
        e.xyy*map(pos+e.xyy)+e.yyx*map(pos+e.yyx)+ 
        e.yxy*map(pos+e.yxy)+e.xxx*map(pos+e.xxx));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p2 = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.8,0.8,0.9)*(1.0-0.7*p2.y*p2.y);
    vec3 rd = normalize(vec3(p2,2.0));    
    vec3 ro = vec3(0.0,0.8,2.5);  
    ro.xz *= rotate(PI*0.77777*floor(iTime/60.0));
    vec3 ta = vec3(0.0,0.2,0.0);
    ro += 0.03*sin(2.0*iTime*vec3(1.1,1.2,1.3));
	ta += 0.03*sin(2.0*iTime*vec3(1.7,1.5,1.6));

#if ZOOM
	#if 1
    	// face
    	ro = vec3(0.0,1.8,0.7);
    	ro.xz *= rotate(1.2*sin(iTime*0.3));
    	ta = vec3(bodyCurve(HEAD),1.5).xzy;
	#else
    	// hand
    	ro = vec3(-1.2,1.3,-0.5);
    	ta = vec3(-0.5,1.0,-1.0);
	#endif
#endif

    rd = lookat(ro,ta)*rd;
    float maxd = 10.0;
    float t = 0.0,d;
    for(int i=0;i<100;i++)
    {
        t += d = map(ro+rd*t);
        if(d<0.001||t>maxd) break;
    }
    if(t<maxd)
    {
        vec3 p = ro+t*rd;
        vec3 nor = calcNormal(p);
		col = doColor(p);
        vec3 li = normalize(vec3(2.0,3.0,3.0));
        float dif = clamp(dot(nor,li),0.0,1.0);
        float amb = max(0.5+0.5*nor.y,0.0);
        float spc = pow(clamp(dot(reflect(rd, nor),li),0.0,1.0),10.0);
        col *= clamp(0.0,1.0,max(dif,0.3)*amb+spc);
        col = mix(vec3(0.6,0.6,0.7),col,gaussian(t*0.15)*min(p.y+0.25,1.0));
		col = pow(col,vec3(0.7));
    }
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float drawScale = iResolution.x / 50.0;
    float speed = iTime * 5.0;
	float x = fragCoord.x;    
    float y = sin(x / drawScale + speed) * ((fragCoord.x / 5.0) + 100.0 * sin(fragCoord.y + x - speed));    
    fragColor = vec4(
        x / distance(vec2(fragCoord.x, fragCoord.y), vec2(x, y)) / iResolution.x * 20.0, //R
        0.02, //G
        0.3 * (y / iResolution.y), //B
        1.0);
}

