//NEW CODE

//Function of shape **************************************************************************
float map (vec3 p) 
{
    vec3 q = fract(p) * 2.0 - 1.0;   //instancing
	return length(q) - 0.25;    //gÃ¶mb
}

//Function of ray********************************************************************************
float trace (vec3 o, vec3 r)   //raytrace , hely, sugarirany
{
	float t = 0.0;
    for (int i = 0; i < 32; i++) //ray
    {
    	vec3 p = o + r*t;
        float d = map(p);    //trace az alakzatban
        t += (d *  0.5 * sin(iTime * 0.5))- 0.1; //mindig a felevel lep

    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) //******************************************
{

//Setup of pixels ***********************************************   

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0-1.0;
    uv.x*= iResolution.x / iResolution.y;

//Set up of rays (camera movement)******************************************************

    vec3 r = normalize(vec3(uv, 1.0));   //ray-t levetiti az image planere    a szaam az a field of view   
    vec3 o = vec3(0.0, 0.0, iTime);  //itt vagyunk a tÃ©rben
    mat2 rot = mat2(cos(iTime * 0.5), -sin(iTime * 0.5), sin(iTime * 0.5), cos(iTime * 0.5));
    r.xz *= rot;  
    r.xy *= rot;
    r.yz *= rot;  
    float t = trace(o, r);
	fragColor = vec4(vec3(1.0 / (1.0 + t * t  * 0.1)),1.0);
}

