float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

bool rtSphere(vec3 pos, vec3 n, float rad) {

    float tca = dot(pos, -n);
    if (tca<EP) {
        return false;
    }
    float d2 = dot(pos,pos)-tca*tca;
    float rad2 = rad*rad;
    if (d2>rad2) {
        return false;
    }
    float thc = sqrt(rad2-d2);
    float t0 = tca-thc;
    float t1 = tca+thc;
    if (t0>t1) {
        float t2 = t1;
        t1 = t0;
        t0 = t2;
    }
    if (t0<0.0) {
        t0 = t1;
        if (t0<0.0) {
            return false;
        }
    }
    return true;
}

float sphere(vec3 pos, float radius)
{
    return length(pos) - radius;
}

float box(vec3 pos, vec3 size)
{
    return length(max(abs(pos) - size, 0.0));
}

float displace(vec3 pos){
    pos.z+=iTime/24.0;
        return sin(pos.x*16.0+pos.y*8.0)*sin(pos.y*16.0+pos.z*8.0)*sin(pos.z*16.0+pos.x*8.0)/8.0;
}

float dist(vec3 pos) {

    float main = displace(pos)/5.0+
