vec3 scan(vec3 pos, vec3 dir) {

    if (!rtSphere(pos, dir, 0.5))
    {
        return amb(dir);
    }

    float totalDistance=0.0;
    for (int i=0; i<IT; i++) {
        float rayDist = dist(pos);
        totalDistance+=rayDist;
        if (abs(rayDist)<=EP) {

           #ifdef AMBONLY
            return vec3(AO(pos));
            #endif



            vec2 eps = vec2(0.0, EPN);
			vec3 normal = normalize(vec3(
    		dist(pos + eps.yxx) - dist(pos - eps.yxx),
    		dist(pos + eps.xyx) - dist(pos - eps.xyx),
    		dist(pos + eps.xxy) - dist(pos - eps.xxy)));



            vec3 sunColor = vec3(1.0,0.8,0.4);
            vec3 sunVector = normalize(vec3(1.0,1.0,1.0));
            float sunAtten = max(0.0, dot(sunVector,normal));

            vec3 ambientColor = vec3(0.6,0.8,0.6);
            float ambientAtten = AO(pos);

            vec3 reflDir = reflect(dir, normal);
            sunAtten+=pow(max(dot(reflDir, sunVector),0.0),16.0)*ambientAtten;

            float fresnel = pow(1.0- abs(dot(dir,normal)),2.0);

            //return vec3(ambientAtten);

            vec4 fog = vec4(0.6, 0.8, 0.6,1.0-1.0/pow(E,totalDistance*0.05));

            vec3 light = mix(sunColor*sunAtten + ambientColor*ambientAtten, amb(reflDir)*ambientAtten, fresnel);
            vec3 world = vec3(0.9,0.3,0.8)*light;
            return mix(world, fog.rgb,fog.a);//*texture(iChannel2, normal).rgb;
        }
        if (totalDistance>=MAX) {
            return amb( dir);
        }
        pos+=dir*(rayDist)*0.25;
    }


    return amb(dir);
}

