	// camera matrix
	vec3 ww = normalize( cLook-cPos );
	vec3 uu = normalize( cross(ww, cUp) );
	vec3 vv = normalize( cross(uu, ww) );

	vec3 rd = normalize( uv.x*uu + uv.y*vv + 2.0*ww );

	// render
	vec3 color = render(cPos, rd, uv);

	fragColor = vec4( color, 1.0 );
}

precision highp float;
#define RES   	iResolution
#define ASP 	RES.x/RES.y
#define M		(vec2(10.5,1./2.5)+vec2(20,7) * (iMouse.xy/RES.xy-.5))
#define ID3   	mat3(1,0,0,0,1,0,0,0,1)
#define Rxy		(mat3(1,0,0,0,cos(M.y),sin(M.y),0,-sin(M.y),cos(M.y))*mat3(-sin(.1 * t),0,cos(.1 * t),0,1,0,cos(.1 * t),0,sin(.1 * t)))
#define t     	iTime
#define NP		.1
#define FP		100.
#define NOO     0
#define PLN     1
#define SPH     2
#define L1P		vec3( 5.3 * sin( 3.2 * sin( .64 * t ) ),  1.1, 5.3 * cos( 3.2 * sin( .63 * t ) ) )
#define L2P		vec3( 0.,  -19.1 + 18. * cos( .2 * sin( .8 * t ) ), 18. * sin( .2 * sin( .8 * t ) ) )
#define N       vec3(0,1,0)
#define MIT		256 //max iterations for fractal, use smaller numbers if speed drops down! 
#define EYD		.4
struct Hit { float d; vec3 p, n; int i; } hit;
vec3   eye, ray;
vec4   col;
void 
    isPln( ) {

        vec3
            n = sign( eye.y ) * N;

        float
            l = dot( n, eye ) / dot( n, ray );

        if( hit.d < l || l < NP ) return;

        hit.d = l;
        hit.p = eye - l * ray;
		hit.n = n;
		hit.i = PLN;
}
void
    isSph( ) {

        float
            ab = dot( ray, eye ),
            bb = dot( eye, eye ),
            ds = ( 1. - bb ) + ab * ab;

        if( 0. <= ds ) {

            ds = sqrt( +ds );

            float
                l = ab - ds;

            if( l <= NP ) 

				l = ab + ds;

            if( hit.d < l || l <= NP ) return;


            hit.d = l;
            hit.p = eye - l * ray;
            hit.n = normalize( hit.p );
            hit.i = SPH;
        }
    }
float
    mb( vec2 iC ) {

        vec2
            c = iC;

        for( int i = 0; i < MIT; ++i ) {

            if( 8. < dot( c, c ) ) return 1. - mod( float( i ), 16. ) / 16.;

            c = vec2( c.x * c.x - c.y * c.y, 2. * c.x * c.y ) - iC;
        }        

        return 0.;
    }
vec3
    getPix( vec2 iXy ) {

        vec2
            d = iXy - 1.5 * vec2( cos( .9 * t ), sin( t ) );

        float
            f = dot( d, d );

        vec3
            c = vec3( 0. );

        if( f < .1 ) 

            c += sqrt( 1. - 10. * f ) *  ( .5 + 2. * vec3( iXy.y, iXy.x, .00 ) );

        d = iXy - 1.5 * vec2( cos( .69 * t ), sin( t ) );

        if( all( lessThan( abs( d - .5 ), vec2( .5 ) ) ) ) 

            c += ( .5 + 2. * vec3( iXy.y, iXy.x, .00 ) );

        return c + texture( iChannel0, mod( iXy + .5, vec2( 1. ) ) ).rgb + smoothstep( 0., mb( iXy * ( 1. / ( 10.01 + 10. * sin( .2 * t ) ) ) - vec2( -2, 0. ) ), .2 + .1 * sin( .76 * t ) ) * vec3( .3, .3, .2 );;
    }

void

    light( ) {

        vec3
            d = L1P - hit.p;

        float
            f = dot( d, d ),
        	b = pow( clamp( dot( d, hit.n ) / f, 0., 1. ), 1. );

        d = L2P - hit.p;
        f = dot( d, d );
            b += pow( clamp( dot( d, hit.n ) / f, 0., 1. ), 11. );

        b = .252422 + .52441 * b;

        col = 
            hit.i == NOO
            	? vec4( b * vec3( .71231, .8521, .913331 ), 1. )
            	: hit.i == SPH 
                    ? vec4( b * getPix( hit.p.xz  / ( 1. - hit.p.y ) ), 1. )
                    : vec4( b * ( vec3( .3, .4, .5 ) + getPix( hit.p.xz ) ), sin( t ) );
    }
void
    mainImage( out vec4 oCol, in vec2 iXy ) {

		eye = vec3( iXy.x < .5 * RES.x ? -EYD : EYD, 0., 2. );
        ray = normalize( eye - vec3( ( 2. * iXy / RES.xy - 1. ) * vec2( ASP, 1. ), 0. ) );
        eye.z += .5 + M.x;
        eye = eye * Rxy;
        ray = ray * Rxy;

        hit = Hit( FP, eye - FP * ray, ray, NOO );

        isSph( );
        isPln( );

        light( );

        oCol = col;
}

