    float scale = sin(iTime)+2.;
	vec2 uv = ((fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y)*scale;
	float graph = graph_f(uv,iResolution.xy,scale);

    //Stolen anti-aliasing script.
    float ret = 0.;
    for (int i = 0; i < 16; i++) { 
    vec2 q = vec2(floor(mod(float(i), 16.0)) / 4.0, floor(float(i)) / 16.0);
    vec2 uv1 = uv+(q/iResolution.xy);
    ret += graph_f(uv1,iResolution.xy,scale);
    }
    graph = ret/16.;

    //Draw center lines.
    float center = float(floor(fragCoord.x) == floor(iResolution.x/2.) || floor(fragCoord.y) == floor(iResolution.y/2.));
    //Grid.
    float grid = float(fract(uv.x-(scale/iResolution.y)) > fract(uv.x) || fract(uv.y-(scale/iResolution.y)) > fract(uv.y));
    //Extra grid.
    for (float h = 1.; h < 4.; h++) {
		float i = pow(2.,h);
        grid += (1./i)*float(fract((uv.x*i)-((scale*i)/iResolution.y)) > fract((uv.x*i)) || fract((uv.y*i)-((scale*i)/iResolution.y)) > fract((uv.y*i)));
    }
	fragColor = vec4(vec3(graph,0.,0.)+(center*.5)+(grid*.3),1.0);
}

