const float PI = 3.141592653589793238462643383;

const vec2 origin = vec2(0.0, 0.0);
const float ViewRadius = 2.0;

const float GridCoefficientLight = 0.6;
const float GridCoefficientDark = 0.6;


vec3 hueForPhase(float phase) {
    vec3 hue = vec3(
        sin(phase + 0.5 * iTime) + 1.0,
        sin(2.0*(phase + 0.67*PI) + 0.3 * iTime) + 1.0,
        sin(2.0*phase + 0.7 * iTime) + 1.0);

    return hue / sqrt(dot(hue, hue));
}

// Should be ~1 at the origin, ~0 at any significant distance from it
float decay(float x) {
	if (abs(x) >= 0.1) {
		return 0.0;
	}
	return 1.0 - abs(x*10.0);//pow(abs(x*10.0), 1)
}

// Offset of x from the integer grid, ignoring integer multiples of n.
float offsetFromGridExcept(int n, float x) {
	float below = floor(x);
    float above = ceil(x);
    if (abs(mod(below, float(n))) < 0.5) {
        below -= 1.0;
    }
    if (abs(mod(above, float(n))) < 0.5) {
        above += 1.0;
    }
	return min(x-below, above-x);
}

float offsetFromGrid(float x) {
	return min(x-floor(x), ceil(x)-x);
}

float gridFn(float x) {
    return offsetFromGrid(x);
}

vec4 eval(vec2 z, vec2 param) {
    float a = param.x;
    float b = param.y;
    vec2 z2 = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y);
    return vec4(z2 + a*z + b*vec2(1.0,0.0),
                2.0*z + a*vec2(1.0, 0.0));
}

// Scaling weirdness: "hue" is expected to be an angle measured in radians,
// sv are [0, 1].
vec3 HSVtoRGB(vec3 hsv) {
    vec3 hue = hueForPhase(hsv.x);
    return (hsv.y * hue + (1.0 - hsv.y)) * hsv.z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//vec2 uv = 2.0 * (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    vec2 uv = 2.0 * ViewRadius * (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
	vec2 param = 2.0 * ViewRadius * (iMouse.xy - 0.5 * iResolution.xy) / iResolution.y;
    if (iMouse.xy == vec2(0.0, 0.0)) {
        param = vec2(0.0, 0.0);
    }


    vec4 value = eval(uv, param);	// xy = function, zw = derivative

    float vabs = sqrt(dot(value.xy, value.xy));
    float dvabs = sqrt(dot(value.zw, value.zw));
    float phase = atan(value.y, value.x);// / (2.0 * PI) + 0.5;
    float dphase = dvabs / (vabs+0.01);

    float phaseOffset = offsetFromGridExcept(4, 16.0*phase / (2.0 * PI));
    float phaseFactor = 1.0 - decay(phaseOffset * 5.0 / (ViewRadius * (dphase + 0.01)));

    float absFactor = 1.0;
    if (vabs > 0.5) {
        float absOffset = vabs * GridCoefficientLight;
        absFactor = 1.0 - decay(gridFn(absOffset) * 12.0 /
            (GridCoefficientLight * ViewRadius * (dvabs + 0.01)));
    }

    float imagOffset = abs(value.y * GridCoefficientDark) + 1.0;
    float imagFactor = 1.0 - decay(gridFn(imagOffset) * 15.0 /
                                   (GridCoefficientDark * ViewRadius * (dvabs + 0.01)));
    float realOffset = abs(value.x * GridCoefficientDark) + 1.0;

    float realFactor = 1.0 - decay(gridFn(realOffset) * 15.0 /
                                   (GridCoefficientDark * ViewRadius * (dvabs + 0.01)));

    vec3 hsv = vec3(
        phase,
        phaseFactor * absFactor,
        imagFactor * realFactor);

    fragColor = vec4(HSVtoRGB(hsv), 1.0);
}

