#define YELLOW vec3(1.,1.,0.)
#define BLUE vec3(0.,0.,1.)

struct Rect
{
    float width;
    float height;
    float x;
    float y;
    vec3  color;
    float rotation;

    vec2 csys; //it's own coordinate system
};

Rect CreateRect(float width, float height, float x, float y, vec3 color)
{
    Rect rect;

    rect.width = width;
    rect.height = height;
    rect.x = x;
    rect.y = y;
    rect.color = color;

    return rect;
}

void DrawRect(inout Rect rect,vec2 p, inout vec3 pix)
{

   	float dx = p.x - rect.x;
    float dy = p.y - rect.y;

    dx = cos(rect.rotation)*(p.x - rect.x) + sin(rect.rotation)*(p.y - rect.y);
    dy = -sin(rect.rotation)*(p.x - rect.x) + cos(rect.rotation)*(p.y - rect.y);

    float fL = - rect.width/2.;  //far left
    float fR = + rect.width/2.;  //far right
    float fT = + rect.height/2.; //far top
    float fB = - rect.height/2.; //far bottom

    //anti-aliasing
    float del;
    del =  smoothstep(fL - 0.005, fL + 0.005,dx);
    del *= 1. - smoothstep(fR - 0.005, fR + 0.005,dx);
    del *=  smoothstep(fB - 0.005, fB + 0.005,dy);
    del *= 1. - smoothstep(fT - 0.005, fT + 0.005,dy);

    float d = 0.005;

    if(dx > fL-d && dx < fR+d 
    && dy < fT+d && dy > fB-d)
    {
        pix = mix(pix,rect.color,del);
    }

    rect.csys = vec2(dx,dy);
}

float GetPathCoord(float p1, float p2, float percent)
{
   return p1 + ((p2 - p1) * percent);
}

const int NUM_PATHS = 6;
vec2 BezierPaths[NUM_PATHS];

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 r = 2.* vec2(fragCoord.xy - .5*iResolution.xy)/iResolution.y;
    vec3 pixel = vec3(0.);

	BezierPaths[0] = vec2(0.,1.);
    BezierPaths[1] = vec2(0.2,1.);
    BezierPaths[2] = vec2(0.,0.);
    BezierPaths[3] = vec2(0.4,0.0);
    BezierPaths[4] = vec2(0.45,0.0);

    BezierPaths[5] = vec2(1.,0.);

    //Draw the bezier path points

    for(int i = 0; i < NUM_PATHS; i++)
    {
        Rect tmp_rect = CreateRect(0.05,0.05,BezierPaths[i].x, BezierPaths[i].y,YELLOW);
        DrawRect(tmp_rect,r,pixel);
    }

    float percent = (mod(iTime,4.0))/ 4.0;

    //Bezier path calculation for multiple paths
    float tmp_x = 0.;
    float tmp_y = 0.;
    vec2 x[NUM_PATHS-1];

    for(int i = 0; i < NUM_PATHS-1; i++)
    {
        tmp_x = GetPathCoord(BezierPaths[i].x , BezierPaths[i+1].x, percent);
        tmp_y = GetPathCoord(BezierPaths[i].y , BezierPaths[i+1].y, percent);

        x[i] = vec2(tmp_x,tmp_y);
    }

    float tmp_nx = GetPathCoord(x[0].x, x[1].x,percent);
    float tmp_ny = GetPathCoord(x[0].y, x[1].y,percent);

    for(int i = 2; i < NUM_PATHS-1; i++)
    {
        tmp_nx = GetPathCoord(tmp_nx,x[i].x,percent);
    	tmp_ny = GetPathCoord(tmp_ny,x[i].y,percent);
    }

    //Draw Bezier Point which follows the path
    Rect rp = CreateRect(0.05,0.05,tmp_nx,tmp_ny,BLUE);
	DrawRect(rp,r,pixel);



    fragColor = vec4(pixel,1.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    float center_r = -0.743, center_i = 0.13182;
    //float zoom = 0.00009 * sin(iTime / 1.2);
    float zoom = 0.00009 * sin(iTime / 1.6);

    //float tr = 0.000009;
    float tr = 0.00008;

    float cr = center_r + (fragCoord.x - iResolution.x / 2.0)*3.5 / iResolution.x * zoom + sin(iTime * 2.0) * tr;
    float ci = center_i + (fragCoord.y - iResolution.y / 2.0)*2.0 / iResolution.y * zoom + cos(iTime * 4.0) * tr;

    int it = 0;
    float x = 0.0, y = 0.0;
    for (int i = 0; i <= 1000; i++) {
        if (x * x + y * y >= 4.0) {
            break;
        }
        float xt = x * x - y * y + cr;
        y = 2.0 * x * y + ci;
        x = xt;
        it = i;
    }

    vec3 color;
    if (it == 1000) {
        color = vec3(0.2, 0.5, 0.4);
    } else {
        float c = -0.2 + sin(float(it) - sin(iTime * 8.0));
        vec3 n_color = vec3(0.6, c, 0.4);
        vec3 i_color = vec3(c, 0.4, 0.6);
        float weight = 0.5 + sin(iTime * 0.8) * 0.4;
        color = mix(n_color, i_color, weight);

    }

    fragColor = vec4(color, 1.0);

}

// "Checkerboard Tunnel"
// by Ulf Biallas | https://twitter.com/ulfbiallas

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 mouse = iMouse.xy;
    float dx = mouse.x - fragCoord.x;
    float dy = mouse.y - fragCoord.y;

    float dist = pow(pow(dx, 2.0) + pow(dy, 2.0), 1.0 / 2.0);
    dist = 500.0 * pow(dist, 0.1);

	float blockSize = 35.0;

    dist = mod(dist, blockSize) - 0.5 * blockSize;
    vec4 color;
    bool distFlag = false;
    if (dist < 0.0) {
        distFlag = true;
    }

    float angle = atan(dy / dx) + 0.5 * 3.14;
    float segmentSize = 3.14 / 4.0;

    angle = mod(iTime+angle, segmentSize) - 0.5 * segmentSize;
    bool angleFlag = false;
    if (angle <= 0.0) {
        angleFlag = true;
    }

    color = vec4(1.0, 1.0, 1.0, 1.0);
    if( (angleFlag && distFlag)) {
        color = vec4(0.0, 0.0, 0.0, 1.0);
    }
    if( (!angleFlag && !distFlag)) {
        color = vec4(0.0, 0.0, 0.0, 1.0);
    }

    fragColor = color;
}

