SDObject castRay( in Ray ray )
{
    int cd=1;
    float precis =0.00001;//(state.y+1.<iTime)?0.0001:0.003;
    float t = ray.tmin;
    SDObject res=NoSDObject;
    SDObject  ores=NoSDObject;
    vec3 p;
    int citer=0;
    for( int i=0; i<200; i++ )
    {
        p=ray.ro+ray.rd*t;
        res = mapScene( p,ray.id,ray.solo );
        if( res.d<precis || t>ray.tmax) {              
            break;
        }
        t += res.d;
    }
    res.t=t;
    if( t>ray.tmax ) return NoSDObject;
    //   res.ray.ro=p;
    return res;
}

vec3 calcNormal( in vec3 pos )
{
    vec3 eps = vec3( 0.004, 0.0, 0.0 );
    vec3 nor = vec3(
        mapScene(pos+eps.xyy,-1,false).d - mapScene(pos-eps.xyy,-1,false).d,
        mapScene(pos+eps.yxy,-1,false).d - mapScene(pos-eps.yxy,-1,false).d,
        mapScene(pos+eps.yyx,-1,false).d - mapScene(pos-eps.yyx,-1,false).d );
    return normalize(nor);
}
float calcAO( in vec3 pos, in vec3 nor )
{
    #ifndef ENABLE_AO
    return 1.;
    #endif

    float occ = 0.20;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos = nor * hr + pos;
        float dd = mapScene( aopos,-1,false ).d;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ); 
}
vec2 softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    vec2 res = vec2(1.,1.);
    float t = mint;
    for( int i=0; i<32; i++ )
    {
        SDObject hit=mapScene( ro + rd*t ,-1,false);
        float h = hit.d;
        #ifdef ENABLE_TRANSPARENCY
        if(hit.mat.type>0 && hit.mat.trans>0.)
        {
            res.y=min(res.y,1.-hit.mat.trans);
        }
        #endif
        res.x = min( res.x, (32.0*h/t) );
        t += clamp( h, 0.00001, 0.99999 );
        if( h<(0.00001) || t>tmax ) break;
    }
    return clamp( res,1.2-res.y, 1.0 );

}

vec3 render(vec3 incol,vec3 col,float d)
{
    float aares=smoothstep(((d)), .9740, .0);
