vec3 hitColor(inout SDObject hit)
{
    Material m=hit.mat;
    vec3 col=hit.mat.col;

    if(m.type==1)
    {
        vec2 texm=hit.mpos.zx*.2;
    }

    if(m.type==2 )
    {
        if(SCREEN_POWER==1)
        {
            //video texture
            vec2 ssres=hit.def.siz.xz;
            vec2 texm=hit.def.utp.xz;
            texm/=ssres;
            texm/=2.;
            texm.y=-texm.y;
            texm+= .5;
            #define fringe 0.015
            col=clamp( _mainImage(texm).rgb ,0.,1.)
                *smoothstep(0.0,fringe,texm.x)
                *smoothstep(0.0,fringe,texm.y)
                *smoothstep(1.,1.-fringe,texm.x)
                *smoothstep(1.,1.-fringe,texm.y);
        }else{
            col=vec3(0.);
        }
    }
    return col;
}

SDObject render(in vec3 bcol,out vec3 ocol,in Ray ray)
{
    SDObject hit=castRay(ray);
    vec3 col=ocol;

    float tamnt=LIGHT_AMOUNT;
    if (hit.mat.type >0)
    {

        Material m=hit.mat;
        col=hitColor(hit);

        vec3 nor = calcNormal( hit.mpos );
        vec3 ref = reflect( ray.rd, nor );

        hit.nor=nor;

        float occ = calcAO( hit.mpos, nor );
        vec3 lig = normalize( vec3(-0.0, 1., 1.) );
        float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig )*tamnt, m.emis, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-hit.mpos.y,0.0,1.0);
        float dom = smoothstep( -0.1, 0.1, ref.y )*m.ref.amount;
        float fre = pow( clamp(1.0+dot(nor,ray.rd),0.0,1.0), 2.0 )*m.ref.amount;
        float spe = pow(clamp( dot( ref, lig ), 0.0, 1.0 ),16.0)*m.ref.amount;

        //	if (hit.mat.m >0)
        // col+=rfm;
        // shadows
        vec3 lin = vec3(0.0);
        #ifdef ENABLE_SHADOWS
        {
            vec2 difss=softshadow( hit.mpos, lig, 0.02, 2.5 );
            dif *= mix(1.,difss.x,1.-m.emis);
            vec2 domss=softshadow( hit.mpos, ref, 0.02, 2.5 );
            dom *= domss.x;
            // dom*=1.-domss.y;//1.-difss.y;
        } 
        #endif
        lin += 1.20*dif*mix(vec3(1.00,1.,0.75),vec3(1.),m.emis);
        lin += 1.20*spe*vec3(1.00,1.,0.75)*dif;
        lin += 0.20*amb*vec3(0.50,.70,1.00)*occ;
        // lin += 0.30*dom*vec3(0.50,0.70,1.00)*occ;
        lin += 0.30*bac*vec3(0.25,0.25,0.25)*occ;
        lin += 0.40*fre*vec3(1.00,1.00,1.00)*occ;
        // col*=1.+0.3*m.emis;
        col = col*lin;

        #ifdef ENABLE_FOG
        col = mix( col, vec3(0.8,0.9,1.0)*0.3*tamnt, (0.9)-exp( -0.002*hit.t*hit.t ) );
        #endif

        ocol=mix(col,ocol,ray.a);
    }else{
        ocol=	mix(bcol*tamnt,col,ray.a);

    }
    return hit;
}
Ray getCamera(vec2 p,vec2 um)
{
    #ifdef AUTO_CAMERA
    if(iMouse.z<0.5)
    {
        um=0.5+0.4*vec2(-1.25-0.5*sin(-2.8+iTime*0.6),cos(-1.18+iTime*0.3));
    }
    #endif
  vec2 cr=um;

    float srat=iResolution.y/iResolution.x;

    cr.x=cr.x+.45;
    float flip=0.5-0.5*cos(remap(cr.y,0.3,0.7,0.,1.,true)*PI);


    //camera
    #define CENTER_CAM vec3(0.,1.95,2.45)
    vec3 cam=vec3(0.,0.4*(1.-2.*flip),3.2);
    vec3 ro = cam*2.5;//vec3(0.,.95,.4);
    vec3 rd = normalize(vec3(p,-1.5));
    vec3 rdc= normalize(vec3(p ,-1.5));

    mat2 mx = mm2(flip*	PI); 
    mat2 mz = mm2(PI*2.*(-cr.x*srat)-PI*0.5);
    mat2 my = mm2(PI*cr.y); 

    rd.xy*=mx;

    ro.zy *= my;rd.zy *= my;
    ro.zx *= mz;rd.zx *= mz;

    rdc.zy*=my;
    rdc.zx*=mz;

    return newRay(ro,rd);

}
