//alternative noise implementation
float hash( float n ) {
    return fract(sin(n)*43758.5453123);
}

float noise( in vec2 x ) {
    vec2 p = floor(x);
    vec2 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0;

    return mix(mix( hash(n+  0.0), hash(n+  1.0),f.x), mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y);
}



mat2 m = mat2(0.8, 0.6, -0.6, 0.8);

float fbm(in vec2 p)
{
    float f = 0.0;
    f += 0.5000*noise(p); p*=m*2.02;
    f += 0.2500*noise(p); p*=m*2.03;
    f += 0.1250*noise(p); p*=m*2.01;
    f += 0.0625*noise(p); p*=m*2.04;
    f /= 0.9375;
    return f;
}





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0*uv;
    p.x *= iResolution.x/iResolution.y;

    //p.x -= 0.75;

    float r = sqrt(dot(p, p));
    float a = atan(p.y, p.x);

    //change this to whatever you want the background
    //color to be
    vec3 bg_col = vec3(1.0);

    vec3 col = bg_col;

    float ss = 0.5 + 0.5*sin(iTime);
	float anim = 1.0 + 0.1*ss*clamp(1.0-r, 0.0, 1.0);
	r *= anim;

    if (r < 0.8) {
        col = vec3(0.0, 0.3, 0.4);

        float f = fbm(5.0*p);
        col = mix(col, vec3(0.2, 0.5, 0.4), f);

		f = 1.0 - smoothstep(0.2, 0.5, r);
		col = mix(col, vec3(0.9, 0.6, 0.2), f);

		a += 0.05*fbm(20.0*p);

		f = smoothstep(0.3, 1.0, fbm(vec2(6.0*r, 20.0*a)));
		col = mix(col, vec3(1.0), f);

		f = smoothstep(0.4, 0.9, fbm(vec2(10.0*r, 15.0*a)));
		col *= 1.0 - 0.5*f;

		f = smoothstep(0.6, 0.8, r);
		col *= 1.0 - 0.5*f;

		f = smoothstep(0.2, 0.25, r);
		col *= f;


		f = 1.0 - smoothstep(0.0, 0.3, length(p - vec2(0.24, 0.2)));
		col += vec3(1.0, 0.9, 0.8)*f*0.8;


		f = smoothstep(0.75, 0.8, r);
		col = mix(col, bg_col, f);
    }




	fragColor = vec4(col, 1.0);
}

// basically draw contours of a height field.
// making a realistic texture is not easy!

const float knobStrength = 2.0;
const float pixelsPerPlank = 100.0;
const float linesPerPlank = 5.5;
const float verticalScale = 6.0;
const float lineStrength = 0.8;
const float plankLength = 6.0;

float rand(vec2 n) { 
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
    vec2 ip = floor(p);
    vec2 u = fract(p);
    u = u*u*(3.0-2.0*u);

    float res = mix(
        mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
        mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
    return res;
}

float noisetex(in vec2 x)
{
    vec2 p = floor(x);
    vec2 f = fract(x);
	f = f*f*(3.0-2.0*f);
	return texture( iChannel0, (p + f + 0.5)/256.0, -100.0 ).x;
}

float height(in vec2 a) {
    a = a*vec2(1.0, 1.0/verticalScale);
    return linesPerPlank * (knobStrength*noise(a) + a.x);
}

// make the lines constant width
// http://iquilezles.org/www/articles/distance/distance.htm
vec2 grad(in vec2 x) {
	vec2 h = vec2(0.05, 0.0);
	return vec2(height(x+h.xy) - height(x-h.xy),
                height(x+h.yx) - height(x-h.yx))/(2.0*h.x);
}

vec4 composit(in vec4 top, in vec4 bottom) {
    return vec4(mix(top.xyz, bottom.xyz, 1.0-top.a), 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    //animation
    fragCoord.y += iTime * 100.;

    vec2 pixel = fragCoord / pixelsPerPlank;
    float plank = floor(pixel.x); // unique per plank
    float start = 15.0 * rand(vec2(plank));
    float item = floor(pixel.y/plankLength + start);

    vec3 woodLine = vec3(90., 45., 18.)/255.;
    vec2 plankPixel = pixel + vec2(124., 11.) * plank;
    float value = height(plankPixel + item);
    vec2 gradient = grad(plankPixel + item);
    float linePos = 1.0 - smoothstep(0.0, 0.08, fract(value)/length(gradient));
    float line = floor(value); // unique per line
    float lineWeight = mix(1.0, 0.4+rand(vec2(line,plank)), 0.8);
    float lineGrain = smoothstep(-0.2, 0.9, noisetex(fragCoord));

    vec3 woodBase = vec3(144., 91., 49.)/255.;
    float darkness = mix(1.0, 0.5+rand(vec2(plank, item)), 0.2);
    float grain = mix(1.0, 0.5 + 0.7*noisetex(fragCoord + 12.5), 0.1);

    float plankGapY = step(0.0, fract(pixel.x)) * (1.0-step(0.02, fract(pixel.x)));
    float plankGapX = step(0.0, fract(pixel.y/plankLength+start)) * (1.0-step(0.02/plankLength, fract(pixel.y/plankLength+start)));

    // final images
    vec4 planks = vec4(0.1, 0.1, 0.1, max(plankGapY, plankGapX));
    vec4 lines = vec4(woodLine, lineStrength*lineWeight*lineGrain*linePos);
    vec4 wood = vec4(woodBase*darkness*grain, 1.0);

    fragColor = composit(planks, composit(lines, wood));
}

struct Rect {
	vec2 pos,
         ext;
    mat2 kord;
    vec4 col;

};


Rect D1 = Rect ( vec2(-.45+.5*cos(iTime),-.6+.5*sin(iTime)), vec2 (1.23,1.23), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.)),
     R1 = Rect ( vec2(-.2+.5*cos(iTime),.2+.5*sin(iTime)), vec2 (.6,.6), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.)),
     R2 = Rect ( vec2(-.2,.2), vec2 (.6,.6), mat2 (1.,0.,0.,1.), vec4 (9,.99,.80,2.));





mat2 Rota (in float alpha){
	float s = sin(alpha),
          c = cos(alpha);

   return mat2 (c, -s, s, c);

}

void Rotarect (in float alpha, inout Rect r_p){

    r_p.kord = Rota (alpha) * r_p.kord;


}





vec4 intersec (in vec2 p_uv, in Rect reco){
	vec2 xynew = p_uv - reco.pos;
    vec2 mat = reco.kord * xynew;

    if ( abs(mat.x) >.5*reco.ext.x && abs(mat.y) >.5*reco.ext.y){
        return vec4 (0.);
    	}
        if (mat.x*mat.x+mat.y*mat.y <.135 && mat.y > -0.2)
        return reco.col;

    return vec4(.0);

}

vec4 intersecD (in vec2 p_uv, in Rect reco){
	vec2 xynew = p_uv - reco.pos;
    vec2 mat = reco.kord * xynew;

    if ( abs(mat.x) >.5*reco.ext.x || abs(mat.y) >.5*reco.ext.y){
        return vec4 (0.);
    	}
        if (abs(1.65*mat.x)*reco.ext.x + mat.y*reco.ext.y >.4)
        return vec4(.0);

    return reco.col;

}



