const float pi = acos(0.0);

float donut(vec3 pos) {

    float rot = atan(pos.x,pos.y)*floor(iTime);

    vec2 pos2 = vec2(length(pos.xy)-sin(rot)*0.1-1.0,pos.z-cos(rot)*0.1);

    float len = length(pos2);

    rot = atan(pos.x,pos.y)*ceil(iTime);

    pos2 = vec2(length(pos.xy)-sin(rot)*0.1-1.0,pos.z-cos(rot)*0.1);

    return mix(len,length(pos2),fract(iTime));
}

float map(vec3 pos) {
    vec3 pos2 = mod(pos+2.5,5.0)-2.5;
    vec3 pos3 = pos-pos2;
    return donut(pos2)-0.1;
}

vec3 calcNormal(vec3 pos, float len) {
    vec2 eps = vec2(0.001,0.0);
    vec3 nor = vec3(
        len-map(pos-eps.xyy),
        len-map(pos-eps.yxy),
        len-map(pos-eps.yyx)
    );
    return normalize(nor);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    fragColor = vec4(0.0,0.0,0.0,1.0);

	vec2 uv = (fragCoord.xy*2.0-iResolution.xy)/ iResolution.y;

    vec3 rayorig = vec3(0.01,0.01,mod(iTime,5.0));
    vec3 raypos = rayorig;
    vec3 raydir = normalize(vec3(uv, 2.0));
    raypos += raydir*0.5;

    float dist = 0.0;
    float len = 0.0;

    for (float i = 0.0; i < 1000.0; i++) {
        len = map(raypos);
        if ( len < 0.01 || dist > 20.0 ) {
            break;
        }
        dist += len;
        raypos += raydir*len;

    }

    if (len < 0.01) {
        fragColor = vec4(abs(calcNormal(raypos, len))*(1.0-dist*0.05),1.0);
    }


}

float heart(vec2 i) {
    // From: https://www.shadertoy.com/view/ldVGzt by coyote & Fabrice
    i.y += .034;
    i *= 1.1;
    return sqrt(dot(i, i) - abs(i.x)*i.y);
}

float diamond(vec2 i) {
    i = abs(i);
    return (i.x+i.y);
}

float square(vec2 i) {
    i = abs(i);
    return max(i.x,i.y);
}

float circle(vec2 i) {
    return length(i);
}

float circle2(vec2 i) {

    return length(vec2(abs(length(i)-.5),max(0.,abs(atan(i.x,i.y))/2.-sin(iTime)-.7)))+.35;
   //return length(vec2(abs(length(i)-.5),1.3-2.*atan(i.x,i.y)))+.35;
}

float honeycomb(vec2 i) {
    i.x*=.866;
    i = abs(i);
    return max(i.x+i.y*.5,i.y);
}

float segment(vec2 uv)
{
    uv = abs(uv);
	float f = max(0.45+uv.x,0.225+uv.y+uv.x);
