#define PI 3.14159265359

float easeInOutExpo(float t, float b, float c, float d) {
	t /= d/2.0;
	if (t < 1.0) return c/2.0 * pow( 2.0, 10.0 * (t - 1.0) ) + b;
	t--;
	return c/2.0 * ( -pow( 2.0, -10.0 * t) + 2.0 ) + b;
}

/**
 * Draw a rectangle at vec2 `pos` with width `width`, height `height` and color `color`.
 * @author jonobr1 / http://jonobr1.com/
 */
vec4 rectangle(vec2 uv, vec2 pos, float width, float height, vec3 color) {
	float t = 0.0;
	if ((uv.x > pos.x) && (uv.x < pos.x + width)
		&& (uv.y > pos.y) && (uv.y < pos.y + height)) {
		t = 1.0;
	}
	return vec4(color, t);
}

vec3 getColor(float frame, float frequency) {
    vec3 color1 = vec3(0.86, 0.86, 0.82);
    vec3 color2 = vec3(0.15, 0.15, 0.15);

    if(cos(frame*2.0+frequency+PI/2.0)>0.0) return color1;
    return color2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 center = vec2(0.5, 0.1);
    const int total = 8;//number of lines

    float frame = iTime;
    if(iMouse.x>0.0) frame *= 5.0 * (iMouse.x / iResolution.x);//velocity


    vec4 color = vec4(getColor(frame, PI), 1.0);//bg
    vec3 rectColor = getColor(frame, 0.0);

    float freqDecay = 2.0;

    float space = 0.05;
    float size = 0.7;
//*Vertical Lines
    for(int i=0; i<total; i++){
        float freq = (1.0-float(i)/float(total))/freqDecay;
        float x = center.x + space*float(i-total/2);
    	float y = center.y;
        float height = size * easeInOutExpo(((cos((frame+freq)*2.0)+1.0)/2.0), 0.0, 1.0, 1.0);
    	if(cos(frame+freq+PI/2.0)<0.0) y += size-height;;
        height += 0.035;
        float width = 0.02;

        vec4 rect = rectangle(uv, vec2(x, y), width, height, rectColor);
	    color = mix(color, rect, rect.a);
    }
/**/
//*Horizontal Lines
    frame += PI/2.0;
    space = 0.08;
	center = vec2(0.25, 0.5);
    size = 0.45;
    for(int i=0; i<total; i++){
        float freq = (1.0-float(i)/float(total))/freqDecay;
        float x = center.x;
        float width = size * easeInOutExpo(((cos((frame+freq)*2.0)+1.0)/2.0), 0.0, 1.0, 1.0);
	    if(cos(frame+freq+PI/2.0)<0.0) x += size - width;
    	float y = center.y + space*float(i-total/2);
        float height = 0.035;
    	width += 0.021;

        vec4 rect = rectangle(uv, vec2(x, y), width, height, rectColor);
	    color = mix(color, rect, rect.a);
    }
/**/
	fragColor = color;
}

#define FOV 90

struct camera
{
    vec3 pos;
    vec3 ray;
    vec3 light;
};

struct material
{
    vec3 color;
    float gloss;
   	float metal;
};

struct sphere
{
	vec3 pos;
    float rad;
    material mat;
};

struct plane
{
    float height;
    vec3 normal;
};

struct RayResult
{
    float t;
    material mat;
    vec3 normal;
};

struct cube
{
	float rad;
    vec3 pos;
    material mat;
};

const int SPHERENUM = 3;
const int CUBENUM = 3;

sphere sp[SPHERENUM];
cube cu[CUBENUM];
material plastic;

float time = iTime;

int iter = 0;

void init()
{
    plastic.color = vec3(0, 0.9, 0);
    plastic.metal = 0.1;
    plastic.gloss = 0.9;
    for (int i = 0; i < SPHERENUM; ++i)
    {
    	sp[i].pos = vec3(float(i) * 2. - 2., sin(time) -1., 0);
        sp[i].rad = 1.;
    	sp[i].mat = plastic;
    }
    for (int i = 0; i < CUBENUM; ++i)
    {
    	cu[i].pos = vec3(float(i) * 3. - float(CUBENUM),  sin(time), 3.);
        cu[i].rad = 1.;
    	cu[i].mat = plastic;
    }
}

float seed = 0.;
float rand(float f) { 
    return fract(sin(++seed * f)*43758.5453123); 
}
vec3 light(){
    return normalize(vec3(sin(time), 0.3, cos(time)));
}

vec3 background(vec3 rd)
{
	float sun = max(0.0, dot(rd, light()));
	float sky = max(0.0, dot(rd, vec3(0.0, 1.0, 0.0)));
	float ground = max(0.0, -dot(rd, vec3(0.0, 1.0, 0.0)));
	return 
		(pow(sun, 256.0)+0.2*pow(sun, 2.0))*vec3(2.0, 1.6, 1.0) +
		pow(ground, 0.5)*vec3(0.4, 0.3, 0.2) +
		pow(sky, 1.0)*vec3(0.5, 0.6, 0.7);
}

RayResult spherecol(camera cam, sphere sp)
{
    RayResult res;

	vec3 rc = cam.pos-sp.pos;
	float c = dot(rc, rc) - (sp.rad*sp.rad);
	float b = dot(cam.ray, rc);
	float d = b*b - c;
	float t = -b - sqrt(abs(d));
	float st = step(0.0, min(t,d));
	res.t =  mix(-1.0, t, st);
    res.mat = sp.mat;
    res.normal = -normalize(sp.pos - (cam.pos+cam.ray*t));
    return res;
}

RayResult cubecol(camera cam, cube cu)
{
    RayResult res;

    vec3 tmin = ((cu.pos + cu.rad * sign(cam.ray) * -1.) - cam.pos) / cam.ray;
    vec3 tmax = ((cu.pos + cu.rad * sign(cam.ray)) - cam.pos) / cam.ray;
    float mi = max(max(tmin.x, tmin.y), tmin.z);
    float ma = min(min(tmax.x, tmax.y), tmax.z);
    res.t = (min(mi, ma) >= 0. ? min(mi, ma) : max(mi, ma));
    if (ma < mi)
        res.t = -1.;
    res.mat = cu.mat;
    res.normal = normalize(vec3(float(mi == tmax.x)-float(mi == tmin.x),float(mi == tmax.y) -float(mi == tmin.y),float(mi == tmax.z) -float(mi == tmin.z)));
    return res;
}

vec3 makeColor2(RayResult r, camera cam)
{
    if (r.t < 0.)
        return background( cam.ray);
    cam.pos += (r.t - 0.001) * cam.ray;
    cam.ray = reflect(cam.ray, r.normal);
    float tomix = (dot(r.normal, cam.ray) * ( 1. - r.mat.metal));
    vec3 li = cam.light;
    cam.light = reflect(cam.light, r.normal);
    return mix(mix(background(cam.ray), vec3(0), 1.- (r.mat.gloss) * dot(li, r.normal)) + (1. - r.mat.gloss)/2. , r.mat.color , tomix);
}

RayResult rayTrace2(camera cam)
{
    RayResult r;

    r.t = -1.;
    if (iter > 4)
        return r;
    else
        iter++;
    for (int i = 0; i < SPHERENUM; ++i)
    {
        RayResult new;
        new = spherecol(cam, sp[i]);
        if (new.t > 0. && (new.t <= r.t || r.t == -1.))
            r = new;
    }
    for (int i = 0; i < CUBENUM; ++i)
    {
        RayResult new;
        new = cubecol(cam, cu[i]);
        if (new.t > 0. && (new.t <= r.t || r.t == -1.))
            r = new;
    }
    return r;
}

vec3 makeColor(RayResult r, camera cam)
{
    if (r.t < 0.)
        return background(cam.ray);
    cam.pos += (r.t - 0.001) * cam.ray;
    cam.ray = reflect(cam.ray, r.normal);
    float tomix = (dot(r.normal, cam.ray) * ( 1. - r.mat.metal));
    vec3 li = cam.light;
    cam.light = reflect(cam.light, r.normal);
    return mix(mix(makeColor2(rayTrace2(cam), cam), vec3(0), 1. - (r.mat.gloss) * dot(li, r.normal)) + (1. - r.mat.gloss)/2. , r.mat.color , tomix);
}


RayResult rayTrace(camera cam)
{
    RayResult r;

    r.t = -1.;
    if (iter > 4)
        return r;
    else
        iter++;
    for (int i = 0; i < SPHERENUM; ++i)
    {
        RayResult new;
        new = spherecol(cam, sp[i]);
        if (new.t > 0. && (new.t <= r.t || r.t == -1.))
            r = new;
    }
    for (int i = 0; i < CUBENUM; ++i)
    {
        RayResult new;
        new = cubecol(cam, cu[i]);
        if (new.t > 0. && (new.t <= r.t || r.t == -1.))
            r = new;
    }
    return r;
}

