    vec4 bc = vec4(mix(vec3(1.0), sc, gl_FragCoord.y / iResolution.y * 0.5), 1.0);
    vec4 c = bc;
    vec3 rd = normalize(cd + cr * uv.x + cu * uv.y);
    float t = np;
    bool h = false;
    vec3 p;
    vec2 d;

    for(int i=0; i<mx; i++)
    {
        p = cp + rd * t;
        d = df(p);

        if((d.x < pr) || (i == (mx - 1)))
        {
            h = true;

            break;
        }
        else
        {
            t += d.x;

            if(t > fp)
            {
                break;
            }
        }
    }

    if(h)
    {
		vec3 ld = lp - p;

        vec3 n = cn(p);

        float dl = max(0.0, dot(n, normalize(ld)));

        vec4 mc = vec4(0.5, 0.5, 0.5, 1.0);
        float s = 0.0;

        if(d.y == 1.0)
        {
            mc = vec4(0.82, 0.66, 0.34, 1.0);
        }
        else if(d.y == 2.0)
        {
            s = pow(dl, 32.0); 

            mc = vec4(0.6, 0.6, 0.6, 1.0);   
        }
        else {
            mc = vec4(0.82, 0.66, 0.34, 1.0);
        }

        #if USE_LIGHT_RADIUS
        vec3 r = ld / lr;
        float a = max(0.0, 1.0 - dot(r, r));
        c = mc * (ac + lc * (dl + s) * a);
        #else
        c = mc * (ac + lc * (dl + s));
        #endif

        c *= ss(p, lp, 0.02, 2.5);
        c -= ao(p, n) * 0.4;

        c = mix(c, bc, 1.0 - exp(-0.01 * t * t));   
    }

    fragColor = c;
}

