// uncomment for 2D curl debugging 
//#define SHOW_ARROW

const float ARROW_TILE_SIZE = 0.045;

// signed distance to capsule (for arrow visualization)
vec2 sdCapsule(vec3 p, vec3 p0, vec3 p1, float r)
{
    vec3 v = vec3( p1 - p0 );
	vec3 w = vec3( p - p0 );
	float t_seg = max( dot( v, w ), 0.0 );

	float v2 = dot( v, v );
	t_seg = min( t_seg, v2 );
	t_seg = t_seg / v2;
	w += v*(-t_seg);

	return vec2( sqrt( dot( w, w) ) - r, t_seg );
}

// computes arrow center location from given pos
vec3 arrowTilePos(vec3 p) 
{
	return (floor(p / ARROW_TILE_SIZE) + 0.5) * ARROW_TILE_SIZE;
}

// get the arrow color under pos (transparent if no hit)
vec4 arrowColor(vec3 p, vec3 arrow_center, vec3 arrow_vec) 
{
    const float scale = ARROW_TILE_SIZE;
    arrow_vec *= 0.15;
	vec2 darrow = sdCapsule(p, arrow_center - arrow_vec*scale, arrow_center + arrow_vec*scale, (ARROW_TILE_SIZE*1.005)*0.5 );
	if( darrow.x > 0.0 )
        return vec4(0.0);

    return vec4( mix(vec3(0.2,0.0,0.0), vec3(0.5,1.0,0.5), darrow.y), 1.0 );
}

