// the scalar field driving the effect
// curl operator is applied on top of it
float field2d(vec3 p)
{
    // combination of an upward stream...
    float v = -p.x*(1.0 - p.y);
    // ... and of simplex noise
    v += 0.25*(1.2 + 0.5*p.y) * simplex3d( vec3( p.xy*4.0, iTime*0.5 ) );

    return v;
}

// the curl operator in 2d
vec3 curl2d(vec3 p)
{
    vec3 e = vec3(0.001,-0.001,0.0);
    float fxp = field2d(p + e.xzz);
    float fxm = field2d(p + e.yzz);
    float fyp = field2d(p + e.zxz);
    float fym = field2d(p + e.zyz);

    vec3 c = vec3( 	(fyp - fym) / (2.*e.x),
                 	(-fxp + fxm) / (2.*e.x),
                 	0.0 );
	return c;   
}

// computes stream color at given pixel pos, returns transparent if outside of range
vec4 getColor2d(vec3 p)
{
    const int step_count = 160;
    const float dt = 0.005;
    int step_idx = 0;

    for( int i = 0; i < step_count; i++ )
    {   
    	vec3 f = curl2d(p);
        p -= f * dt;
        if( p.y < -1.3 )
            break;
        step_idx++; 
    }

    if( step_idx < step_count && abs(p.x) < 0.4 )
    {
        float fade = 1.0 - smoothstep( 0.15, 0.4, abs(p.x) );
        float ratio = pow( float(step_idx) / float(step_count), 2.0 );
        vec3 col = texture( iChannel0, vec2(abs(p.x), ratio-iTime*0.5) ).rgb;
        col *= vec3(0.8, 0.1, 0.1);	 // red tint
        col = pow( col, vec3(1.6) ); // darkens color

        return vec4( col, fade * (1.0 - ratio) );
    }

    return vec4(0.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 p = vec3( (fragCoord.xy / iResolution.xx - 0.5) * 2.0, 0.0 );

#ifdef SHOW_ARROW
    vec4 ar = arrowColor( p, arrowTilePos(p), curl2d( arrowTilePos(p) ) );
#else
    vec4 ar = vec4(0.0);
#endif

    vec4 c = getColor2d( p );
    vec3 bg_col = vec3(field2d( p ));
    vec4 col = mix( vec4(bg_col*0.5 + 0.5,1.0), ar, ar.a );
    col = mix( col, c, c.a );

    fragColor = col;
}

//SETTINGS
const int LOOPS = 1000;
float DEPTH_DIV = 120.;
float SCALE_DIV = 50.;
const float PRECISE = 1.;
bool VOLUME = false;

#define PI 3.1415926535897932384626433832795028
vec3 rX(vec3 p, float a) { //YZ
	float c,s;vec3 q=p;
	c = cos(a); s = sin(a);
	p.y = c * q.y - s * q.z;
	p.z = s * q.y + c * q.z;
    return p;
}

vec3 rY(vec3 p, float a) { //XZ
	float c,s;vec3 q=p;
	c = cos(a); s = sin(a);
	p.x = c * q.x + s * q.z;
	p.z = -s * q.x + c * q.z;
    return p;
}

vec3 rZ(vec3 p, float a) { //XY
	float c,s;vec3 q=p;
	c = cos(a); s = sin(a);
	p.x = c * q.x - s * q.y;
	p.y = s * q.x + c * q.y;
    return p;
}

//Map, 1 is wall, 0 is empty.
float map(vec3 p) { 
    float time = iTime;
    p = rY(p,(fract(time*.4)*float(mod(time*.4,4.)>3.)*(PI*2.)));
    float tunnel = float((p.x > 5. || p.x < -5.) || (p.y > 3. || p.y < -3.));
    float radius = 1.4;
    float sphere = float(length(p-vec3(asin(sin(time))*2.8,abs(sin(time*3.)*2.)-((3.-(radius/2.))),5.)) < radius);
    radius = 0.2;
    vec3 pos = vec3(0.,.7,1.4);
    pos = rX(p-pos,time);
    float bar = float((pos.y > -radius) && (pos.y < radius) && (pos.z > -radius) && (pos.z < radius));
    return tunnel+sphere+bar;
}

#define clamps(x) clamp(x,0.,1.)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    DEPTH_DIV *= PRECISE; SCALE_DIV *= PRECISE;
	vec2 uv = (fragCoord.xy / iResolution.xy)-.5;
    uv.x *= iResolution.x / iResolution.y;

    //Raycaster / Layering
    vec3 p; //Position
    float a = 0.; //"Alpha"
    for (int i = 0; i < LOOPS*int(max(PRECISE,1.)); i++) {
        vec3 pos = vec3(uv*((float(i)/SCALE_DIV)+1.),float(i)/DEPTH_DIV);
        if (map(pos) >= 1.) {
            if (VOLUME) {
                a += 1./float(LOOPS*int(max(PRECISE,1.)));
            } else {
                a = 1.;
                break; //Pixel doesn't need to be filled anymore. Stop the loop.
            }
        } else {
            p = pos;
        }
    }
    float fog = (p.z*DEPTH_DIV)/(float(LOOPS)*PRECISE);
    if (VOLUME) {
        fragColor = vec4(vec3(a),1.0);
    } else {
		fragColor = vec4(fract(p)*(1.-pow(fog,4.)),1.0);
    }
}

