	vec2 cp;
	vec2 p;
	for(int x=-2;x<=2;x++)
    for(int y=-2;y<=2;y++)
	{	
		p = vec2(x,y);
    	p += .5 + .5*sin( t * 10. + 9. * fract(sin((f+p)*mat2(2,5,5,2)))) - g;
		a = atan(abs(p.x),abs(p.y));
		r = length(p);
		p = vec2(a/3.14159*0.8,r);
		k = min(k, mix(max(abs(p.x)-p.y, abs(p.y)-p.x), mix(max(abs(p.x), abs(p.y)), length(p), -4.), -4.8));
    }

	fragColor.rgb = vec3(0.4, 0.54, 1) * k;
