float iSphere(in vec3 rayOrigin, in vec3 rayDirection, in vec4 sphere)
{
    vec3 oc = rayOrigin - sphere.xyz;
    float b = 2.0 * dot(oc, rayDirection);
    float c = dot(oc, oc) - sphere.w * sphere.w;
    float h = b*b - 4.0*c;
    if (h < 0.0)
        return -1.0;
    float t = (-b - sqrt(h)) / 2.0;
    return t;
}

vec3 nSphere(in vec3 pos, in vec4 sphere)
{
    return (pos - sphere.xyz)/sphere.w;
} 

float iPlane(in vec3 rayOrigin, in vec3 rayDirection)
{
    return -rayOrigin.y / rayDirection.y;
}

vec3 nPlane(in vec3 pos)
{
    return vec3(0.0, 1.0, 0.0);
}

vec4 sphere1 = vec4(0.0, 1.0, 0.0, 1.0);
float intersect(in vec3 rayOrigin, in vec3 rayDirection, out float resultT)
{
    resultT = 1000.0;
    float id = -1.0;
    // intersect with a sphere
    float tSphere = iSphere(rayOrigin, rayDirection, sphere1);
    // intersect with a plane
    float tPlane = iPlane(rayOrigin, rayDirection);

    if (tSphere > 0.0)
    {
        id = 1.0;
        resultT = tSphere;
    }

    if (tPlane > 0.0 && tPlane < resultT)
    {
        id = 2.0;
        resultT = tPlane;
    }

    return id;
}

