}

float map(vec3 p) {
    return max(min(sdfSphere(p), sdfBox(p)), -sdfSphere2(p));
}

float trace(vec3 r, vec3 o) {
	float t = 0.0;
    for (int i = 0; i < 32; ++i) {
    	vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }

    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = 1.0 - uv * 2.0;

    uv.x *= iResolution.x / iResolution.y;

    vec3 r = normalize(vec3(uv, 1.25));

    float theta = iTime * 0.1;
    r.yz *= mat2(cos(theta), -sin(theta), sin(theta), cos(theta));

    vec3 o = vec3(iTime * 0.2, 0.0, -iTime * 0.2);

    float t = trace(r, o);
    float fog = 1.0 / (1.0 + t * t * 0.5);
    vec3 fcc = vec3(1.0 - fog);

    vec3 floored = floor( o + r * t);
    if (fog > 0.2) {
		fragColor = vec4(fcc.x * ((mod(floored.x, 10.0)) / 10.0 + 0.5), 1.0 - fcc.x * ((mod(floored.x, 10.0)) / 10.0 + 0.5), 0.0, 1.0);
    } else {
    	fragColor = vec4(uv.x, 1.0, uv.y, 1.0);
    }
}

//Converts hsv to rgb - How the heck does this work? O_o
//Taken from https://gist.github.com/kylemcdonald/f8df3bc2f8d38ca2b7cb
vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

// Converts rgb to hsv - also have no idea how this works
//Not needed currently but might use it if I do more with this :3
//Taken from http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl
vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

