#define iTime sin(iTime*2.2)

#define DTM 78.0

#define D 8.0
#define T 35.0
#define M 35.0

#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy*2.0-1.0;

    uv.x *= iResolution.x/iResolution.y;

    float scale = (sin(iTime)*.5+.5);

    float d;

    vec3 col = vec3(0.0);

    for(int i = 0; i < 33; i++)
    {
        vec2 p = vec2(acos(float(i)/33.0*PI*2.0+iTime), asin(float(i)/33.0*PI*2.0+iTime));

        d = length(uv-p)*0.7;


    	if(d <= 3.0)
        {
            col += (vec3(
            sin(-iTime*PI*2.0+D+d*PI),
            cos(-iTime*PI*2.0+4.0*PI/3.0+M+d*PI),
            cos(-iTime*PI*2.0+2.0*PI/2.0+T+d*PI))*.5+.5)*sin(d*PI*DTM+iTime*DTM);
        }
    }

    d = length(uv)*2.2;

    col += vec3(
    	sin(-iTime*PI*3.0+D+d*PI),
        cos(-iTime*PI*2.0+4.0*PI/3.0+M+d*PI),
        cos(-iTime*PI*3.0+3.0*PI/2.0+T+d*PI));

		fragColor = vec4(col, 1.0);
}

// comment to disable perspective
#define MODE7

// hash without sine
// https://www.shadertoy.com/view/4djSRW
#define MOD3 vec3(.1031, .11369, .13787) // int range
vec2 hash22(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * MOD3);
    p3 += dot(p3, p3.yzx+19.19);
    return fract(vec2((p3.x+p3.y)*p3.z, (p3.x+p3.z)*p3.y));
}

// get hash offset (jitter and p are assigned in main)
#define getH(d) jitter * hash22(floor(p-d)) - d;

// basic line function (ends are clipped not rounded)
float line(in vec2 p, vec2 p0, vec2 p1) {
    vec2 n = normalize(p1-p0);
    p -= p0;
    float l = .5 * length(p1-p0);
    return abs(dot(p, vec2(n.y, -n.x))) + step(l, abs(dot(p, n)-l));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 res = iResolution.xy;
	vec2 uv = (fragCoord.xy-res/2.) / res.y + 1.e-4; // a small value is added to avoid errors where floor meets fract

    // init p, with zoom
	vec2 p = 16. * uv;

    // 'mode 7'
    #ifdef MODE7
    	p.y -= 10.;
    	p = - 5. * vec2(p.x/p.y, 20./p.y);
    #endif

    // scrolling
    p.x += iTime;

    // adjust cell jitter with mouse x
    // usually this would be a fixed number or 1.0 (not considered)
    float jitter = iMouse.z>0. ? min(1., iMouse.x / res.x) : 1.;

    // random offsets of neighbors (clockwise starting north)
    vec3 v = vec3(-1., 0., 1.);
    vec2 o[10];
    o[0] = getH(v.yz);
    o[1] = getH(v.zz);
    o[2] = getH(v.zy);
    o[3] = getH(v.zx);
    o[4] = getH(v.yx);
    o[5] = getH(v.xx);
    o[6] = getH(v.xy);
    o[7] = getH(v.xz);
    o[8] = o[0];	 // trying to prevent excess code
	o[9] = o[1];	// <--'

    // center offset
    vec2 c = getH(0.);

    // center cell dot (we won't know which lines connect, so all line caps contribute)
    float f = length(fract(p)-c);

    // check neighbors and combine into f:
    for(int n=0; n<8; n++) {

        // dots from neighbors
        f = min(f, length(fract(p)-o[n]));

        // lines between center and neighbors
        if(distance(c, o[n]) < 1.)
			f = min(f, line(fract(p), c, o[n]));

        // lines between adjacent neighbors
        if(distance(o[n], o[n+1]) < 1.)
			f = min(f, line(fract(p), o[n], o[n+1]));

        // lines between every other neighbor
        if(distance(o[n], o[n+2]) < 1.)
			f = min(f, line(fract(p), o[n], o[n+2]));
    }

    // adjust line thickness with mouse y
    f = iMouse.w>0. ? min(1., f / (1.-iMouse.y/res.y)) : f;

    #ifdef MODE7
    	fragColor = vec4(mix(f*vec3(1.3, 1., .7), vec3(.8, .8, 1.), clamp(p.y/70., 0., 1.)), 1.);
    #else
    	fragColor = vec4(f*vec3(1.3, 1., .7), 1.);
    #endif
}

float mfunc (float x)
{
  return abs(-1.0 + abs(-12.25 + abs(-8.0 + abs(-12.0 + abs(.95 * x + 4.0))))); 
}

float wfunc(float x)
{
//	return sin(x) + .15*sin(1.5*x);
    float wave = .25* sin(3.0*x) + .15*sin(1.5*x + 6.0 * iTime) + .14*cos(x + 3.0*iTime);
    return wave;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float domain = 9.0 * 3.14159;

    vec2 origin = vec2(-10.0, -8.0);

	vec2 uv = fragCoord.xy / iResolution.xy;
    uv *= domain;
    uv += origin;

     float cameraOff = 2.0*sin(iTime);

    vec3 col = vec3(0.0);
    {   
    	float outVal = mfunc(uv.x*2.0 + cameraOff);
    	float difference = abs(outVal - uv.y);

    	float edge = smoothstep(.1, .75, difference);
    	edge = pow(edge, .15);

    	col = vec3(10.0, 1.5, 0.0)*(1.0 - edge);
	}

     {   
    	float outVal = wfunc (uv.x + 1.25* iTime + (.5 * cameraOff));
    	float difference = abs(outVal - uv.y);

    	float edge = smoothstep(.1, .75, difference);
    	edge = pow(edge, .15);

    	col += vec3(0.0, 1.5, 10.0)*(1.0 - edge);
	}


   	fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time = iTime;
   	float scale = ( 40.0+20.0*sin(0.3*time));
    vec2 p = scale*(fragCoord - 0.5*iResolution.xy) / iResolution.y;

    //rotate
    float rotationrate = 0.1;
    float s = sin(rotationrate*time);
    float c = cos(rotationrate*time);
    p = mat2(c,s,-s,c)*p;

    //bipolar coordinates (sigma, tau)
    float a = min(10.0,2.0*time);
    float alpha = a*a - dot(p,p);
    float beta = a*a + dot(p,p);
    float sigma = atan( 2.0*a*p.y ,alpha);
    float tau = 0.5*log((beta + 2.0*a*p.x)/(beta - 2.0*a*p.x));

    //do something funky in bipolar corrdinates
    float freq = 20.0;
    float rate = 3.0;
    vec2 osc = 0.5*(1.0 + cos(freq*vec2(sigma,tau) + rate*time)) ;
    float bipolarOscillations  = 0.5*(osc.x+osc.y);

    float spotPattern = pow(bipolarOscillations,2.0 + 1.5*sin(0.2*time));
    float gray = spotPattern;
    vec3 color = vec3(gray);
 	fragColor  = vec4(color,1.0);
}

