//#define debug_sphere

precision highp float;

// Test color
vec3 mag = vec3(1.5454,0.5464,1.161981);
vec3 test = vec3(1.5454,0.5464,1.161981);

// pixel XY
vec2 xy;


// frustum limits
const float ncp = 0.;
const float fcp = 2.;
const float step = 0.03;

// lights
struct light{
	vec3 p;
	float di;
	float si;
	vec4 c;	
};
light KL;
light FL;

// time
float T = 0.;

// sin-cos
float sn = 0.;
float cn = 1.;

// ray data
struct ray{
 	vec3 o;
	vec3 d;
	bool h;
	vec3 hp;
	vec3 n;
    float m;
};	

void sincos(float t)
{
	sn = sin(t);
	cn = cos(t);
}	

//--------------------------------------------------

float sphere(vec3 _p, vec3 _c, float _r)
{
	return length(_p-_c)-_r;
}

float plane(vec3 _p, float _h)
{
	return _p.y - _h;
}

vec2 opU(vec2 _d, vec2 _o)
{
    return _d.x < _o.x ? _d : _o;
}

vec2 opI(vec2 _d, vec2 _o)
{
    return _d.x > _o.x ? _d : _o;
}

//--------------------------------------------------

// Materials
#define MAT_PLANE 0.01
#define MAT_KEYLIGHT 0.02

vec2 map(vec3 _p)
{
	vec2 d0 = vec2(100000.,0.);
    vec2 d = d0;

    vec2 sphere1 = vec2(sphere(_p,vec3(.0,0.,.0),.6),.5);
    vec2 sphere2 = vec2(sphere(_p,vec3(sn,-.5,cn),.8),.8);
    vec2 sphere3 = vec2(sphere(_p,vec3(-sn,-.5,-cn),.8),.8);
    vec2 plane1 = vec2(plane(_p,-1.),MAT_PLANE);
    vec2 light1 = vec2(sphere(_p,KL.p/30.,.1),MAT_KEYLIGHT);
    vec2 light2 = vec2(sphere(_p,FL.p/30.,.1),MAT_KEYLIGHT);

	d = opU(sphere2,sphere3);
	d = opI(d,sphere1);
    d = opU(d,plane1);

    // lights
    //d = opU(d,light1);
    //d = opU(d,light2);

    #ifdef debug_sphere
    d = opU(d0,sphere1);
    d = opU(d,plane1);
    #endif

	return d;	
}

ray camera(vec3 _pos, vec3 _target, vec2 _xy, float _fov)
{    
    // initialize ray
    ray cr;
	cr.o = vec3(_xy,0.);
	cr.d = normalize(vec3(_fov * _xy,1.));
	cr.h = false;

    // rotation matrix
    vec3 la = normalize(_target - _pos);
    vec3 up = vec3(0.,1.,0.);
    vec3 ri = normalize(cross(up,la));
    up = cross(la,ri);

    mat3 m = mat3(ri,up,la);

    // translation
    vec3 t = _pos;

    // apply transforms
    cr.d = m * cr.d;
    cr.o = m * cr.o;
    cr.o = t + cr.o;

    return cr;
}	

ray setupCamera(float t, vec2 xy)
{
    vec3 cam = vec3(sn,1.5,cn*.5) * .3;
    vec3 target = vec3(0.);
    ray cr = camera(cam, target, xy, .2);

    return cr;
}


//--------------------------------------------------

void rayCast(inout ray _r)
{	
	// ray start point
	vec3 p = _r.o;
	vec2 d = vec2(0.);
	float ld = 0.;

    sincos(iTime*-4.);

    // traverse map
    for(int t = 0; t < 1000; t++)
    {
        d = map(p);        
        p += _r.d * d.x;
        if(d.x < 0.001)
        {
            _r.h = true;
            _r.hp = p;
            _r.m = d.y;

            break;
        }
    }
}

void rayMarch(inout ray _r)
{
	// ray start point
	vec3 p = _r.o;
	vec2 d = vec2(0.);
	float ld = 0.;

	// traverse frustum
	for(float s=ncp; s<fcp; s+=step)
	{
		// step in front
		p += _r.d*step;

		// check hit
		d = map(p);
		if(!_r.h && d.x<0.)
		{
			_r.h = true;

			// interpolate p
			float i = step*ld/(ld-d.x);
			_r.hp = p;//_r.o + _r.d * (s-step+i);            
            break;
		}

		//last d
		ld = d.x;
	}
}

void normal(inout ray _r)
{
    vec2 eps = vec2(.001,.0);
    float dx = map(_r.hp + eps.xyy).x - map(_r.hp - eps.xyy).x;
    float dy = map(_r.hp + eps.yxy).x - map(_r.hp - eps.yxy).x;
    float dz = map(_r.hp + eps.yyx).x - map(_r.hp - eps.yyx).x; 
    _r.n = normalize(vec3(dx,dy,dz));
}

//--------------------------------------------------

vec4 material(ray _r)
{
    if(_r.m == MAT_PLANE)
    {
        float f = mod( floor(5.*_r.hp.z) + floor(5.*_r.hp.x), 2.0);
        return vec4(f);
    }
    else if(_r.m == MAT_KEYLIGHT)
    {
        return KL.c;
    }

	return vec4(.5,.2,.3,1.)/2.;	
}

vec4 diffuse(ray _r, light _l)
{		
	// diffuse
	vec3 ld = normalize(_l.p - _r.hp);
	float LdotN = max(dot(ld,_r.n),0.);

	vec4 diff = LdotN * _l.di * _l.c;

	return vec4(diff);
}

vec4 specular(ray _r, light _l)
{		
	// specular
	vec3 ld = normalize(_l.p - _r.hp);
	vec3 rf = reflect(ld,_r.n);
	float RdotE = max(dot(normalize(rf),normalize(_r.d)),0.);

	vec4 spec = RdotE * _l.si * _l.c;

	spec = pow(spec,vec4(12.)) * 4.;

	return vec4(spec);
}

vec4 ambient(light _l)
{
	return .05 * _l.c;	
}

float hardShadow(ray _r, vec3 _light)
{
    ray r = _r;
    r.o = _r.hp;
    r.d = normalize(_light - _r.hp);

    float s = 1.;
    float t = 0.02;    
    for(int i=0; i<30; i++)
    {        
        vec3 p = r.o + r.d * t;

        float d = map(p).x;

        if(d < 0.002)
            return 0.;

        t += d;
    }

    return 1.;
}

float softShadow(ray _r, vec3 _light)
{
    float tmin = 0.02;
    float tmax = 2.5;

    ray lr = _r;
    lr.o = _r.hp;
    lr.d = normalize(_light - _r.hp );

    float t = tmin;    
    float ss = 1.;
    for(int i=0; i<30; i++)
    {                
        vec3 p = lr.o + lr.d * t;
        float d = map(p).x;

        ss = min(ss, 1. * d / t);

        if(d < 0.002 || t > tmax)
            break;        

        t += clamp(d, .02, .1);
    }

    return clamp(ss,0.,1.);
}


vec4 colorize(ray _r)
{
	vec4 c = vec4(0.);

	// normal
	normal(_r);

	// material
	c = material(_r);

	// lighting
	c *= diffuse(_r,KL) * softShadow(_r,KL.p) + diffuse(_r,FL) * softShadow(_r,FL.p);
	//c *= diffuse(_r,KL) * hardShadow(_r,KL.p) + diffuse(_r,FL) * hardShadow(_r,FL.p);
	c += specular(_r,KL);
	c += specular(_r,FL);
	//c += ambient(KL);
	//c += ambient(FL);

 ///debug
 //test = vec3(softShadow(_r,KL.p));

	return c;
}

//--------------------------------------------------

vec4 render()
{
	vec4 c = vec4(0.);
	T = iTime;
	ray r;
	const float loops = 5.;

	// motion blur
	for(float i=0.; i<loops; i++)
	{
		// cast ray
		sincos(T);

        // camera
		r = setupCamera(T,xy);

		rayCast(r);		

		// hit
		if( r.h )
		{
			// Colorize
			c += colorize(r)/loops;
		}	
		// miss
		else
		{
			c += vec4(0.,0.,.2,0.)/loops;
		}		

		// move time for interframe
		T -= 1./(loops*20.);
	}

	return c;
}

