const mat2 foldb = mat2(0.7071067690849304, -0.7071067690849304, 0.7071067690849304, 0.7071067690849304);
const mat2 foldi = mat2(0.7071067690849304, 0.7071067690849304, -0.7071067690849304, 0.7071067690849304);

void fold(inout vec2 vec) {
    vec = (abs(vec*foldb))*foldi;
}

float DE(in vec3 pos) {

    float rot = (iTime*0.25)*PI*2.0;

    pos = rotateX(pos, rot);
    pos = rotateY(pos, rot);
    pos = rotateZ(pos, rot*0.5);

    // folding
    for(int i=0; i<16; i++) {

        fold(pos.xy);
        //if(pos.x+pos.y<0.0){pos.xy=-pos.yx;}
        //pos.xy = mix(pos.xy, -pos.yx, float(pos.x+pos.y<0.0));

        pos = rotateY(pos,  0.9+cos(iTime)*0.1);

        fold(pos.xz);
        //if(pos.x+pos.z<0.0){pos.xz=-pos.zx;}
        //pos.xz = mix(pos.xz, -pos.zx, float(pos.x+pos.z<0.0));

        pos = rotateZ(pos, -0.6-cos(iTime)*0.1);

        fold(pos.yz);
        //if(pos.y+pos.z<0.0){pos.yz=-pos.zy;}
        //pos.yz = mix(pos.yz, -pos.zy, float(pos.y+pos.z<0.0));


    }

    return min(max(
        tet(pos),
        -(length(pos)-1.7)
    ), length(pos)-1.6);
}

const vec3 shadowColor = vec3(0.0, 0.0, 0.25);
const vec3 backColor = vec3(0.5, 0.4, 0.5);
const vec3 oColor = vec3(0.25, 0.9, 0.25);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // camera
	vec2 view = fragCoord.xy / iResolution.xy * 2.0 - 1.0;

    float aspect = iResolution.x/iResolution.y;
    float a = tan(FOV * (PI/180.0) / 2.0);
    vec2 ab = vec2(a * aspect, a);

    // the start point, and the direction of the ray
    vec3 dir = normalize(vec3(ab*view, -1.0));
    vec3 point = vec3(0.0, 0.0, 5.0+sin(iTime)*1.0);

    // ray-march
    float closest = infinity;

    for(int i = 0; i<MAX_STEP; i++) {

        vec3 pos = point;

        float dist = DE(pos);

        if(dist < 0.0025) {
            fragColor = vec4(mix(
                oColor,
                shadowColor,
                sqrt(float(i)/MAX_STEP_F)
            ), 1.0);
            return;
        }

        closest = min(closest, dist);

        point += dir*dist;

    }

    // background shading
    fragColor = vec4(mix(
        shadowColor,
        backColor,
        sqrt(clamp(closest/2.0, 0.0, 1.0))
    ), 1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // [0, res] to [-1, 1]
	vec2 coord = -1.0 + (fragCoord.xy / iResolution.xy)*2.0;    
    vec2 ncoord = coord * vec2(iResolution.x / iResolution.y, 1.0); // take aspect ratio into account

    // rotation
    float angle = radians(iTime);
    angle = sin(angle*35.0) * radians(10.0); // fluctuate angle
    vec2 tcoord = vec2(ncoord.x * cos(angle) - ncoord.y * sin(angle),
                       ncoord.y * cos(angle) + ncoord.x * sin(angle));

    float lookAtOffsetY = sin(radians(iTime * 40.0)) * .5;

    // simple plane projection
    vec2 uv = vec2(tcoord.x, 1.0) / (tcoord.y + lookAtOffsetY);
    uv /= 4.0;

    // scrolling 
    uv.y = abs(uv.y) + iTime;

    vec4 col = texture(iChannel0, uv);
    col.r += .2; // tint

    // horizon
    col *= smoothstep(0.0, 0.4, abs(tcoord.y + lookAtOffsetY));

    // post process
    float vignette = (1.0 - abs(coord.x * coord.x)) * (1.0 - abs(coord.y * coord.y));    
    col *= vignette;

    fragColor = col;
}

float hash(float n) {
    return fract(sin(n)*43578.5453);
}

float box(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

float de(vec3 p) {
    vec4 q = vec4(p, 1);
	q.y = mod(q.y + 1.0, 2.0) - 1.0;
    q.xyz -= 1.0;

    for(int i = 0; i < 3; i++) {
        q.xyz = abs(q.xyz + 1.0) - 1.0;
        q = 1.2*q/clamp(dot(q.xyz, q.xyz), 0.25, 1.0);
    }

    float f = box(q.xyz, vec3(1.0))/q.w;
    f = min(f, p.y + 2.0);
    f = min(f, min(p.x + 3.0, -p.x + 3.0));
    f = min(f, min(p.z + 3.0, -p.z + 3.0));

    return f;
}

float trace(vec3 ro, vec3 rd, float mx) {
    float t = 0.0;
    for(int i = 0; i < 80; i++) {
        float d = de(ro + rd*t);
        if(d < 0.001 || t >= mx) break;
        t += d;
    }

    if(t < mx) return t;
    return -1.0;
}

// Approvement thanks to Shane. vstrace= shadow trace in volumentric loop.
// less detailed, dithering and breaks quicker.
float vstrace(vec3 ro, vec3 rd, float mx) {
    float t = 0.1*hash(dot(ro, rd));
    for(int i = 0; i < 50; i++) {
        float d = de(ro + rd*t);
        if(d < 0.01 || t >= mx) break;
        t += d;
    }

    if(t < mx) return t;
    return -1.0;
}

vec3 normal(vec3 p, out float e) {
    vec2 h = vec2(0.001, 0.0);

    vec3 n1 = vec3(
        de(p + h.xyy),
        de(p + h.yxy),
        de(p + h.yyx)
	);

    vec3 n2 = vec3(
        de(p - h.xyy),
        de(p - h.yxy),
        de(p - h.yyx)
	);

    // edge detection.
    float d = de(p);

    vec3 e3 = abs(d - 0.5*(n1 + n2));
    e = min(1.0, pow(e3.x + e3.y + e3.z, 0.55)*10.0);
    return normalize(n1 - n2);
}

vec4 texcube(sampler2D s, vec3 p, vec3 n) {
    vec3 m = pow(n, vec3(30.0));
    vec4 x = texture(s, p.zy);
    vec4 y = texture(s, p.xz);
    vec4 z = texture(s, p.xy);

    return (m.x*x + m.y*y + m.z*z)/(m.x + m.y + m.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

    vec3 ro = vec3(-2.9*sin(iTime*0.5), -1, 2.9*cos(iTime*0.5));
    vec3 ww = normalize(vec3(0, -0.7, 0)-ro);
    vec3 uu = normalize(cross(vec3(0, 1, 0), ww));
    vec3 vv = normalize(cross(ww, uu));
    vec3 rd = normalize(uv.x*uu + uv.y*vv + 1.97*ww);

    vec3 col = vec3(0);
    vec3 key = vec3(0, 0.0*sin(iTime*0.3), 0);

    float t = trace(ro, rd, 10.0);
    if(t > 0.0) {
        float edg;
        vec3 pos = ro + rd*t;
        vec3 nor = normal(pos, edg);

        // ambient occlusion.
        float occ = 0.0, sca = 1.0, ste = 0.003;
        for(int i = 0; i < 15; i++) {
            float d = de(pos + nor*ste);
            occ += (ste - d)*sca;
            sca *= 1.0;
            ste += ste/(float(i) + 1.0);
        }
        occ = 1.0 - clamp(occ, 0.0, 1.0);

        vec3 lig = normalize(key - pos);
        float dis = length(pos - key);

        // direct lighting with hard shadows.
        col += 0.3*clamp(dot(lig, nor), 0.0, 1.0)
            *step(0.0, -trace(pos + nor*0.001, lig, dis));

        // indirect lighting with ambient occlusion.
        col += 0.1*clamp(dot(-lig, nor), 0.0, 1.0)*occ;

        // material.
        col *= texcube(iChannel0, 0.5*pos, nor).xyz;

        // edge emission texture           // avoid pixel dancing by fading the effect while the veiwer is farther away.
        col += mix(col, vec3(0, 0.1, 2.1), edg/(0.7*length(ro)));
    }

    // volumetric shadows
    float s = hash(dot(uv, vec2(12.23, 39.343)))*0.05;
    float vol = 0.0;
    // need less light strength the closer you are to the light.
    float e = 0.1*smoothstep(0.0, 3.5, length(key - ro));
    for(int i = 0; i < 70; i++) {
        if(s > t) break;
        vec3 pos = ro + rd*s;

        vec3 lig = normalize(key - pos);
        float dis = length(key - pos);

        // shadow trace at each position along the march.
        float l = step(0.0, -vstrace(pos, lig, dis));
        // light strength is proportional to distance from light.
        l *= e/dis;

        vol += l;
        s += 0.05;
    }

    // blue light rays.
    col += 0.6*vec3(0.5*vol, 0.5*vol, vol);
    col = pow(col, vec3(1.0/2.2));

    // vignetting
    vec2 q = fragCoord/iResolution.xy;
	col *= pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.25 );

	fragColor = vec4(col, 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 screen = fragCoord.xy / iResolution.xy;
    float sound = texture (iChannel0, vec2(screen.x, 0.75)).x;

    vec3 color = vec3(.0);

    color += vec3(abs(sin(iTime)) - smoothstep(0.0, 0.2, abs(sound - screen.y)),
                  abs(sin(iTime * 2.0/3.0 * 3.1415)) - smoothstep(0.0, abs(sin(iTime) * 0.1), abs(sound - screen.y)),
                  abs(sin(iTime * 4.0/3.0 * 3.1415)) - smoothstep(0.0, abs(sin(iTime) * 0.1), abs(sound - screen.y)));

    fragColor = vec4(color, 1.0);
}

#define MARCH_STEPS 30.0
#define EPSILON 0.001
#define GRADIENT_STEP 0.02

//#define DEMONSTRATE_BUG true

#define M_PI 3.1415926535897932384626433832795

// for generating distance map of box with rounded corners
float box(vec3 p, vec3 b, float roundness)
{
	return length(max(abs(p)-(b-vec3(roundness)),0.0))-roundness;
}

// combine shapes smoothly
float smin( float a, float b, float k )
{
    float h = clamp(0.5+0.5*(b-a)/k,0.0,1.0);
    return mix(b,a,h)-k*h*(1.0-h);
}

// field of view of camera
#define FOV 0.06
// generate combined distance field
float map(vec3 p)
{
    /*
#ifndef DEMONSTRATE_BUG
    // repeat following shape?
    if(mod(iTime,6.)>2.0) p.xz=mod(p.xz,vec2(8.)-4.);
#else
    if(DEMONSTRATE_BUG) p.xz=mod(p.xz,vec2(8.)-4.);
#endif
*/
	p.xz=mod(p.xz,vec2(8.))-4.;

    // draw rounded box
    float boxSize = 2.;
	float d = box(p,vec3(boxSize), boxSize/3.);

	return d;
}

// raymarching procedure with lots of output info
void march(vec3 origin, vec3 direction, out vec3 rayHead, out bool hit, out float dist, out float steps)
{
    float distanceTraveled = 0.;
    hit = true;
    for(float step=0.; step<MARCH_STEPS; step++)
    {
        rayHead = origin + (direction*distanceTraveled);
        dist = map(rayHead);
        steps = step;
        if(dist<EPSILON||step>=MARCH_STEPS) return;
        distanceTraveled += dist;
    }
    hit = false;
}

// get origin and direction of ray to be cast for camera/screen xy (pixel) coordinate
void cameraRay(vec2 scanLines, vec3 cameraPosition, vec3 target, float fov, out vec3 pos, out vec3 dir)
{
	vec3 forward = normalize(target-cameraPosition);
    vec3 up = vec3(0.,1.,0.);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(forward,right));

    right = right*scanLines.x*fov;
    up = up*scanLines.y*fov;

    pos = cameraPosition;
    dir = (right+up+forward);
}

// get surface normal of point on distance field for shading
vec3 gradient( vec3 pos )
{
	const vec3 dx = vec3( GRADIENT_STEP, 0.0, 0.0 );
	const vec3 dy = vec3( 0.0, GRADIENT_STEP, 0.0 );
	const vec3 dz = vec3( 0.0, 0.0, GRADIENT_STEP );
	return normalize(vec3(
		map( pos + dx ) - map( pos - dx ),
		map( pos + dy ) - map( pos - dy ),
		map( pos + dz ) - map( pos - dz )
	));
}

// calculate diffuse lighting for point on distance field given surface normal
float diffuse(vec3 normal, vec3 lightPosition)
{
	return max(dot(normal,normalize(lightPosition)),0.);
}

// calculate diffuse lighting for point on distance field
float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t );
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

// calculate combined lighting for point on distance field
float shadePoint(vec3 position, vec3 normal, vec3 lightPosition)
{
    float diffuseLight = diffuse(normal, lightPosition);
	// speculative light - it might be specular, but we're not really sure
    float specularLight = pow(diffuseLight,30.);
	float shadow = softshadow(position, normalize(lightPosition), 0.02, 2.25);
    return (diffuseLight+specularLight)*shadow;
}

// generate background color
vec3 background(vec2 uv)
{
	return vec3(0.5,0.5,0.5)*uv.y;
}

// compose image
void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    // scale coordinates to -1 -> +1, adjusted for screen aspect ratio
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv = uv*2.-1.;
    uv.x = uv.x*iResolution.x/iResolution.y;

    // get ray representing camera scanline position and direction
    vec3 cameraPosition = normalize(vec3(sin(iTime*1.5),-0.7,cos(iTime*1.5)))*200.;
    vec3 cameraOrigin;
    vec3 cameraDirection;
    cameraRay(uv, cameraPosition, vec3(0.,0.,0.), FOV, cameraOrigin, cameraDirection);

    // calculate distance, hit point, etc of distance field via raymarching
    vec3 rayHead;
    bool hit;
    float dist;
    float steps = 40.;
    march(cameraOrigin, cameraDirection, rayHead, hit, dist, steps);

    // calculate normal for point on distance field
    vec3 normal = gradient(rayHead);

    // calculate light for point
	vec3 rotLightPos = vec3(-1.,-1.,0.);
    vec3 latRotLightPos = vec3(0.,-1.,-1.);
    vec3 lightPos = vec3(cos(iTime),sin(iTime),cos(iTime));
    float light = 0.;
    light += shadePoint(rayHead, normal, rotLightPos);
    light += shadePoint(rayHead, normal, latRotLightPos);
    light += shadePoint(rayHead, normal, lightPos);
    light /= 3.;

    // specify color of entire distance field
    vec3 fieldColor = vec3(1.0,0.4,0.4);

    // final steps and background
    vec3 color = fieldColor*light;
    if(!hit) color = background(uv);

    // set fragment shader color
    fragColor = vec4(color,1.);
}

float func (float x)
{
	return sin(x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float domain = 4.0 * 3.14159;

    vec2 origin = vec2(0.0, -2.0);

	vec2 uv = fragCoord.xy / iResolution.xy;
    uv *= domain;
    uv += origin;


    float outVal = func(uv.x + iTime);

    float difference = abs(outVal - uv.y);

    float edge = smoothstep(.1, .2, difference);

    fragColor = vec4(edge);

    const float axisLineSize = .05;
    const float gridLineSize = .025;

    //draw graph paper

    fragColor = abs(uv.x) < axisLineSize ? vec4(0.0,0.0,1.0,1.0) : fragColor;
    fragColor = abs(uv.y) < axisLineSize ? vec4(0.0,0.0,1.0,1.0) : fragColor;

    vec2 frac = fract(uv);
    bool onGrid = any(lessThan(abs(frac), vec2(gridLineSize)));

    fragColor = onGrid ? vec4(0.0,0.0,1.0,1.0) : fragColor;


}

#define TAU 2. * acos(-1.)

float annulus( vec2 p, float r1, float r2 ) {
    return max(length(p) - r1, r2 - length(p));
}

float rect( vec2 pos, vec2 dim ) {
    return length(max(abs(pos)-dim,0.0));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 st = fragCoord.xy / iResolution.xy;


    if(iResolution.y>iResolution.x){
        //Vertical
        st.y*=iResolution.y/iResolution.x;
        st.y-=(iResolution.y*.5-iResolution.x*.5)/iResolution.x;
    }else{
        //Horizontal
        st.x*=iResolution.x/iResolution.y;
        st.x-=(iResolution.x*.5-iResolution.y*.5)/iResolution.y;
    }  


    fragColor  = vec4((floor(st*10.0)/10.),0.5+0.5*sin(iTime),1.0);

    float cells = 10.0;

    st = floor(st*cells)/cells;

    float w = 3. * ( sin(st.y * TAU * 2.) ) + st.y * 30.0;
    float v = (cos(st.x * TAU * 2. + w) + 1.0) / 2.0;

	st = st * cells;

    float f = 0.;

    if( mod(st.x,2.0) < 1.0 ) {
        f = mod(st.y,2.0) < 1.0 ? 0.2 : 1. ;
    } else {
        f = mod(st.y,2.0) < 1.0 ? 1. : 0.2 ;       
    }

    vec3 c = vec3( step(0.5, v ), f, 1.-f*v );
    vec3 t = vec3( sin(iTime* 0.11), sin(iTime * 0.07), sin(iTime * 0.17));


    //st = fract(st);

    if( f == 1.0 ) {
        c = 1. - c;
    }


    fragColor = vec4(c + t, 1.0);
}

