// COPPER BARS, AMIGA STYLE!
// 30 Oct 2016

// Follow me on Twitter : @GeekyMcGeekface
// Feedback welcome, this is my first ShaderToy.

// v1.1 (1/11/2016) : Factored into vectors. Thanks for FabriceNeyret2 for the suggestions.
// v1.2 (1/11/2016) : ADD_SOME_PLOP. (Thanks https://www.shadertoy.com/view/MlSSDV). Also music.

#define ADD_SOME_PLOP

//#define DO_BRIGHTNESS_SNAP
#define BRIGHTNESS_SNAP	(0.2)

#define QUANTIZE_X				// Comment out to show smooth sine-waves. Enable for Amiga-style copper.
#define QUANTIZE_SIZE		8.0
#define BAR_SIZES			vec3(3.0, 2.5, 2.0)		// Bigger = Thinner bars

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
#ifdef ADD_SOME_PLOP
    // Funky plasma-type effect
    // Tweaked version of : https://www.shadertoy.com/view/MlSSDV
	float t = iTime/2.;
    vec2 U = 8.* fragCoord / iResolution.xy - 4.;
//    U = 4.* U / iResolution.xy;

    for (int i=0; i<8; i++)
    	U += cos( U.yx *3. + vec2(t,1.6)) / 3.,
        U += sin( U.yx + t + vec2(1.6,0)) / 2.,
        U *= 1.3;

    vec4 o;
//	o += length(mod(U,2.)-1.);  // black & white
	o += length(U) / 32.0;
#endif

    vec2 fragPos = vec2(2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;   

#ifdef QUANTIZE_X
    fragPos.x = (floor(fragPos.x * QUANTIZE_SIZE) / QUANTIZE_SIZE);
#endif

    float xQuant = fragPos.x + iTime;
    float xQuant2 = xQuant + iTime;
    vec3 c;

    c.x = ((sin(xQuant*2.5)*0.5) * (cos((xQuant2*1.7)+0.55) * 0.5)) + iTime * 0.2;
    c.y = sin(xQuant2*3.8+0.3)*0.13 * sin(iTime * 0.9);
    c.z = cos(xQuant*3.1) * 0.07;
    c = (c + fragPos.y) * BAR_SIZES;

    // This line injects gaps between the bars, otherwise all components of c
    // are continuous (non-zero) values, which means that c.x (=red) is
    // a continuous signal & only red bars are visible.
    c *= step( vec3(1.0, 2.0, 0.0), mod(c, vec3(2.0, 4.0, 1.0) ));

    // See : http://pietermans.com/blog/triangle-wave-algorithms/
    // Move to range [-1,+1] (0.5 ensures round bars /\ vs \/)
    // and abs() fn makes signal into sawtooth [0,1]
    c = abs((fract(c+0.5) * 2.0) - 1.0);

    // Snap values < BRIGHTNESS_SNAP down to 0.
#ifdef DO_BRIGHTNESS_SNAP
    c *= step( BRIGHTNESS_SNAP, c );
#endif

   	fragColor = c.x > 0.0 ? vec4(c.x, 0.0, 0.0 , 1.0)
        	:	c.y > 0.0 ? vec4(0.0, c.y, 0.0 , 1.0)
            :				vec4(0.0, 0.0, c.z, 1.0);
#ifdef ADD_SOME_PLOP
    fragColor *= o;
#endif
}

float sphere( in vec3 p){
 return length(p) - 0.5;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
    vec2 st = 2.0*uv -1.0;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    vec3 cam = vec3(0.0, 0.0, -1.0);
    vec3 ray = vec3(st.x, st.y, 1);
    ray = normalize(ray);
    float depth = 0.0;
    for(int i=0; i<64; i++){
     vec3 p = cam + depth * ray;
     depth = depth += sphere(p);

    }
    vec3 p = cam + depth * ray;
    if(abs(sphere(p)) < 0.1){

     fragColor = vec4(0.3+dot(normalize(p), normalize(vec3(1.0,1.0,0.0)))); 
    }

}

// Created by inigo quilez - iq/2013
