const float e = 2.7182818284590452353602874713527;
const float PI = 3.14159265359;
const float TWOPI = 2.0*PI;


//https://swiftcoder.wordpress.com/2010/06/21/logarithmic-spiral-distance-field/
float Spiral(vec2 vector, float a, float b){
    float angle = atan(vector.y,vector.x);
    float r = length(vector);
    float n = (log(r/a)/b - angle)/TWOPI;
    float r1 = a*pow(e,b*(angle+floor(n)*TWOPI));
    float r2 = a*pow(e,b*(angle+ceil(n)*TWOPI));
    return min(abs(r1-r),abs(r2-r));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    float scale = 30.0;
	vec2 uv = fragCoord.xy / iResolution.xy;    
    uv.x *= iResolution.x/iResolution.y;
	uv *= scale; 
    vec2 center = vec2(iResolution.x/iResolution.y*0.5,0.5)*scale;

    float s = 9999.0;
    const float offset = 5.0;
    for( float i = -6.0; i <= 6.0; i++)
        for( float j = -3.0; j <= 3.0; j++){
        	s = min(s,Spiral(uv-center+vec2(offset*i,offset*j),1.0,sin(iTime*0.1)*3.0*length(vec2(i,j))));
        }

    vec3 color = mix(vec3(0.0,0.0,0.0),vec3(0.45,0.25,0.2),s);
    fragColor = vec4(color,1.0);

}

