// get the distance from a torus (how does this work?)
float distTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

// get the distance from a sphere
float distSphere( vec3 p, float s )
{
  p = p - vec3(0.,0.,-0.25);
  return length(p)-s;
}

float distBox(vec3 p, vec3 b, float roundness)
{
	return length(max(abs(p)-(b-vec3(roundness)),0.0))-roundness;
}

ray getLookRayFlawed(vec2 uv, vec3 eye, vec3 target, float fov)
{
	target = vec3(target.x + (uv.x*fov), target.y + (uv.y*fov), target.z);
    vec3 fwdDirection = normalize(target-eye);
    return ray(eye,fwdDirection);
}

ray getLookRay(vec2 uv, vec3 cameraPos, vec3 target, float fov)
{
	vec3 forward = normalize(target-cameraPos);
    vec3 up = vec3(0.,1.,0.);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(forward,right));

    right = right*uv.x*fov;
    up = up*uv.y*fov;

    return ray(cameraPos,right+up+forward);
}
