float dist(vec3 p)
{
	float d = 0.;

    d = distBox(p, vec3(0.09), 0.05);
    d = max(-distTorus(p, vec2(0.08,0.045)), d);

    return d;
}

const float maxSteps = 80.;
float marchRay(ray marcher)
{
    float hitDistance = 0.00001;
    float distanceTravelled = 0.;
	for(float i=0.0; i<maxSteps; i++)
    {
        float nextDistanceTravelled = dist(marcher.position + (marcher.direction * distanceTravelled));
        if(nextDistanceTravelled < hitDistance) return 1.-(i/maxSteps);
        distanceTravelled += nextDistanceTravelled;
    }
    return 0.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // scale fragCoord xy to 0.0 -> 1.0
	vec2 uv = fragCoord.xy/iResolution.xy;
    // transform uv x/y to go from -1.0 -> 1.0
    uv = uv * vec2(2) - vec2(1);
    // tranform uv x axis so that it's not skewed by aspect ratio
    uv.x = uv.x * iResolution.x / iResolution.y;

    float mask = 1.0-distance(uv, vec2(0.,0.));
    vec3 rot = vec3(-6.,-6.,-6.);
    rot = vec3(sin(iTime), cos(iTime*0.5), cos(iTime)) * 30.;
    ray lookRay = getLookRay(uv, rot, vec3(0.,0.,0.), 0.005);
	mask = marchRay(lookRay);
    fragColor = vec4(mask,mask,mask,1.);
}

#define PI 3.14159265359
