#define time (sin((float(__LINE__+123)*123.0)*.25+.5)*(iTime))
#define flux(x) (vec3(cos(x),cos(4.0*PI/3.0+x),cos(2.0*PI/3.0+x))*.5+.5)
#define circle(x) (vec2(cos(x),sin(x)))

float saw(float x)
{
    float f = mod(floor(abs(x)), 2.0);
    float m = mod(abs(x), 1.0);
    return f*(1.0-m)+(1.0-f)*m;
}
vec2 saw(vec2 x){ return vec2(saw(x.x), saw(x.y)); }
vec3 saw(vec3 x){ return vec3(saw(x.x), saw(x.y), saw(x.z)); }
vec4 saw(vec4 x){ return vec4(saw(x.x), saw(x.y), saw(x.z), saw(x.w)); }

vec2 cmul(vec2 v1, vec2 v2) { return vec2(v1.x * v2.x - v1.y * v2.y, v1.y * v2.x + v1.x * v2.y); }
vec2 cdiv(vec2 v1, vec2 v2) { return vec2(v1.x * v2.x + v1.y * v2.y, v1.y * v2.x - v1.x * v2.y) / dot(v2, v2); }


float s1;
float s2;

void mainImage(out vec4 f,vec2 p)
{
    const int iters = 4;
    const int trans = 4;
    p = p/iResolution.xy-.5;
    p *= 2.0;
    p *= iResolution.x/iResolution.y;
    float f1 = 0.0;
    float m1 = 1.0;
    vec2 p0 = p;
    for(int i = 0; i < iters; i++)
    {
        s1 = float(i+1)/float(iters);

        float f2 = 0.0;
        float m2 = 1.0;

    	vec2 quotient = vec2(0.0);
        for(int j = 0; j < trans; j++)
    	{
            s2 = float(j+1)/float(trans);
            vec2 offa = circle(s2*12.0+time)*
                (1.5+sin(time));
            vec2 offb = circle(s2*1234.0+time)/4.0*
                (1.5+sin(time));
            vec2 numerator = p + offa;
            vec2 denominator = p + offb;
            quotient += (cdiv(numerator, denominator));
            m2 *= dot(denominator, denominator);
            m2 *= dot(numerator, denominator);
            m2 = clamp(sqrt(abs(m2)), 0.0, 1.0);

            f2 += length(quotient)/float(trans);
    	}

        m1 *= m2;
        p = (cos(quotient*PI*2.0))*m1+p*(1.0-m1);

        f1 += atan(p.y, p.x)/PI;
    }

    m1 = pow(m1, 1.0/float(iters));

    float s = time;
    float b = smoothstep(0.0, 1.0/5.0, saw(s+f1));
    float w = smoothstep(4.0/5.0, 1.0, saw(s+f1));

    float t = smoothstep(0.4, .6, sin(time)*.5+.5);
    f = vec4(p.xy, 0.0, 1.0)*m1;
    f = vec4(flux(f1), 1.0)*saw(m1);
}

//Just trying to pull apart nimitz' spiral from https://www.shadertoy.com/view/MdlXRS
//to see how it works!

#define TAU 6.28318530716

float spiral(vec2 p,float scl, float phase) 
{
	float r = length(p);
	r = log(r);
	float a = atan(p.y, p.x);
	return abs(mod(scl*(r-1.0/scl*a) - phase*2.0,TAU)-1.)/2.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    float modifier1 = 1.0 / spiral(uv, 1.0, iTime + 2.3);
    float modifier2 = 1.0 / spiral(uv, 2.0, iTime);
    float modifier3 = 1.0 / spiral(uv, 3.0, iTime*2.0 - 1.0);
    float modifier4 = 1.0 / spiral(uv, 25.0 , iTime*5.0 - 2.0);
    vec3 color = vec3 (0.19, 0.43, 0.82);
	fragColor = vec4(color,1.0)
        * (modifier1 +
           modifier2 +
           modifier3 + 
           modifier4)/4.0;
}

vec2 bipolar(vec2 p,float a, float b){

    float alpha = a*a - dot(p,p);
    float beta = a*a + dot(p,p);
    float gamma = sqrt(alpha*alpha - 4.0*p.y*p.y*a*a);
    float sigma = atan( 2.0*a*p.y ,alpha + b*gamma );
    float tau = 0.5*log((beta + 2.0*a*p.x)/(beta - 2.0*a*p.x));

    return vec2(sigma,tau);
}

