float rand(vec3 a)
{
    return fract(sin(dot(vec3(30,1.3,320)*a*a,vec3(12.9898,8.233,-19.848))*43758.5453))-.5;
}


vec3 g(vec3 co)//random gradiant
{
    return vec3(rand(co.xyz),rand(co.zxy),rand(co.yzx));
}

vec4 f1(vec3 a, vec3 b) //gradiant/position dot product for square vertex
{
    return vec4(dot(g(a),b),dot(g(a+vec3(1,0,0)),b-vec3(1,0,0)),dot(g(a+vec3(0,1,0)),b-vec3(0,1,0)),dot(g(a+vec3(1,1,0)),b-vec3(1,1,0)));
}
float ip(vec4 a,vec2 b)//interpolation for a square
{
    return mix(mix(a.r,a.g,smoothstep(0.,1.,b.x)),mix(a.b,a.a,smoothstep(0.,1.,b.x)),smoothstep(0.,1.,b.y));
}

float perlin(vec3 p,float time)
{
    time+=iTime*.2;
    p+=time;
    vec3 r = fract(p);
    vec3 l = floor(p);
    vec4 a = f1(l,r);
    float n1 = ip(a,r.xy);
    a = f1(l+vec3(0,0,1),r-vec3(0,0,1));
    return mix(n1,ip(a,r.xy),smoothstep(0.,1.,r.z));//inetrpolation for the cube
}
float fbm(vec3 p,float time)
{
     float b =perlin(p,time);
    for (int i=0;i<5;i++){
        p*=1.5;
    b= mix(b,perlin(p,time),.05);
    }
    return b;
}
