    vec2 mse = (iMouse.xy / iResolution.x-vec2(.5,.5*iResolution.y/iResolution.x))*12.3;
    mat3 r = mat3(1,0,0,0,cos(mse.y),sin(mse.y),0,-sin(mse.y),cos(mse.y))*mat3(cos(mse.x),0,sin(mse.x),0,1,0,-sin(mse.x),0,cos(mse.x));
    vec3 vd = normalize(vec3(uv,1.))*r;
    vec3 col =vec3(max3(((90.*sin(500.*cross(vec3(fbm(vd,1.),fbm(vd,0.),fbm(vd,3.)),vd)))))>89.).rgb*sin(100.*cross(vec3(fbm(vd,1.),fbm(vd,0.),fbm(vd,3.)),vd));
   // col = texture(iChannel0,90.*sin(cross(vec3(fbm(vd,1.),fbm(vd,0.),fbm(vd,3.)),vd))).rgb;
