const float MAX_SCALE = 20.0;
const float MIN_SCALE = 2.0;
const mat3 m = mat3( 0.00,  0.80,  0.60,
                    -0.80,  0.36, -0.48,
                    -0.60, -0.48,  0.64 );

vec3 hsv2rgb(vec3 c)
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

float hash( vec2 p )
{    
	float h = dot(p,vec2(127.1,311.7));
    return fract(sin(h)*43758.5453123);
}

float hash(float f)
{    
	float h = f * 311.7;
    return fract(sin(h)*43758.5453123);
}

float scale(vec2 sq){
    return floor(mix(MIN_SCALE,MAX_SCALE,hash(vec2(sq))));
}

vec2 square(vec3 pos){
    vec2 sq = vec2(floor(pos.x)+0.5,floor(pos.z)+0.5);
	sq = vec2(floor(pos.x)+0.5,floor(pos.z)+0.5);
        return sq;
}

float dTo(vec3 pos){
        float s = scale(square(pos));
        vec2 sq = square(s*pos);
        vec2 delta = s*pos.xz - sq;
        return dot(delta,delta);}

bool isPath(vec3 pos){
    return abs(pos.x) < 0.07 || mod(pos.z,2.0) < 0.14;
    return abs(pos.x - sin(pos.z)*20.0) <1.0 || abs(pos.x) < 0.07;
}

bool isFarm(vec3 pos){
    float h = hash(square(pos));
    return h > 0.6 && h < 0.7 ;
}

float sdPlane( vec3 p )
{
    float t = iTime/2.0;
    float h=0.0;
    h -= 0.6*(sin(0.1*(p.x-p.z)+1.6)*cos(0.23*p.z));
    if (!isPath(p) && !isFarm(p)) h += (1.0-dTo(p))*0.05;
	return p.y-h;
}


//----------------------------------------------------------------------

vec2 map( in vec3 pos )        
{
    return  vec2( sdPlane(     pos), 1.0 );;
}

vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 0.0;
    float tmax = 40.0;

	float precis = 0.002;
    float t = tmin;
    float m = -1.0;
    for( int i=0; i<50; i++ )
    {
	    vec2 res = map( ro+rd*t );
        if( res.x<precis || t>tmax ) break;
        t += res.x;
	    m = res.y;
    }

    if( t>tmax ) m=-1.0;
    return vec2( t, m );
}



vec3 calcNormal( in vec3 pos )
{
	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}


vec3 diffuse(vec3 pos){
        vec2 sq = square(pos);
    	if(isPath(pos)) return vec3(0.3,0.3,0.3);
        if(isFarm(pos)) return vec3(0.3,0.3,0.1);
    	sq = square(pos);
        float d2 = dTo(pos);
        float h = 0.2+0.3*hash(sq);
        h += floor(d2*20.0)*0.05;

        vec3 rgb1 = hsv2rgb(vec3(h,0.9,0.3));
        //vec3 rgb2 = hsv2rgb(vec3(0.6,0.6,0.2));
        //vec3 rgb3 = hsv2rgb(vec3(0.0,1.0,1.0));
        //rgb1 = mix(rgb1,rgb2,mod(sq.x, 2.0));

    	if (hash(sq) < 0.2) return vec3(0.6,0.4,0.2);

        return rgb1;
}



vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 skyTop = hsv2rgb(vec3(0.6,0.6,0.4));
    vec3 skyBottom = hsv2rgb(vec3(0.55, 0.3, 0.6));
    vec3 col = mix(skyBottom,skyTop,rd.y*5.0);
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y;
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );
        vec3 diff = diffuse(pos);
        vec3 L = vec3(2.0);
        col = diff * dot(nor,vec3(0.0,1.0,0.0)) * L;
    	col = mix( col, skyBottom, 1.0-exp( -0.002*t*t ) ); //fog

    col = mix(vec3(0.5,0.0,0.0),col,m);
    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy/iResolution.xy;
    vec2 p = -1.0+2.0*q;
	p.x *= iResolution.x/iResolution.y;
    vec2 mo = vec2(8.0,1.0);//iMouse.xy/iResolution.xy;

	float time = 15.0 + iTime*5.0;

	// camera	
	vec3 ro = vec3( -0.5+3.5*cos(6.0*mo.x), 4.0 + 1.0*mo.y, -5.0 + 3.5*sin(6.0*mo.x) );
    vec3 ta = vec3( -0.5, -0.4, 0.5 );
    ro.z += 0.2*time;
    ta.z += 0.2*time;

	// camera-to-world transformation
    mat3 ca = setCamera( ro, ta, 0.0 );

    // ray direction
	vec3 rd = ca * normalize( vec3(p.xy,2.0) );

    // render	
    vec3 col = render( ro, rd );

	col = pow( col, vec3(0.4545) );

    fragColor=vec4( col, 1.0 );
}

