#define USE_SMOOTH
#define M_PI 3.14159265358979
#define M_2PI 6.28318530717958
#define RADIUS .25
#define NUMCURLS 6

//http://iquilezles.org/www/articles/palettes/palettes.htm
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 O, vec2 U ) {
    vec2 R = iResolution.xy;
    U = (2.*U-R)/R.y;

    float 	dist = length(U),
			normTheta = .5+atan(U.y, U.x)/M_2PI,
        	val = 0.;

    for (int i=-1; i<NUMCURLS-1; i++) {
        float iFloat = float(i);
        float 	ringPre = (iFloat == -1.)? 0.: (normTheta + iFloat) * RADIUS,
            	ringPost = (normTheta + iFloat + 1.) * RADIUS;

#ifdef USE_SMOOTH
        float ringVal = smoothstep(-.01, 0., dist-ringPre) - smoothstep(-.01, 0., dist-ringPost);
#else
        float ringVal = step(ringPre, dist) - step(ringPost, dist);
#endif
        val += ringVal * (normTheta + iFloat + 1.);
    }

    val /= float(NUMCURLS);
    val += cos(iTime*.3);

    vec3 col = pal(val, 
                   vec3(0.5,0.5,0.5),
                   vec3(0.5,0.5,0.5),
                   vec3(1.0,1.0,1.0),
                   vec3(0.0,0.33,0.67) );

    O = vec4(col * smoothstep(0.81, .8, dist), 1.);
}

/////////////////////////////////////////////////////////////////////////////
// Set COL to 0. to see the shapes colorized.
// Use ISSOLID in conjunction with ISTHRESHOLDto determine if 
// the shape should be filled in.
/////////////////////////////////////////////////////////////////////////////

#define COL 1.
#define ISSOLID 0
#define ISTHRESHOLD 1
#define PI 3.14159
#define MAXSIDES 10

struct metaball{
    vec2 pos;
    vec3 colour;
    float radius;

};    
struct metangon{
	vec2 pos;
    int numSides;
    vec3 colour;
    float radius;
};

vec2 rotate(in vec2 vec, in float rot){
    vec2 newVec;
	newVec.x = vec.x * cos(rot) - vec.y * sin(rot);
	newVec.y = vec.x * sin(rot) + vec.y * cos(rot); 
    return newVec;
}

vec3 doMetangon(in metangon mngon, in vec2 testPoint, in float rot){
    // Basically, we want to fake a "distance" to use by finding the projection
    // of the vector from the center of the shape to the testPoint onto the
    // normal (scaled by "radius") of each side, and take the biggest one.

    vec2 testVec = testPoint - mngon.pos;   
    vec2 sideNormal = vec2(0., mngon.radius);
    sideNormal = rotate(sideNormal, rot);    
    float maxDist = dot(testVec, normalize(sideNormal));    
    for (int i = 1; i < MAXSIDES; i++){
        // A silly hack to get around the need for constant loop iterations
        if (i >= mngon.numSides){
            break;
        }            
        sideNormal = rotate(sideNormal, radians(360./ float(mngon.numSides))); 
        maxDist = max(maxDist, dot(testVec, normalize(sideNormal)));
    }

    return mngon.colour * mngon.radius / maxDist;
}

vec3 doMetaball(in metaball mball, in vec2 testPoint){
    vec2 pos = mball.pos;
	return mball.colour *  mball.radius/length(pos - testPoint);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xx;

    metaball ball1 = metaball(vec2(0.5+ .5*cos(iTime + 0.4), 0.25 + .2*sin(iTime)),
                              vec3(COL,1.,1.), .1);
    metangon cube1 = metangon(vec2(0.5 + .5 * sin(iTime), 0.15), 4,
                              vec3(1.,1., COL), .07);
    metangon tri1 = metangon(vec2(abs(0.3 + .5 * sin(iTime)), 0.4 + .2 * cos(iTime)), 3,
                              vec3(1.,COL, 1.), .07);
    metaball ball2 = metaball(vec2(abs(0.1 + sin(iTime)), 0.2 + 0.2 * cos(iTime)), 
                              vec3(1., COL, COL), 0.05);    
    metangon pentagon1 = metangon(vec2(0.5, abs(0.3 + .2 * sin(iTime * 0.7 + 5.))), 5,
                              vec3(COL, 1., COL), .075);    
    metangon septagon1 = metangon(vec2(abs(0.4 + sin(iTime)), 0.4 - 0.2 * cos(iTime)), 7,
                              vec3(1., 1., 1.), 0.06);

    vec3 rgb = vec3(0.);
    rgb += doMetaball(ball2, uv);
    rgb += doMetaball(ball1, uv);
    rgb += doMetangon(cube1, uv, iTime * 1.1 + 0.3);
	rgb += doMetangon(tri1, uv, iTime);
    rgb += doMetangon(pentagon1, uv, -iTime);
    rgb += doMetangon(septagon1, uv, 0.);                            
    rgb /= 1.75;

    #if ISTHRESHOLD == 1
    float threshold = 1.;
    if (rgb.x <= threshold){
        rgb.x = 0.;
    }
    if (rgb.y <= threshold){
        rgb.y = 0.;
    }
    if (rgb.z <= threshold){
        rgb.z = 0.;
    }    
    #if ISSOLID == 0
    if (rgb.x > threshold + 0.1 || 
        	rgb.y > threshold + 0.1 ||
        	rgb.z > threshold + 0.1){
        rgb = vec3(0.);
    }
    #endif
    #endif

	fragColor = vec4(rgb ,1.0);
}

precision highp int;precision highp float;

// Parameters for your demo

#define THICKNESS (0.85)
    // larger means narrower
#define SPACING (200.0)
    //ditto
#define SPEED (0.6)
    // Higher is faster

float           //not a color, but an ingredient
get_sine_01(    //Named for its output range
    in float time, in vec2 coords,      // parameters!
    in vec2 center, in float radius, in float speed)
{
    float whereami = radius*distance(center,coords) - speed*time;
    return 0.5+0.5*sin(whereami);
} //get_sine_01

float squish(in float f)
{ //-1..1->0..1
  return (f+1.0)*0.5;
}
vec2 lisa(in float time, in float a, in float b, in float delta)
{ // a Lissajous curve.  Formula from en.wikipedia.org.
    return vec2(squish(sin(a*time+delta)), squish (sin(b*time)));
} //lisa

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 pixel_coord = fragCoord.xy / iResolution.xy;

    // Centers of sines a and b.  All numbers empirical.
    vec2 ca = lisa(t*0.5*SPEED,        5.0,4.0,0.0);
    vec2 cb = lisa(t*0.835744*SPEED,   3.0,2.0,1.8);

    cb = mix(ca, cb, 0.6);      //Move cb closer to ca.
        // I did this because the centers were spending too much time apart
        // and this was an easy way to mitigate that.

    // Sine values
    float sa = get_sine_01(t, pixel_coord, ca, SPACING, 10.0);
    float sb = get_sine_01(t, pixel_coord, cb, SPACING, 10.0);

    // Make them sharp rather than blurry
    sa=step(THICKNESS,sa);
    sb=step(THICKNESS,sb);

    // Combine the two sines
    float res=max(sa,sb);
        // or min(sa,sb)   sa-sb   mix(sa,sb,0.5)
    fragColor = vec4(res,0.0,0.0,1.0);
       // or vec4(sa,sb,0.0,1.0);

} //mainImage

