vec3 HSVtoRGB(float h, float s, float v) 
{
  return((clamp(abs(fract(h +vec3(0.,2./3.,1./3.))*2.-1.)*3.-1.,0.,1.)-1.)*s+1.)*v;
}

mat3 rot3xy( vec2 angle )
{
  vec2 c = cos( angle );
  vec2 s = sin( angle );
  return mat3( c.y,       -s.y,        0.0,
               s.y * c.x,  c.y * c.x, -s.x,
               s.y * s.x,  c.y * s.x,  c.x );
}

vec4 DE1( in vec4 v )
{
  float fR = dot( v.xyz, v.xyz );
  vec4 q;
  int k = 0;
  vec3 vO = vec3( 0.0, 0.0, 0.0 );

  for ( int i = 0; i < 32; i++ )
  {
    q = v*g_matIterator1;
    v.xyz = q.xyz;

    if ( dot( v, g_planeReflect1 ) < 0.0 )
    {
      q = v*g_matReflect1;
      v.xyz = q.xyz;
      vO.x += 1.0;
    }

    if ( dot( v, g_planeReflect2 ) < 0.0 )
    {
      q = v*g_matReflect2;
      v.xyz = q.xyz;
      vO.y += 1.0;
    }

    if ( dot( v, g_planeReflect3 ) < 0.0 )
    {
      q = v*g_matReflect3;
      v.xyz = q.xyz;
      vO.z += 1.0;
    }

    q = v*g_matIterator2;
    v.xyz = q.xyz;

    v.xyz = v.xyz*g_fScale;
    fR = dot( v.xyz, v.xyz );
    k = i;
  }
  return vec4( vO, ( sqrt( fR ) - 2.0 ) * pow( g_fScale, -float(k+1) ) );
}

