void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float tr = texture(iChannel0,vec2(0.01, 0.01)).r;  // sound value
  g_fScale -= (tr*2.3 - 2.0) / 18.0;    // fractal scaling

  if (AAX>1 || AAY>1)
  {
    vec4 col = vec4(0,0,0,1);
    for (int xp = 0; xp < AAX; xp++)
    for (int yp = 0; yp < AAY; yp++)
    {
      vec2 pos = fragCoord + vec2(xp,yp) / vec2(AAX,AAY);
      col += render (pos);
    }
    fragColor.rgb = col.rgb / AA;
  }
  else fragColor = render (fragCoord);
}

#define NEAR 0.01
#define FAR 10.0

#define STEP_SIZE 0.6
#define MAX_ITER 200
#define DELTA 0.005
#define A_SPEED 0.1
#define CAM_DIST 3.0

#define RECAST_MAX 5

#define GLOW_POWER 6.0
#define GLOW_COLOR vec3(0.2,0.4,0.4)
#define GRASS_SIZE vec2(0.005,0.4)

#define SKY_COLOR vec3(0.1,0.3,0.4)

#define ONE 0.00390625
#define ONEHALF 0.001953125

#define PIXEL_SCALE 1.0

float fade(float t) {
  // return t*t*(3.0-2.0*t); // Old fade, yields discontinuous second derivative
  return t*t*t*(t*(t*6.0-15.0)+10.0); // Improved fade, yields C2-continuous noise
}

float noise(vec2 P)
{
  vec2 Pi = ONE*floor(P)+ONEHALF; // Integer part, scaled and offset for texture lookup
  vec2 Pf = fract(P);             // Fractional part for interpolation

  // Noise contribution from lower left corner
  vec2 grad00 = texture(iChannel0, Pi).rg * 4.0 - 1.0;
  float n00 = dot(grad00, Pf);

  // Noise contribution from lower right corner
  vec2 grad10 = texture(iChannel0, Pi + vec2(ONE, 0.0)).rg * 4.0 - 1.0;
  float n10 = dot(grad10, Pf - vec2(1.0, 0.0));

  // Noise contribution from upper left corner
  vec2 grad01 = texture(iChannel0, Pi + vec2(0.0, ONE)).rg * 4.0 - 1.0;
  float n01 = dot(grad01, Pf - vec2(0.0, 1.0));

  // Noise contribution from upper right corner
  vec2 grad11 = texture(iChannel0, Pi + vec2(ONE, ONE)).rg * 4.0 - 1.0;
  float n11 = dot(grad11, Pf - vec2(1.0, 1.0));

  // Blend contributions along x
  vec2 n_x = mix(vec2(n00, n01), vec2(n10, n11), fade(Pf.x));

  // Blend contributions along y
  float n_xy = mix(n_x.x, n_x.y, fade(Pf.y));

  // We're done, return the final noise value.
  return n_xy;
}

float tnoise(vec3 p)
{
    return (noise(p.xz/5.0) + (noise(p.xz/2.0))/12.0)*4.0;
}

vec3 displaceTerrain(vec3 p)
{
    p.y += tnoise(p) ;
    return p;
}

vec3 displaceGrass(vec3 p)
{
    p = displaceTerrain(p);
    p.x += sin(p.z + p.y*3.0 + iTime/2.0)/8.0 * (p.y*3.0) + cos(p.x + p.y*6.0 + iTime*3.0)/20.0 * (p.y*6.0);
    p.y -= abs(sin(p.x*20.01124 + p.z*24.0241)/20.0);

    p.x += sin(p.z*12.042)*0.04 +  sin(p.z*9.1230)*0.06;
    p.z += sin(p.x*9.612)*0.03 +  sin(p.x*13.3231)*0.055;

    return p;
}

float sdGrassField(vec3 p)
{
    vec3 q = displaceTerrain(p);

    vec3 grassDist = vec3(0.05,0,0.05);
	vec3 pg = displaceGrass(p) + grassDist * 0.5;
	vec3 k = floor (pg / grassDist);
	pg -= grassDist * (k + 0.5);

    vec2 b = GRASS_SIZE;

    float g = length(max(abs(pg)-vec3(b,0.001),0.0));
    float f = -q.y;

    return min(f,g);
}

float sdSphere(vec3 p)
{
    p += vec3(0,0,0);
    return length(p) - 1.0;
}

float map(vec3 p)
{
    return sdGrassField(p);//min(sdSphere(p),);
}

float getLightFactor(vec3 p,vec3 normal)
{
    vec3 lightPos = vec3(0,-2.0,iTime);
    vec3 lp = p - lightPos;
    float power = 3.0;
    float intensity = max(1.0-length(lp)/power,0.0);
    float dist = 1.0-dot(normalize(lp),normal);

    float factor = intensity*dist;

    return factor;
}

vec3 castRay(vec3 pos, vec3 dir)
{
    //Initialize RayMarching
    vec3 p = pos;
    float rl = 0.0;

    vec3 color = vec3(0);//Output color

    float near = FAR;
    float iters = 0.0;

    //March
    for(int i = 0; i < MAX_ITER; i++)
    {
        near = map(p);//Nearest surface distance

        rl += near;
        if(near <= DELTA || rl > FAR)//If the ray hits something
        {
            break;
        }
        p += STEP_SIZE*near*dir;//March forward
        iters++;
    }

    if(near >= DELTA)//If the point is outside a volume
    {
        vec3 a = SKY_COLOR;
        vec3 b = GLOW_COLOR;
        float n = pow(iters/float(MAX_ITER), 1.0/GLOW_POWER);
        color = mix(a,b,n);
    }
    else
    {
        vec2 delta = vec2 (DELTA, 0.0);
		vec3 normal = normalize (vec3 (
			map (p + delta.xyy) - map (p - delta.xyy),
			map (p + delta.yxy) - map (p - delta.yxy),
			map (p + delta.yyx) - map (p - delta.yyx)));

        float lightFactor = getLightFactor(p,normal);

        vec3 sky = GLOW_COLOR;

        vec2 ss = p.xz*2.0;

        color = vec3(texture(iChannel0, mod(ss.xy/30.0,vec2(1))).r/1.7,texture(iChannel0, mod(ss.xy/30.0,vec2(1))).g/1.5,0);
        color = mix(color,sky/3.0,1.0-lightFactor);
        float fog = clamp(rl / FAR,0.0,1.0);
        color = mix(color,GLOW_COLOR,fog);
    }

    //color += vec3(pow(iters/float(MAX_ITER), 1.0/GLOW_POWER));
    //fog
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Pixel scaling. Coulda done that somewhere else to prevent rendering the same pixel
    vec2 fc;
    fc.x = floor(fragCoord.x/PIXEL_SCALE)*PIXEL_SCALE;
    fc.y = floor(fragCoord.y/PIXEL_SCALE)*PIXEL_SCALE;

    //Setting up screen-correct uv
	vec2 uv = ( fc.xy / iResolution.xy ) * 2.0 - vec2( 1 );
    uv.x *= iResolution.x/iResolution.y;

    //Setting up rotation
    float sa = sin( iTime * A_SPEED );
    float ca = cos( iTime * A_SPEED );

    //Creating ray
    vec3 or = vec3(sa*CAM_DIST,0,-ca*CAM_DIST + iTime);
    or.y = -tnoise(or)-1.3;
    vec3 di = -normalize(vec3(uv.x,uv.y,-1.0));

    //Rotating orientation
    mat3 r;
    r[0] = vec3(ca,0,sa);
    r[1] = vec3(0,1,0);
    r[2] = vec3(-sa,0,ca);
    di = r*di;

    vec3 color = castRay(or,di);

	fragColor = vec4(color,1);
}

