// This shows the multiplication table modulo 200 represented as lines connecting points
//	on the unit circle, as explained in the Mathologer video https://www.youtube.com/watch?v=qhbuKbxJsk8
//
// In the background is the generalized Mandelbrot set with an arbitrary power that shows the connection
//	of the shapes obtained with the "modulo multiplication" and the amount of lobes in the Mandelbrot set.
//  (this was also suggested by the same Mathologer video)
//
const float	TWOPI = 6.283185307179586476925286766559;

// You can remove this line for the "regular" multiplication but this mirroring
//  makes the fitting to the Mandelbrot lobes better...
#define MIRROR

// Comment this to obtain the regular Mandelbrot set, otherwise coordinates outside the unit circle are reciprocated inside (love it!)
#define RECIPROCAL_MANDELBROT

const float	MAX_LOBES = 10.0;	// Maximum amount of Mandelbrot lobes
const float	MODULO = 200.0;		// Amount of subdivisions of the circle

// Computes the generalized mandelbrot set Zn+1 = Zn^p + C with an arbitrary p
// We can notice that p is directly driving Z's argument, hence the apparent multiple rotations of the set's main lobes
//
vec3 ComputeMandelbrot( vec2 _uv, float _mul ) {
    float	stepsCount = 0.0;
    vec2	z = vec2( 0.0 );
    for ( float i=0.0; i < 64.0; i++ ) {
        z += _uv;

        float	f = pow( dot( z, z ), 0.5 * _mul );
        float	a = _mul * atan( z.y, z.x );
        z = f * vec2( cos( a ), sin( a ) );
        if ( dot( z, z ) > 10.0 )
            break;
        stepsCount++;
    }
    return vec3( stepsCount / 64.0 );
}

vec3 ComputeInnerColor( vec2 _uv, float _mul ) {

    float	isOnLine = 0.0;

	vec2	scStart, scEnd;

    #ifdef MIRROR
		_uv.y = abs( _uv.y );	// Better matching
    #endif

	// Compute start and end anglse based on multiplier
	float	dStartAngle = TWOPI / MODULO;
	float	dEndAngle = _mul * dStartAngle;
    float	offset = -0.5 * TWOPI / (_mul - 1.0);	// This angular offset is used to make troughs concide with the Mandelbrot set
	float	startAngle = dStartAngle + offset;
	float	endAngle = dEndAngle + offset;

    for ( float i=1.0; i < MODULO; i++ ) {
        // Compute start and end position on the unit circle, forming a line
        scStart.y = sin( startAngle );
        scStart.x = cos( startAngle );
        scEnd.y = sin( endAngle );
        scEnd.x = cos( endAngle );

        // Compute line normal
        vec2	normal = normalize( vec2( scEnd.y - scStart.y, scStart.x - scEnd.x ) );

        // Compute distance to line
        vec2	delta = _uv - scStart;
        float	orthoDistance = abs( dot( delta, normal ) );

        // Check if the current position is on the line or not
        isOnLine += smoothstep( 0.005, 0.0, orthoDistance );

        startAngle += dStartAngle;
        endAngle += dEndAngle;
    }

    return vec3( 1.0 - clamp( isOnLine, 0.0, 1.0 ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2	R = iResolution.xy;
    vec2	uv = ( 2. * fragCoord - R ) / R.y;

uv *= 1.5 + 0.5 * sin( iTime );	// Fun with flags

	float	mul = 1.0 + 0.5 * MAX_LOBES * (1.0 - cos( 0.2 * iTime ));

    float	radius = length( uv );
	float	D = mix( 1.0, 1.0 / (radius*radius), smoothstep( 0.95, 1.05, radius ) );
    #ifdef RECIPROCAL_MANDELBROT
		vec3	C = 0.5 * (ComputeInnerColor( D*uv, mul ) + ComputeMandelbrot( D*uv, mul ));
    #else
		vec3	C = 0.5 * (ComputeInnerColor( D*uv, mul ) + ComputeMandelbrot( uv, mul ));
    #endif
	fragColor = vec4( C, 1.0 );
}

