#define MARCH_STEPS 50.0
#define MARCH_EPSILON 0.04
#define SHADOW_STEPS 50.0
#define SHADOW_EPSILON 0.005
#define GRADIENT_STEP 0.02
#define AA
//#define RENDER_DISTANCE

#define M_PI 3.1415926535897932384626433832795

float sphere( vec3 p, float s )
{
  return length(p)-s;
}

// --- //

// field of view of camera
#define FOV 0.15
float CAMERA_DISTANCE = 40.;
// generate combined distance field
float map(vec3 p)
{
	float d;

    vec3 q = p;
    q = q - vec3(0.,-1.,0.);
    q.x = q.x + 0.6*cos((iTime*2.+q.y)*2.*M_PI/3.);
    q.z = q.z + 0.4*cos((iTime*3.+q.y)*2.*M_PI/3.);
    d = sphere(q, 2.);

    // ground plane
    float planeWidth = 5.;
    float e = -p.y+2.+MARCH_EPSILON;
    e = max(e, -(planeWidth - abs(p.z)));
    e = max(e, -(planeWidth - abs(p.x)));

    d = min(d, e);

	return d;
}

// raymarching procedure with lots of output info
void march(vec3 origin, vec3 direction, out vec3 rayHead, out bool hit, out float dist, out float steps)
{
    float distanceTraveled = 0.;
    hit = true;
    for(float step=0.; step<MARCH_STEPS; step++)
    {
        //rayHead = origin + (direction*distanceTraveled);
        rayHead = origin + (direction*distanceTraveled)*.5;
        dist = map(rayHead);
        steps = step;
        if(dist<MARCH_EPSILON||step>=MARCH_STEPS) return;
        distanceTraveled += dist;
    }
    hit = false;
}

// get origin and direction of ray to be cast for camera/screen xy (pixel) coordinate
void cameraRay(vec2 scanLines, vec3 cameraPosition, vec3 target, float fov, out vec3 pos, out vec3 dir)
{
	vec3 forward = normalize(target-cameraPosition);
    vec3 up = vec3(0.,1.,0.);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(forward,right));

    right = right*scanLines.x*fov;
    up = up*scanLines.y*fov;

    pos = cameraPosition;
    dir = (right+up+forward);
}

// get surface normal of point on distance field for shading
vec3 gradient( vec3 pos )
{
	const vec3 dx = vec3( GRADIENT_STEP, 0.0, 0.0 );
	const vec3 dy = vec3( 0.0, GRADIENT_STEP, 0.0 );
	const vec3 dz = vec3( 0.0, 0.0, GRADIENT_STEP );
	return normalize(vec3(
		map( pos + dx ) - map( pos - dx ),
		map( pos + dy ) - map( pos - dy ),
		map( pos + dz ) - map( pos - dz )
	));
}

// calculate diffuse lighting for point on distance field given surface normal
float diffuse(vec3 normal, vec3 lightPosition)
{
	return max(dot(normal,normalize(lightPosition)),0.);
}

// calculate diffuse lighting for point on distance field
float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<int(SHADOW_STEPS); i++ )
    {
		float h = map( ro + rd*t );
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<SHADOW_EPSILON || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

// calculate combined lighting for point on distance field
float shadePoint(vec3 position, vec3 normal, vec3 lightPosition)
{
    float diffuseLight = diffuse(normal, lightPosition);
	// speculative light - it might be specular, but we're not really sure
    float specularLight = pow(diffuseLight,50.);
	float shadow = softshadow(position, lightPosition, 0.02, 2.25);
    float ambient = 0.1;
    return (diffuseLight*0.4+specularLight*2.9)*shadow+ambient;
}

// generate background color
vec3 background(vec2 uv)
{
    return vec3(0.,0.,0.);
	//return vec3(0.5,0.5,0.5)*uv.y;
}

vec4 getPixel(vec2 fragCoord)
{
    // scale coordinates to -1 -> +1, adjusted for screen aspect ratio
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv = uv*2.-1.;
    uv.x = uv.x*iResolution.x/iResolution.y;

    // get mouse coords
    vec2 mouse = iMouse.xy/iResolution.xy;

    // get ray representing camera scanline position and direction
    float cameraRotationSpeed = 0.2;
    vec3 cameraPosition = normalize(vec3(sin(iTime*cameraRotationSpeed),
                                         -.15,
                                         cos(iTime*cameraRotationSpeed)));
    if(iMouse.z>0.)
        cameraPosition = normalize(vec3(	sin(mouse.x*2.*M_PI),
                                        	-((1.-mouse.y)*2.)-0.001,
                                        	cos(mouse.x*2.*M_PI)));
    cameraPosition *= CAMERA_DISTANCE;
    vec3 cameraOrigin;
    vec3 cameraDirection;
    cameraRay(uv, cameraPosition, vec3(0.,0.,0.), FOV, cameraOrigin, cameraDirection);

    // calculate distance, hit point, etc of distance field via raymarching
    vec3 rayHead;
    bool hit;
    float dist;
    float steps;
    march(cameraOrigin, cameraDirection, rayHead, hit, dist, steps);

    // calculate normal for point on distance field
    vec3 normal = gradient(rayHead);

    // calculate light for point
    vec3 rotLightPos = vec3(sin(iTime),-1.5,cos(iTime));
    float light = 0.;
    light += shadePoint(rayHead, normal, rotLightPos)*2.;

   	vec3 lightPos;
    lightPos = vec3(1.,-0.5,1.);
    light += shadePoint(rayHead, normal, lightPos)*0.6;
    lightPos = vec3(-1.,-0.5,1.);
    light += shadePoint(rayHead, normal, lightPos)*0.6;
    lightPos = vec3(1.,-0.5,-1.);
    light += shadePoint(rayHead, normal, lightPos)*0.6;
    lightPos = vec3(-1.,-0.5,-1.);
    light += shadePoint(rayHead, normal, lightPos)*0.6;


    light /= 2.;

    // specify color of entire distance field
    vec3 fieldColor = vec3(1.,0.8,0.5);
    if(rayHead.y-2. > -0.001) {
        float checkerMask = (mod(rayHead.x,1.)>0.5?1.:0.) + (mod(rayHead.z,1.)>0.5?1.:0.)/2.;
        fieldColor = vec3(0.9,0.9,1.)*(checkerMask*0.7+0.3);
    }
    // final steps and background
    vec3 color = fieldColor*light;
    if(!hit) color = background(uv);

    // distance map
    #ifdef RENDER_DISTANCE
    color = vec3(steps/MARCH_STEPS);
    #endif

    float vignette = 1.;
    vignette *= pow(1.-abs(uv.x/2.), 0.9);
    vignette *= pow(1.-abs(uv.y), 1.);
    color *= vignette;

    // set fragment shader color
    return vec4(color,1.);
}

// compose image
void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 aaX = fragCoord;
    aaX.x += 0.5;
    vec2 aaY = fragCoord;
    aaY.y += 0.5;
	vec4 color = getPixel(fragCoord);
    #ifdef AA
    color += getPixel(aaX);
    color += getPixel(aaY);
    color /= 3.;
    #endif
    fragColor = color;
}

vec3 L(vec3 d) {
    return mix(vec3(0,.5,1), vec3(1), pow(max(dot(d, vec3(0, 1, 0)), 0.), .2));
}
void mainImage( out vec4 c, vec2 f )
{
	vec2 R=iResolution.xy,u = f/R;

    vec3 d = vec3(u*2.-1., 1);

    vec3 po = vec3(0, 4, iTime);
    vec3 p = po;
    d.y*=R.y/R.x;
     vec3 m = L(d+vec3(0, .6, 0));

    bool h = false;

    for (int i = 0; i < 100; ++i) {
    	float f = p.y+texture(iChannel0, p.xz/10.).r*2.+sin(p.x+p.z/10.)*cos(p.z-p.x/100.)-texture(iChannel0, p.xz/100.).r*2.;
        if (length(p-po)>20.) break;
        if (f < .2) {
            h = true;

        }
        p+=d*f/5.;
    }
    if (h)
    	    m = mix(
                texture(iChannel1, p.xz).grb+(vec3(.8-length(p-po)/20.))/2.,
                m, pow(length(p-po)/20., 25.))*(1.-texture(iChannel0, p.xz/10.).r*.7);
    c = pow(vec4(m, 1)*(1.-length(u-.5)/1.62), vec4(1./1.5));
}

#define MARCH_STEPS 150.0
#define EPSILON 0.00005
#define GRADIENT_STEP 0.02

#define M_PI 3.1415926535897932384626433832795

float torus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float sphere( vec3 p, float s )
{
  p = p - vec3(0.,0.,-0.25);
  return length(p)-s;
}

float box(vec3 p, vec3 b, float roundness)
{
	return length(max(abs(p)-(b-vec3(roundness)),0.0))-roundness;
}

void cameraRay(vec2 scanLines, vec3 cameraPosition, vec3 target, float fov, out vec3 pos, out vec3 dir)
{
	vec3 forward = normalize(target-cameraPosition);
    vec3 up = vec3(0.,1.,0.);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(forward,right));

    right = right*scanLines.x*fov;
    up = up*scanLines.y*fov;

    pos = cameraPosition;
    dir = (right+up+forward);
}

float smin( float a, float b, float k )
{
    float h = clamp(0.5+0.5*(b-a)/k,0.0,1.0);
    return mix(b,a,h)-k*h*(1.0-h);
}

#define FOV 0.8
float map(vec3 p)
{
    const float times = 9.;
	float d = 1000.;
    for(float i=0.;i<times;i++)
    {
    	d = smin(sphere(p,pow(0.83,i)*1.8),d,pow(1.,i)*0.9);
        p.z-=pow(sin(iTime+(2.*M_PI*i/times)),2.);
        p.x+=-pow(0.99,i)*1.;
    }
   	for(float i=0.;i<times;i++)
    {
    	d = smin(sphere(p,pow(0.83,7.-i)*1.8),d,pow(1.,i)*0.9);
        p.z-=pow(sin(iTime+(2.*M_PI*i/times)),2.);
        p.x-=-pow(0.99,i)*1.;
    }
   	for(float i=0.;i<times;i++)
    {
    	d = smin(sphere(p,pow(0.83,i)*1.8),d,pow(1.,i)*0.9);
        p.z+=pow(sin(iTime+(2.*M_PI*i/times)),2.);
        p.x-=-pow(0.99,i)*1.;
    }
    for(float i=0.;i<times;i++)
    {
    	d = smin(sphere(p,pow(0.83,7.-i)*1.8),d,pow(1.,i)*0.9);
        p.z+=pow(sin(iTime+(2.*M_PI*i/times)),2.);
        p.x+=-pow(0.99,i)*1.;
    }
    return d;
}

void march(vec3 origin, vec3 direction, out vec3 rayHead, out bool hit, out float dist, out float steps)
{
    float distanceTraveled = 0.;
    hit = true;
    for(float step=0.; step<MARCH_STEPS; step++)
    {
        rayHead = origin + (direction*distanceTraveled);
        dist = map(rayHead);
        steps = step;
        if(dist<EPSILON||step>=MARCH_STEPS) return;
        distanceTraveled += dist;
    }
    hit = false;
}

vec3 gradient( vec3 pos )
{
	const vec3 dx = vec3( GRADIENT_STEP, 0.0, 0.0 );
	const vec3 dy = vec3( 0.0, GRADIENT_STEP, 0.0 );
	const vec3 dz = vec3( 0.0, 0.0, GRADIENT_STEP );
	return normalize(vec3(
		map( pos + dx ) - map( pos - dx ),
		map( pos + dy ) - map( pos - dy ),
		map( pos + dz ) - map( pos - dz )
	));
}

float diffuse(vec3 normal, vec3 lightPosition)
{
	return max(dot(normal,normalize(lightPosition)),0.);
}

float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t );
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

float shadePoint(vec3 position, vec3 normal, vec3 lightPosition)
{
    float diffuseLight = diffuse(normal, lightPosition);
	// is this specular?
    // no, this could only be
    // speculative light
    float specularLight = pow(diffuseLight,30.);
	float shadow = 1.;//softshadow(position, normalize(lightPosition), 0.02, 2.25);
    return (diffuseLight+specularLight)*shadow;
}

vec3 background(vec2 uv)
{
	return vec3(0.5,0.5,0.5)*uv.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    // scale coordinates to -1 -> +1, adjusted for screen aspect ratio
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv = uv*2.-1.;
    uv.x = uv.x*iResolution.x/iResolution.y;

    // get ray representing camera scanline position and direction
    vec3 cameraPosition = vec3(sin(iTime/4.),-1.,cos(iTime/4.))*5.;
    vec3 cameraOrigin;
    vec3 cameraDirection;
    cameraRay(uv, cameraPosition, vec3(0.,0.,0.), FOV, cameraOrigin, cameraDirection);

    // calculate distance, hit point, etc of distance field via raymarching
    vec3 rayHead;
    bool hit;
    float dist;
    float steps = 40.;
    march(cameraOrigin, cameraDirection, rayHead, hit, dist, steps);

    // calculate normal for point on distance field
    vec3 normal = gradient(rayHead);

    // calculate light for point
	vec3 rotLightPos = vec3(-1.,-1.,0.);
    vec3 latRotLightPos = vec3(0.,-1.,-1.);
    vec3 lightPos = vec3(cos(iTime),sin(iTime),cos(iTime));
    float light = 0.;
    light += shadePoint(rayHead, normal, rotLightPos);
    light += shadePoint(rayHead, normal, latRotLightPos);
    light += shadePoint(rayHead, normal, lightPos);
    light /= 3.;

    // specify color of entire distance field
    vec3 fieldColor = vec3(1.0,0.4,0.4);

    // final steps and background
    vec3 color = fieldColor*light;
    if(!hit) color = background(uv);

    // set fragment shader color
    fragColor = vec4(color,1.);
}

/* 
    Combustible Voronoi Layers
	--------------------------

    The effect itself is nothing new or exciting, just some moving 3D Voronoi layering. 
    However, the fire palette might prove useful to some.

*/


// This is my favorite fire palette. It's trimmed down for shader usage, and is based on an 
// article I read at Hugo Elias's site years ago. I'm sure most old people, like me, have 
// visited his site at one time or another:
//
// http://freespace.virgin.net/hugo.elias/models/m_ffire.htm
//

// Hash function. This particular one probably doesn't disperse things quite as nicely as some 
// of the others around, but it's compact, and seems to work.
//
vec3 hash33(vec3 p){ 

    float n = sin(dot(p, vec3(7, 157, 113)));    
    return fract(vec3(2097152, 262144, 32768)*n); 
}

// 3D Voronoi: Obviously, this is just a rehash of IQ's original.
//
float voronoi(vec3 p){

	vec3 b, r, g = floor(p);
	p = fract(p); // "p -= g;" works on some GPUs, but not all, for some annoying reason.

	// Maximum value: I think outliers could get as high as "3," the squared diagonal length 
	// of the unit cube, with the mid point being "0.75." Is that right? Either way, for this 
	// example, the maximum is set to one, which would cover a good part of the range, whilst 
	// dispensing with the need to clamp the final result.
	float d = 1.; 

    // I've unrolled one of the loops. GPU architecture is a mystery to me, but I'm aware 
    // they're not fond of nesting, branching, etc. My laptop GPU seems to hate everything, 
    // including multiple loops. If it were a person, we wouldn't hang out. 
	for(int j = -1; j <= 1; j++) {
	    for(int i = -1; i <= 1; i++) {

		    b = vec3(i, j, -1);
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));

		    b.z = 0.0;
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));

		    b.z = 1.;
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));

	    }
	}

	return d; // Range: [0, 1]
}

// Standard fBm function with some time dialation to give a parallax 
// kind of effect. In other words, the position and time frequencies 
// are changed at different rates from layer to layer.
//
float noiseLayers(in vec3 p) {

    // Normally, you'd just add a time vector to "p," and be done with 
    // it. However, in this instance, time is added seperately so that 
    // its frequency can be changed at a different rate. "p.z" is thrown 
    // in there just to distort things a little more.
    vec3 t = vec3(0., 0., p.z+iTime*.5);

    const int iter = 5; // Just five layers is enough.
    float tot = 0., sum = 0., amp = 1.; // Total, sum, amplitude.

    for (int i = 0; i < iter; i++) {
        tot += voronoi(p + t) * amp; // Add the layer to the total.
        p *= 2.0; // Position multiplied by two.
        t *= 1.5; // Time multiplied by less than two.
        sum += amp; // Sum of amplitudes.
        amp *= 0.5; // Decrease successive layer amplitude, as normal.
    }

    return tot/sum; // Range: [0, 1].
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Screen coordinates.
	vec2 uv = (fragCoord.xy - iResolution.xy*0.5) / iResolution.y;

	// Shifting the central position around, just a little, to simulate a 
	// moving camera, albeit a pretty lame one.
	//uv += vec2(sin(iTime*0.5)*0.25, cos(iTime*0.5)*0.125);

    // Constructing the unit ray. 
	vec3 rd = normalize(vec3(uv.x, uv.y, 3.1415926535898/12.));

    // Rotating the ray about the XY plane, to simulate a rolling camera.
	//float cs = cos(iTime*0.25), si = sin(iTime*0.25);
    // Apparently "r *= rM" can break in some older browsers.
	//rd.xy = rd.xy*mat2(cs, -si, si, cs); 

	// Passing a unit ray multiple into the Voronoi layer function, which 
	// is nothing more than an fBm setup with some time dialation.
	float c = noiseLayers(rd*2.);

	// Optional: Adding a bit of random noise for a subtle dust effect. 
	//c = max(c + dot(hash33(rd)*2.-1., vec3(0.015)), 0.);

    // Coloring:

    // Nebula.
